      <div className="row">
        {/* H√¨nh ·∫£nh s·∫£n ph·∫©m */}
        <div className="col-md-6">
          <div className="card shadow-sm">
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={product.product_name}
              onError={(e) =>
