      {/* S·∫£n ph·∫©m li√™n quan c√≥ th·ªÉ th√™m sau */}
      <div className="row mt-5">
        <div className="col-12">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">‚ÑπÔ∏è Th√¥ng tin b·ªï sung</h5>
              <ul className="list-unstyled">
                <li>‚úÖ B·∫£o h√†nh ch√≠nh h√£ng 12 th√°ng</li>
                <li>‚úÖ ƒê·ªïi tr·∫£ trong 30 ng√†y</li>
                <li>‚úÖ Giao h√†ng to√†n qu·ªëc</li>
                <li>‚úÖ H·ªó tr·ª£ tr·∫£ g√≥p 0%</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
