// 

import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";
import Swal from "sweetalert2";

const Login = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    email: "",
    password: "",
    remember: true,
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      /* ================== 1. LOGIN ================== */
      const res = await authService.login({
        email: formData.email,
        password: formData.password,
      });

      const loginData = res.data;

      const token =
        loginData.token ||
        loginData["jwt-token"] ||
        loginData.jwt;

      const role =
        loginData.role ||
        loginData.roles?.[0]?.roleName ||
        loginData.authorities?.[0]?.authority;

      if (!token) {
        throw new Error("Server không trả về token");
      }

      /* ================== 2. LƯU TOKEN + ROLE ================== */
      localStorage.setItem("token", token);
      if (role) {
        localStorage.setItem("role", role); // ADMIN | USER
      }

      /* ================== 3. LẤY USER INFO ================== */
      let userInfo = null;
      let realUserId = null;

      try {
        userInfo = await userService.getUserByEmail(formData.email);
        realUserId =
          userInfo.user_id ||
          userInfo.userId ||
          userInfo.id;
        if (realUserId) realUserId = parseInt(realUserId);
      } catch (err) {
        console.warn("Không lấy được userInfo, thử parse token...");
      }

      /* ================== 4. FALLBACK: PARSE TOKEN ================== */
      if (!realUserId) {
        try {
          const payload = JSON.parse(atob(token.split(".")[1]));
          realUserId = parseInt(
            payload.userId || payload.sub || payload.user_id
          );
        } catch (err) {
          console.warn(err);
        }
      }

      if (!realUserId) {
        throw new Error("Không xác định được userId");
      }

      /* ================== 5. LƯU USER ================== */
      const userToStore = {
        userId: realUserId,
        email: formData.email,
        firstName:
          userInfo?.firstName ||
          userInfo?.first_name ||
          "Tài khoản",
        lastName:
          userInfo?.lastName ||
          userInfo?.last_name ||
          "",
      };

      localStorage.setItem("user", JSON.stringify(userToStore));

      /* ================== 6. UPDATE HEADER + CART ================== */
      window.dispatchEvent(new Event("authUpdated"));
      window.dispatchEvent(new Event("cartUpdated"));

      /* ================== 7. THÔNG BÁO ================== */
      Swal.fire({
        icon: "success",
        title: "Đăng nhập thành công!",
        text: `Chào mừng ${userToStore.firstName}!`,
        timer: 1500,
        showConfirmButton: false,
      });

      /* ================== 8. REDIRECT ================== */
      if (role === "ADMIN") {
        navigate("/admin");
      } else {
        navigate("/");
      }
    } catch (err) {
      console.error(err);
      Swal.fire({
        icon: "error",
        title: "Đăng nhập thất bại",
        text:
          err.response?.data?.message ||
          err.message ||
          "Email hoặc mật khẩu không đúng",
        confirmButtonColor: "#ff6a00",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="login-page"
      style={{
        backgroundColor: "#f8f9fa",
        minHeight: "100vh",
        padding: "40px 0",
      }}
    >
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-5 col-lg-4">
            <div
              className="card shadow-sm border-0"
              style={{ borderRadius: "15px" }}
            >
              <div
                className="login-header p-4 text-center text-white"
                style={{
                  backgroundColor: "#ff6a00",
                  borderRadius: "15px 15px 0 0",
                }}
              >
                <h3 className="fw-bold mb-1">Chào mừng!</h3>
                <p className="mb-0 opacity-75 small">
                  Đăng nhập để tiếp tục mua sắm
                </p>
              </div>

              <div className="card-body p-4">
                <form onSubmit={handleSubmit}>
                  {/* Email */}
                  <div className="mb-3">
                    <label className="form-label small fw-bold">
                      Email
                    </label>
                    <input
                      name="email"
                      type="email"
                      className="form-control"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      disabled={loading}
                    />
                  </div>

                  {/* Password */}
                  <div className="mb-3">
                    <label className="form-label small fw-bold">
                      Mật khẩu
                    </label>
                    <input
                      name="password"
                      type="password"
                      className="form-control"
                      value={formData.password}
                      onChange={handleChange}
                      required
                      disabled={loading}
                    />
                  </div>

                  <button
                    type="submit"
                    className="btn btn-primary w-100 fw-bold py-2"
                    style={{ backgroundColor: "#ff6a00" }}
                    disabled={loading}
                  >
                    {loading ? "Đang đăng nhập..." : "ĐĂNG NHẬP"}
                  </button>

                  <div className="text-center mt-3">
                    <p className="mb-0 text-muted small">
                      Chưa có tài khoản?{" "}
                      <Link
                        to="/register"
                        className="fw-bold text-decoration-none"
                        style={{ color: "#ff6a00" }}
                      >
                        Đăng ký
                      </Link>
                    </p>
                  </div>
                </form>
              </div>
            </div>

            <div className="text-center mt-4">
              <Link to="/" className="text-muted small">
                ← Quay lại trang chủ
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
