import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
    password: "",
    confirmPassword: "",
    street: "",
    buildingName: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });

    if (backendErrors[e.target.name]) {
      setBackendErrors({ ...backendErrors, [e.target.name]: "" });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setBackendErrors({});

    if (form.password !== form.confirmPassword) {
      setBackendErrors({ confirmPassword: "Mật khẩu nhập lại không khớp!" });
      Swal.fire({
        icon: "error",
        title: "Lỗi mật khẩu",
        text: "Mật khẩu xác nhận phải giống với mật khẩu đã nhập.",
        confirmButtonColor: "#ff6a00"
      });
      return;
    }

    setLoading(true);

    try {
      const payload = {
        firstName: form.first_name,
        lastName: form.last_name,
        mobileNumber: form.mobile_number,
        email: form.email,
        password: form.password,
        role: [{ roleId: 102, roleName: "USER" }],
      };

      if (
        form.street || form.buildingName || form.city ||
        form.state || form.country || form.pincode
      ) {
        payload.address = {
          street: form.street,
          buildingName: form.buildingName,
          city: form.city,
          state: form.state,
          country: form.country,
          pincode: form.pincode,
        };
      }

      await authService.register(payload);

      Swal.fire({
        title: "Đăng ký thành công!",
        text: "Chào mừng bạn gia nhập gia đình bé Ánh",
        icon: "success",
        confirmButtonColor: "#ff6a00",
        confirmButtonText: "Đăng nhập ngay"
      }).then((result) => {
        if (result.isConfirmed) {
          navigate("/login");
        }
      });

    } catch (error) {
      const errorData = error.response?.data;

      if (typeof errorData === "object") {
        setBackendErrors({
          first_name: errorData.firstName,
          last_name: errorData.lastName,
          email: errorData.email,
          mobile_number: errorData.mobileNumber,
          password: errorData.password
        });

        Swal.fire({
          icon: "warning",
          title: "Dữ liệu chưa đúng",
          text: "Vui lòng kiểm tra lại các thông tin màu đỏ bên dưới.",
          confirmButtonColor: "#ff6a00"
        });
      } else {
        Swal.fire({
          icon: "error",
          title: "Đăng ký thất bại",
          text: errorData || "Có lỗi xảy ra, vui lòng thử lại sau.",
          confirmButtonColor: "#d33"
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="section-content padding-y bg-light" style={{ minHeight: "90vh" }}>
      <div className="card mx-auto shadow-sm" style={{ maxWidth: "520px", marginTop: "40px", borderRadius: "15px" }}>
        <article className="card-body p-4">
          <header className="mb-4 text-center">
            <h4 className="card-title fw-bold" style={{ color: "#ff6a00" }}>TẠO TÀI KHOẢN</h4>
            <p className="text-muted small">Thông tin cần từ 5-30 ký tự theo yêu cầu hệ thống</p>
          </header>

          <form onSubmit={handleSubmit}>

            {/* === FORM USER === */}
            <div className="row">
              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Họ</label>
                <input name="first_name" className={`form-control ${backendErrors.first_name ? 'is-invalid' : ''}`}
                  value={form.first_name} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.first_name}</div>
              </div>

              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Tên</label>
                <input name="last_name" className={`form-control ${backendErrors.last_name ? 'is-invalid' : ''}`}
                  value={form.last_name} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.last_name}</div>
              </div>
            </div>

            <div className="mb-3">
              <label className="form-label small fw-bold">Email</label>
              <input name="email" type="email"
                className={`form-control ${backendErrors.email ? 'is-invalid' : ''}`}
                value={form.email} onChange={handleChange} required />
              <div className="invalid-feedback">{backendErrors.email}</div>
            </div>

            <div className="mb-3">
              <label className="form-label small fw-bold">Số điện thoại</label>
              <input name="mobile_number"
                className={`form-control ${backendErrors.mobile_number ? 'is-invalid' : ''}`}
                value={form.mobile_number} onChange={handleChange} required />
              <div className="invalid-feedback">{backendErrors.mobile_number}</div>
            </div>

            <div className="row mb-4">
              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Mật khẩu</label>
                <input name="password" type="password"
                  className={`form-control ${backendErrors.password ? 'is-invalid' : ''}`}
                  value={form.password} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.password}</div>
              </div>

              <div className="col-md-6 mb-3">
                <label className="form-label small fw-bold">Nhập lại mật khẩu</label>
                <input name="confirmPassword" type="password"
                  className={`form-control ${backendErrors.confirmPassword ? 'is-invalid' : ''}`}
                  value={form.confirmPassword} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.confirmPassword}</div>
              </div>
            </div>

            <button className="btn w-100 fw-bold py-2 text-white"
              style={{ backgroundColor: "#ff6a00" }} disabled={loading}>
              {loading ? "Đang xử lý..." : "TIẾP TỤC ĐĂNG KÝ"}
            </button>

          </form>
        </article>
      </div>

      <p className="text-center mt-4 text-muted">
        Bạn đã có tài khoản? <Link to="/login" className="fw-bold" style={{ color: "#ff6a00" }}>Đăng nhập</Link>
      </p>
    </section>
  );
};

export default Register;
