import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";
import ProductCard from "../product/ProductCard";
import Swal from "sweetalert2";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [suggestedProducts, setSuggestedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [addingToCart, setAddingToCart] = useState(false);

  const currentUser = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        setLoading(true);

        // 1Ô∏è‚É£ L·∫§Y CHI TI·∫æT S·∫¢N PH·∫®M
        const productData = await productService.getProductById(productId);
        setProduct(productData);

        console.log("PRODUCT DATA:", productData);

        // 2Ô∏è‚É£ L·∫§Y CATEGORY ID (AN TO√ÄN 100%)
        const categoryId =
          productData.categoryId ||
          productData.category?.categoryId ||
          productData.category_id ||
          productData.categories?.[0]?.categoryId;

        if (!categoryId) {
          console.error("‚ùå Kh√¥ng l·∫•y ƒë∆∞·ª£c categoryId", productData);
          setRelatedProducts([]);
        } else {
          // 3Ô∏è‚É£ L·∫§Y S·∫¢N PH·∫®M C√ôNG DANH M·ª§C
          const related = await productService.getProductsByCategory(
            categoryId,
            0,
            8
          );

          const filtered = (related?.content || []).filter(
            (p) => (p.productId ?? p.id) !== Number(productId)
          );

          setRelatedProducts(filtered);
        }

        // 4Ô∏è‚É£ S·∫¢N PH·∫®M G·ª¢I √ù
        const suggested = await productService.getAllProducts(0, 4);
        setSuggestedProducts(suggested?.content || []);

      } catch (error) {
        console.error("‚ùå L·ªói ProductDetail:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, [productId]);

  // üõí ADD TO CART
  const handleAddToCart = async () => {
    if (!currentUser) {
      Swal.fire({
        title: "Y√™u c·∫ßu ƒëƒÉng nh·∫≠p",
        text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng",
        icon: "warning",
        showCancelButton: true,
        confirmButtonText: "ƒêƒÉng nh·∫≠p",
        cancelButtonText: "ƒê·ªÉ sau",
      }).then((res) => res.isConfirmed && navigate("/login"));
      return;
    }

    try {
      setAddingToCart(true);
      const cart = await cartService.getActiveCart(currentUser.email);
      await cartService.addToCart(cart.cartId, productId, 1);

      Swal.fire({
        icon: "success",
        title: "ƒê√£ th√™m v√†o gi·ªè h√†ng",
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (err) {
      Swal.fire("L·ªói", err.message || "Kh√¥ng th·ªÉ th√™m gi·ªè h√†ng", "error");
    } finally {
      setAddingToCart(false);
    }
  };

  if (loading)
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: "60vh" }}>
        <div className="spinner-grow text-primary"></div>
      </div>
    );

  if (!product)
    return (
      <div className="container mt-5 text-center">
        <h5>Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m</h5>
      </div>
    );

  const currentPrice = product.specialPrice || product.price;

  return (
    <div className="container mt-4 mb-5 animate__animated animate__fadeIn">
      {/* BREADCRUMB */}
      <nav aria-label="breadcrumb" className="mb-4">
        <ol className="breadcrumb bg-light p-3 rounded-pill shadow-sm">
          <li className="breadcrumb-item">
            <Link to="/">Trang ch·ªß</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/product">S·∫£n ph·∫©m</Link>
          </li>
          <li className="breadcrumb-item active text-truncate">
            {product.productName}
          </li>
        </ol>
      </nav>

      {/* CHI TI·∫æT */}
      <div className="row g-5">
        <div className="col-lg-6">
          <div className="bg-white p-4 rounded-4 shadow-sm text-center">
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={product.productName}
              className="img-fluid product-zoom"
              style={{ maxHeight: "500px", objectFit: "contain" }}
            />
          </div>
        </div>

        <div className="col-lg-6">
          <h1 className="fw-bold">{product.productName}</h1>

          <div className="p-3 bg-light rounded-4 my-3">
            <span className="h3 text-danger fw-bold">
              {currentPrice?.toLocaleString()} ‚Ç´
            </span>
            {product.specialPrice && (
              <del className="ms-3 text-muted">
                {product.price?.toLocaleString()} ‚Ç´
              </del>
            )}
          </div>

          <p className="text-muted">
            {product.description || "M·ªπ ph·∫©m ch√≠nh h√£ng, an to√†n cho da."}
          </p>

          <button
            className="btn btn-primary btn-lg rounded-pill mt-3"
            onClick={handleAddToCart}
            disabled={addingToCart || product.quantity === 0}
          >
            {addingToCart ? "ƒêang th√™m..." : "üõí Th√™m v√†o gi·ªè h√†ng"}
          </button>
        </div>
      </div>

      {/* S·∫¢N PH·∫®M C√ôNG LO·∫†I */}
      <div className="mt-5 pt-4">
        <h4 className="fw-bold mb-4 border-start border-4 border-primary ps-3">
          S·∫£n ph·∫©m c√πng lo·∫°i
        </h4>

        <div className="row">
          {relatedProducts.length > 0 ? (
            relatedProducts.map((item) => (
              <ProductCard
                key={item.productId || item.id}
                product={item}
              />
            ))
          ) : (
            <p className="text-muted">Kh√¥ng c√≥ s·∫£n ph·∫©m c√πng danh m·ª•c</p>
          )}
        </div>
      </div>

      <style>{`
        .product-zoom {
          transition: transform 0.4s ease;
          cursor: zoom-in;
        }
        .product-zoom:hover {
          transform: scale(1.1);
        }
      `}</style>
    </div>
  );
};

export default ProductDetail;
