// src/pages/checkout/CheckOut.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import orderService from '../../services/orderService';

const CheckOut = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('COD');
  const navigate = useNavigate();

  const currentUser = JSON.parse(localStorage.getItem('user')) || {};

  useEffect(() => {
    loadCart();
  }, []);

  const loadCart = async () => {
    try {
      if (!currentUser.email) {
        alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ thanh to√°n');
        navigate('/login');
        return;
      }

      const cartData = await cartService.getActiveCart(currentUser.email);
      console.log('üì¶ Gi·ªè h√†ng thanh to√°n:', cartData);

      if (!cartData || !cartData.products || cartData.products.length === 0) {
        alert('‚ùå Gi·ªè h√†ng tr·ªëng');
        navigate('/cart');
        return;
      }

      setCart(cartData);
    } catch (error) {
      console.error('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng:', error);
      alert('‚ùå L·ªói khi t·∫£i gi·ªè h√†ng');
    }
  };

  const handlePlaceOrder = async () => {
    if (!currentUser.email) {
      alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ƒë·∫∑t h√†ng');
      navigate('/login');
      return;
    }

    if (!cart || !cart.cartId) {
      alert('‚ùå Kh√¥ng t√¨m th·∫•y gi·ªè h√†ng');
      return;
    }

    try {
      setLoading(true);
      console.log('üí∞ ƒê·∫∑t h√†ng v·ªõi ph∆∞∆°ng th·ª©c:', paymentMethod);

      // S·ª≠ d·ª•ng orderService thay v√¨ cartService
      const order = await orderService.createOrderFromCart(
        currentUser.email, 
        cart.cartId, 
        paymentMethod
      );

      console.log('‚úÖ ƒê∆°n h√†ng th√†nh c√¥ng:', order);

      alert('üéâ ƒê·∫∑t h√†ng th√†nh c√¥ng! C·∫£m ∆°n b·∫°n ƒë√£ mua s·∫Øm.');

      // X√≥a gi·ªè h√†ng localStorage
      localStorage.removeItem('cart');

      // ƒêi·ªÅu h∆∞·ªõng ƒë·∫øn trang x√°c nh·∫≠n ƒë∆°n h√†ng
      navigate('/order-success', { state: { order } });

    } catch (error) {
      console.error('‚ùå L·ªói khi ƒë·∫∑t h√†ng:', error);

      // Hi·ªÉn th·ªã th√¥ng b√°o l·ªói chi ti·∫øt h∆°n
      const errorMessage = error.response?.data?.message || error.message || 'L·ªói kh√¥ng x√°c ƒë·ªãnh';
      alert(`‚ùå L·ªói khi ƒë·∫∑t h√†ng: ${errorMessage}`);
    } finally {
      setLoading(false);
    }
  };

  if (!cart) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i th√¥ng tin thanh to√°n...</p>
        </div>
      </div>
    );
  }

  const totalAmount = cartService.calculateTotal(cart.products);

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Thanh To√°n</h2>

      <div className="row">
        <div className="col-md-8">
          {/* Th√¥ng tin s·∫£n ph·∫©m */}
          <div className="card mb-4">
            <div className="card-header">
              <h5>S·∫£n ph·∫©m trong ƒë∆°n h√†ng</h5>
            </div>
            <div className="card-body">
              {cart.products.map((item, index) => (
                <div key={index} className="d-flex justify-content-between align-items-center mb-3">
                  <div className="d-flex align-items-center">
                    <img
                      src={item.image || 'https://cdn-icons-png.flaticon.com/512/679/679922.png'}
                      alt={item.productName}
                      className="rounded me-3"
                      style={{ width: '60px', height: '60px', objectFit: 'cover' }}
                    />
                    <div>
                      <h6 className="mb-1">{item.productName}</h6>
                      <small className="text-muted">S·ªë l∆∞·ª£ng: {item.quantity}</small>
                    </div>
                  </div>
                  <span className="fw-bold text-danger">
                    {((item.specialPrice || item.price || 0) * (item.quantity || 1)).toLocaleString()}‚Ç´
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Ph∆∞∆°ng th·ª©c thanh to√°n */}
          <div className="card mb-4">
            <div className="card-header">
              <h5>Ph∆∞∆°ng th·ª©c thanh to√°n</h5>
            </div>
            <div className="card-body">
              <div className="form-check mb-2">
                <input
                  className="form-check-input"
                  type="radio"
                  name="paymentMethod"
                  value="COD"
                  checked={paymentMethod === 'COD'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <label className="form-check-label">
                  üí∞ Thanh to√°n khi nh·∫≠n h√†ng (COD)
                </label>
              </div>
              <div className="form-check mb-2">
                <input
                  className="form-check-input"
                  type="radio"
                  name="paymentMethod"
                  value="BANK_TRANSFER"
                  checked={paymentMethod === 'BANK_TRANSFER'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <label className="form-check-label">
                  üè¶ Chuy·ªÉn kho·∫£n ng√¢n h√†ng
                </label>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-4">
          {/* T·ªïng thanh to√°n */}
          <div className="card">
            <div className="card-header">
              <h5>T·ªïng thanh to√°n</h5>
            </div>
            <div className="card-body">
              <div className="d-flex justify-content-between mb-2">
                <span>T·∫°m t√≠nh:</span>
                <span>{totalAmount.toLocaleString()}‚Ç´</span>
              </div>
              <div className="d-flex justify-content-between mb-2">
                <span>Ph√≠ v·∫≠n chuy·ªÉn:</span>
                <span className="text-success">Mi·ªÖn ph√≠</span>
              </div>
              <hr />
              <div className="d-flex justify-content-between mb-3">
