// src/services/productService.js
import axios from "axios";

const API_BASE_URL = "http://localhost:8080/api";

const productService = {
  // ðŸŸ¢ Public APIs
  getAllProducts: () => axios.get(`${API_BASE_URL}/public/products`),

  getProductById: (productId) =>
    axios.get(`${API_BASE_URL}/public/products/${productId}`),

  getProductsByCategory: (categoryId) =>
    axios.get(`${API_BASE_URL}/public/categories/${categoryId}/products`),

  searchProductsByKeyword: (keyword) =>
    axios.get(`${API_BASE_URL}/public/products/keyword/${keyword}`),

  getProductImage: (fileName) =>
    `${API_BASE_URL}/public/products/image/${fileName}`,

  // ðŸ” Admin APIs
  createProduct: (categoryId, data, token) =>
    axios.post(`${API_BASE_URL}/admin/categories/${categoryId}/products`, data, {
      headers: { Authorization: `Bearer ${token}` },
    }),

  updateProduct: (productId, data, token) =>
    axios.put(`${API_BASE_URL}/admin/products/${productId}`, data, {
      headers: { Authorization: `Bearer ${token}` },
    }),

  deleteProduct: (productId, token) =>
    axios.delete(`${API_BASE_URL}/admin/products/${productId}`, {
      headers: { Authorization: `Bearer ${token}` },
    }),

  updateProductImage: (productId, imageFile, token) => {
    const formData = new FormData();
    formData.append("file", imageFile);

    return axios.put(`${API_BASE_URL}/admin/products/${productId}/image`, formData, {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    });
  },
};

export default productService;
