import { useEffect, useState } from "react";
import { Link } from "react-router-dom"; // Th√™m import n√†y
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    productService
      .getAllProducts()
      .then((data) => {
        setProducts(data.content || data);
      })
      .catch((err) => console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err));
  }, []);

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase font-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
        </h3>
      </header>

      <div className="row">
        {products.map((p) => (
          <div key={p.product_id} className="col-xl-3 col-lg-3 col-md-4 col-6 mb-4">
            <div className="card card-product-grid shadow-sm h-100">
              <div className="img-wrap">
                {/* S·ª≠ d·ª•ng Link ƒë·ªÉ bao b·ªçc ·∫£nh v√† t√™n s·∫£n ph·∫©m */}
                <Link to={`/product/${p.product_id}`} className="text-decoration-none">
                  <img
                    src={`${IMAGE_BASE_URL}/${p.image}`}
                    alt={p.product_name}
                    onError={(e) =>
                      (e.target.src =
                        "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                    }
                    style={{
                      width: "100%",
                      height: "250px",
                      objectFit: "cover",
                      borderRadius: "10px",
                    }}
                  />
                </Link>
              </div>

              <div className="info-wrap p-3 d-flex flex-column">
                {/* T√™n s·∫£n ph·∫©m v·ªõi link */}
                <Link 
                  to={`/product/${p.product_id}`} 
                  className="text-decoration-none"
                >
                  <h6 className="text-truncate text-dark font-weight-bold">
                    {p.product_name}
                  </h6>
                </Link>

                <p className="text-muted small mb-2 flex-grow-1">
                  {p.description?.substring(0, 40)}...
                </p>

                <div className="price-wrapper">
                  {p.special_price ? (
                    <>
                      <span className="price h5 text-danger mb-0">
                        {p.special_price.toLocaleString()} ‚Ç´
                      </span>
                      <span className="original-price text-muted text-decoration-line-through small ms-2">
                        {p.price.toLocaleString()} ‚Ç´
                      </span>
                    </>
                  ) : (
                    <span className="price h5 text-danger mb-0">
                      {p.price.toLocaleString()} ‚Ç´
                    </span>
                  )}
                </div>

                {p.discount > 0 && (
                  <small className="text-success mt-1">
                    üî• Gi·∫£m {p.discount}%
                  </small>
                )}

                {/* Hi·ªÉn th·ªã s·ªë l∆∞·ª£ng */}
                <small className={`mt-1 ${p.quantity === 0 ? 'text-danger' : 'text-success'}`}>
                  {p.quantity === 0 ? '‚ùå H·∫øt h√†ng' : `üì¶ C√≤n ${p.quantity} s·∫£n ph·∫©m`}
                </small>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProductList;
