const ProductSold = () => {
  const [topProducts, setTopProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [posts, setPosts] = useState([]); 
  const [loading, setLoading] = useState(false);

  useEffect(() => {
