import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
    password: "",
    confirmPassword: "",
    // Địa chỉ để trống để người dùng nhập
    street: "",
    buildingName: "",
    city: "",
    state: "",
    country: "VN",
    pincode: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });

    // Xóa lỗi khi người dùng bắt đầu sửa lại ô đó
    if (backendErrors[name]) {
      setBackendErrors({ ...backendErrors, [name]: "" });
    }
  };

  // Hàm kiểm tra logic trước khi gửi API
  const validateForm = () => {
    let errors = {};
    const phoneRegex = /^[0-9]{10}$/;
    const pincodeRegex = /^[0-9]{6}$/;

    if (!phoneRegex.test(form.mobile_number)) {
      errors.mobile_number = "Số điện thoại phải gồm 10 chữ số.";
    }
    if (!pincodeRegex.test(form.pincode)) {
      errors.pincode = "Mã Pincode phải đúng 6 chữ số.";
    }
    if (form.password.length < 6) {
      errors.password = "Mật khẩu phải từ 6 ký tự trở lên.";
    }
    if (form.password !== form.confirmPassword) {
      errors.confirmPassword = "Mật khẩu xác nhận không khớp.";
    }

    setBackendErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return; // Dừng nếu validation client thất bại

    setLoading(true);
    try {
      const payload = {
        firstName: form.first_name,
        lastName: form.last_name,
        mobileNumber: form.mobile_number,
        email: form.email,
        password: form.password,
        address: {
          street: form.street,
          buildingName: form.buildingName,
          city: form.city,
          state: form.state,
          country: form.country,
          pincode: form.pincode,
        },
        role: [{ roleId: 102, roleName: "USER" }],
      };

      await authService.register(payload);

      Swal.fire({
        title: "Thành công!",
        text: "Tài khoản của bạn đã sẵn sàng.",
        icon: "success",
        confirmButtonColor: "#ff6a00",
      }).then(() => navigate("/login"));

    } catch (error) {
      const errorData = error.response?.data;
      if (error.response?.status === 400 && typeof errorData === "object") {
        setBackendErrors({
          first_name: errorData.firstName,
          last_name: errorData.lastName,
          email: errorData.email,
          mobile_number: errorData.mobileNumber,
          password: errorData.password,
          // Thêm lỗi địa chỉ từ backend nếu có
          street: errorData["address.street"],
          pincode: errorData["address.pincode"]
        });
      } else {
        Swal.fire({
          icon: "error",
          title: "Lỗi đăng ký",
          text: typeof errorData === "string" ? errorData : "Email hoặc số điện thoại đã tồn tại!",
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="section-content padding-y bg-light" style={{ minHeight: "100vh" }}>
      <div className="card mx-auto shadow-sm" style={{ maxWidth: "650px", marginTop: "20px", borderRadius: "15px" }}>
        <article className="card-body p-4">
          <header className="mb-4 text-center">
            <h4 className="card-title fw-bold" style={{ color: "#ff6a00" }}>THÔNG TIN ĐĂNG KÝ</h4>
          </header>

          <form onSubmit={handleSubmit}>
            {/* --- Thông tin cá nhân --- */}
            <h6 className="text-muted mb-3 border-bottom pb-2">1. Thông tin cá nhân</h6>
            <div className="row mb-3">
              <div className="col">
                <label className="small fw-bold">Họ</label>
                <input name="first_name" type="text" className={`form-control ${backendErrors.first_name ? 'is-invalid' : ''}`} value={form.first_name} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.first_name}</div>
              </div>
              <div className="col">
                <label className="small fw-bold">Tên</label>
                <input name="last_name" type="text" className={`form-control ${backendErrors.last_name ? 'is-invalid' : ''}`} value={form.last_name} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.last_name}</div>
              </div>
            </div>

            <div className="row mb-3">
              <div className="col-md-6">
                <label className="small fw-bold">Email</label>
                <input name="email" type="email" className={`form-control ${backendErrors.email ? 'is-invalid' : ''}`} value={form.email} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.email}</div>
              </div>
              <div className="col-md-6">
                <label className="small fw-bold">Số điện thoại</label>
                <input name="mobile_number" type="text" className={`form-control ${backendErrors.mobile_number ? 'is-invalid' : ''}`} value={form.mobile_number} onChange={handleChange} required placeholder="VD: 0912345678" />
                <div className="invalid-feedback">{backendErrors.mobile_number}</div>
              </div>
            </div>

            {/* --- Địa chỉ --- */}
            <h6 className="text-muted mt-4 mb-3 border-bottom pb-2">2. Địa chỉ nhận hàng</h6>
            <div className="row mb-3">
              <div className="col-md-8">
                <label className="small fw-bold">Tên đường/Số nhà</label>
                <input name="street" type="text" className={`form-control ${backendErrors.street ? 'is-invalid' : ''}`} value={form.street} onChange={handleChange} required />
              </div>
              <div className="col-md-4">
                <label className="small fw-bold">Tòa nhà (nếu có)</label>
                <input name="buildingName" type="text" className="form-control" value={form.buildingName} onChange={handleChange} />
              </div>
            </div>

            <div className="row mb-3">
              <div className="col-md-4">
                <label className="small fw-bold">Thành phố</label>
                <input name="city" type="text" className="form-control" value={form.city} onChange={handleChange} required />
              </div>
              <div className="col-md-4">
                <label className="small fw-bold">Tỉnh/Bang</label>
                <input name="state" type="text" className="form-control" value={form.state} onChange={handleChange} required />
              </div>
              <div className="col-md-4">
                <label className="small fw-bold">Mã Pincode</label>
                <input name="pincode" type="text" className={`form-control ${backendErrors.pincode ? 'is-invalid' : ''}`} value={form.pincode} onChange={handleChange} required placeholder="6 chữ số" />
                <div className="invalid-feedback">{backendErrors.pincode}</div>
              </div>
            </div>

            {/* --- Mật khẩu --- */}
            <h6 className="text-muted mt-4 mb-3 border-bottom pb-2">3. Bảo mật</h6>
            <div className="row mb-4">
              <div className="col-md-6">
                <label className="small fw-bold">Mật khẩu</label>
                <input name="password" type="password" className={`form-control ${backendErrors.password ? 'is-invalid' : ''}`} value={form.password} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.password}</div>
              </div>
              <div className="col-md-6">
                <label className="small fw-bold">Xác nhận mật khẩu</label>
                <input name="confirmPassword" type="password" className={`form-control ${backendErrors.confirmPassword ? 'is-invalid' : ''}`} value={form.confirmPassword} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.confirmPassword}</div>
              </div>
            </div>

            <button type="submit" className="btn w-100 fw-bold text-white py-2" style={{ backgroundColor: "#ff6a00" }} disabled={loading}>
              {loading ? <span className="spinner-border spinner-border-sm"></span> : "HOÀN TẤT ĐĂNG KÝ"}
            </button>
          </form>
        </article>
      </div>
      <p className="text-center mt-3">
        Đã có tài khoản? <Link to="/login" style={{ color: "#ff6a00" }} className="fw-bold text-decoration-none">Đăng nhập ngay</Link>
      </p>
    </section>
  );
};

export default Register;
