import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([{ role: 'ai', text: 'Chào nàng! Em là Ngọc Anh Beauty AI đây. ❤️' }]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    useEffect(() => {
        scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        try {
            // Đảm bảo URL này khớp với port Spring Boot (thường là 8080)
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: userMsg
            });

            console.log("Dữ liệu nhận được:", res.data);

            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: res.data.reply || "Hic, em không biết nói gì luôn..." 
            }]);
        } catch (error) {
            console.error("Lỗi gọi API:", error);
            setMessages(prev => [...prev, { role: 'ai', text: 'Server đang bận nàng ơi, chờ em xíu!' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1050 }}>
            <button onClick={() => setIsOpen(!isOpen)} className="btn btn-warning rounded-circle shadow-lg" style={{ width: '60px', height: '60px' }}>
                <i className={`bi ${isOpen ? 'bi-x-lg' : 'bi-robot'} fs-3`}></i>
            </button>

            {isOpen && (
                <div className="card shadow-lg mt-2" style={{ width: '350px', height: '500px', display: 'flex', flexDirection: 'column', borderRadius: '15px', overflow: 'hidden' }}>
                    <div className="card-header bg-warning text-dark fw-bold">Ngọc Anh Beauty AI</div>
                    <div className="card-body overflow-auto p-3" style={{ flex: 1, backgroundColor: '#f8f9fa' }}>
                        {messages.map((msg, i) => (
                            <div key={i} className={`d-flex mb-3 ${msg.role === 'user' ? 'justify-content-end' : 'justify-content-start'}`}>
                                <div className={`p-2 px-3 rounded-4 shadow-sm ${msg.role === 'user' ? 'bg-warning' : 'bg-white border'}`} 
                                     style={{ maxWidth: '85%', fontSize: '14px', whiteSpace: 'pre-wrap' }}>
                                    {msg.text}
                                </div>
                            </div>
                        ))}
                        {loading && <div className="text-muted small">Đang suy nghĩ...</div>}
                        <div ref={scrollRef} />
                    </div>
                    <div className="card-footer bg-white border-0 p-3">
                        <div className="input-group">
                            <input type="text" className="form-control border-warning shadow-none" placeholder="Hỏi em..." 
                                   value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && handleSend()} />
                            <button onClick={handleSend} className="btn btn-warning"><i className="bi bi-send"></i></button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default GeminiChat;
