import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    const initializeUser = async () => {
      const storedUser = localStorage.getItem("user");
      const storedToken = localStorage.getItem("token");

      console.log("üîê Auth Check:", {
        hasUser: !!storedUser,
        hasToken: !!storedToken,
        tokenPreview: storedToken ? storedToken.substring(0, 20) + "..." : "No token"
      });

      // Ki·ªÉm tra authentication
      if (!storedUser || !storedToken) {
        setMessage({ 
          type: "error", 
          text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ch·ªânh s·ª≠a h·ªì s∆°" 
        });
        setTimeout(() => navigate("/login"), 2000);
        return;
      }

      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        const userId = parsedUser.userId || parsedUser.user_id;
        if (!userId) {
          setMessage({ type: "error", text: "Kh√¥ng t√¨m th·∫•y ID ng∆∞·ªùi d√πng" });
          return;
        }

        console.log("üÜî Loading profile for user ID:", userId);
        setLoading(true);

        // L·∫•y th√¥ng tin user t·ª´ API
        const userData = await userService.getUserProfile(userId);
        console.log("‚úÖ User profile loaded:", userData);

        // C·∫≠p nh·∫≠t form data
        setFormData({
          first_name: userData.first_name || "",
          last_name: userData.last_name || "",
          email: userData.email || "",
          mobile_number: userData.mobile_number || ""
