import React, { useState, useEffect } from 'react';
import ReactStars from 'react-stars';
import axios from 'axios';
import Swal from 'sweetalert2';

const ProductReview = ({ productId, currentUser }) => {
  const [reviews, setReviews] = useState([]);
  const [canReview, setCanReview] = useState(false);
  const [comment, setComment] = useState("");
  const [rating, setRating] = useState(5);

  useEffect(() => {
    fetchReviews();
    checkReviewPermission();
  }, [productId, currentUser]);

  const fetchReviews = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/public/products/${productId}/reviews`);
      setReviews(res.data);
    } catch (err) {
      console.error("Lỗi lấy review:", err);
    }
  };

  const checkReviewPermission = async () => {
    if (currentUser?.userId) {
      try {
        const res = await axios.get(`http://localhost:8080/api/public/can-review`, {
          params: { userId: currentUser.userId, productId: productId }
        });
        setCanReview(res.data);
      } catch (err) {
        setCanReview(false);
      }
    }
  };

  const handleSubmit = async () => {
    if (!comment.trim()) {
      Swal.fire('Lỗi', 'Vui lòng nhập nội dung đánh giá', 'error');
      return;
    }

    try {
      const reviewDTO = {
        comment,
        rating,
        productId,
        userId: currentUser.userId
      };
      const res = await axios.post(`http://localhost:8080/api/admin/reviews`, reviewDTO);
      setReviews([res.data, ...reviews]);
      setComment("");
      setCanReview(false); // Đánh giá xong thì ẩn form (vì mỗi người chỉ được 1 lần)
      Swal.fire('Thành công', 'Cảm ơn bạn đã đánh giá!', 'success');
    } catch (err) {
      Swal.fire('Lỗi', err.response?.data || 'Không thể gửi đánh giá', 'error');
    }
  };

  return (
    <div className="mt-5 border-top pt-5">
      <h4 className="fw-bold mb-4 border-start border-4 border-primary ps-3 text-uppercase">Khách hàng đánh giá</h4>

      {/* FORM GỬI ĐÁNH GIÁ (Chỉ hiện khi đủ điều kiện) */}
      {canReview ? (
        <div className="card shadow-sm mb-5 p-4 border-0 bg-light rounded-4">
          <h5 className="fw-bold mb-3">Để lại đánh giá của bạn</h5>
          <div className="mb-2">
            <span className="small text-muted me-2">Chọn số sao:</span>
            <ReactStars count={5} size={30} value={rating} onChange={(newRating) => setRating(newRating)} color2={'#ffd700'} />
          </div>
          <textarea 
            className="form-control border-0 shadow-sm rounded-3" 
            rows="3" 
            value={comment} 
            onChange={(e) => setComment(e.target.value)}
            placeholder="Bạn thấy sản phẩm này thế nào? Chia sẻ cho mọi người cùng biết nhé..."
          ></textarea>
          <div className="text-end mt-3">
            <button className="btn btn-primary px-4 rounded-pill" onClick={handleSubmit}>Gửi đánh giá ngay</button>
          </div>
        </div>
      ) : (
        currentUser && (
          <div className="alert alert-info rounded-4 border-0 shadow-sm mb-5">
            <i className="bi bi-info-circle me-2"></i>
            Hệ thống chỉ cho phép đánh giá khi bạn đã mua và nhận được hàng thành công.
          </div>
        )
      )}

      {/* DANH SÁCH ĐÁNH GIÁ */}
      <div className="review-list">
        {reviews.length > 0 ? (
          reviews.map((r) => (
            <div key={r.reviewId} className="card mb-3 border-0 shadow-sm rounded-4 p-3">
              <div className="d-flex justify-content-between align-items-start">
                <div className="d-flex align-items-center">
                  <div className="bg-primary text-white rounded-circle d-flex align-items-center justify-content-center me-3" style={{width: '45px', height: '45px', fontSize: '1.2rem'}}>
                    {r.userName?.charAt(0).toUpperCase()}
                  </div>
                  <div>
                    <h6 className="mb-0 fw-bold">{r.userName}</h6>
                    <ReactStars count={5} size={16} edit={false} value={r.rating} color2={'#ffd700'} />
                  </div>
                </div>
                <span className="text-muted small">{new Date(r.createdAt).toLocaleDateString()}</span>
              </div>
              <p className="mt-3 mb-0 text-dark">{r.comment}</p>
            </div>
          ))
        ) : (
          <p className="text-muted text-center my-4 italic">Chưa có đánh giá nào cho sản phẩm này.</p>
        )}
      </div>
    </div>
  );
};

export default ProductReview;
