import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobileNumber: "",
    password: "",
    confirmPassword: "",
    // Address fields
    street: "",
    buildingName: "",
    city: "",
    state: "",
    country: "Vietnam",
    pincode: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    if (backendErrors[name]) {
      setBackendErrors({ ...backendErrors, [name]: "" });
    }
  };

  const validateForm = () => {
    let errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10,11}$/;

    if (!emailRegex.test(form.email)) errors.email = "Email không hợp lệ.";
    if (!phoneRegex.test(form.mobileNumber)) errors.mobileNumber = "Số điện thoại phải từ 10-11 số.";
    if (form.password.length < 6) errors.password = "Mật khẩu phải ít nhất 6 ký tự.";
    if (form.password !== form.confirmPassword) errors.confirmPassword = "Mật khẩu không khớp.";
    if (!form.street) errors.street = "Vui lòng nhập tên đường/số nhà.";
    if (!form.city) errors.city = "Vui lòng nhập thành phố.";
    if (!form.pincode) errors.pincode = "Vui lòng nhập mã bưu chính.";

    setBackendErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      // Mapping dữ liệu theo đúng cấu trúc JSON Backend yêu cầu
      const payload = {
        firstName: form.firstName,
        lastName: form.lastName,
        mobileNumber: form.mobileNumber,
        email: form.email,
        password: form.password,
        roles: [{ roleId: 102, roleName: "USER" }], // ID 102 theo AppConstants
        address: {
          street: form.street,
          buildingName: form.buildingName,
          city: form.city,
          state: form.state,
          country: form.country,
          pincode: form.pincode,
        }
      };

      await authService.register(payload);

      Swal.fire({
        icon: "success",
        title: "Đăng ký thành công",
        text: "Bạn có thể đăng nhập ngay bây giờ!",
        confirmButtonColor: "#ff6a00",
      }).then(() => navigate("/login"));

    } catch (error) {
      const errorData = error.response?.data;
      // Xử lý lỗi validation từ Spring Boot (MethodArgumentNotValidException)
      if (typeof errorData === "object") {
        setBackendErrors(errorData);
      } else {
        Swal.fire({
          icon: "error",
          title: "Lỗi hệ thống",
          text: errorData || "Email hoặc số điện thoại đã tồn tại.",
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="section-content padding-y bg-light" style={{ minHeight: "100vh" }}>
      <div className="card mx-auto shadow-sm" style={{ maxWidth: "700px", marginTop: "30px", borderRadius: "15px" }}>
        <article className="card-body p-4">
          <header className="mb-4 text-center">
            <h4 className="card-title fw-bold" style={{ color: "#ff6a00" }}>TẠO TÀI KHOẢN MỚI</h4>
            <p className="text-muted small">Vui lòng điền đầy đủ thông tin bên dưới</p>
          </header>

          <form onSubmit={handleSubmit}>
            {/* --- Thông tin cá nhân --- */}
            <h6 className="fw-bold mb-3"><i className="bi bi-person-circle me-2"></i>Thông tin cá nhân</h6>
            <div className="row g-3 mb-3">
              <div className="col-md-6">
                <label className="form-label small">Họ & Tên đệm</label>
                <input name="firstName" type="text" className={`form-control ${backendErrors.firstName ? 'is-invalid' : ''}`} value={form.firstName} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.firstName}</div>
              </div>
              <div className="col-md-6">
                <label className="form-label small">Tên</label>
                <input name="lastName" type="text" className={`form-control ${backendErrors.lastName ? 'is-invalid' : ''}`} value={form.lastName} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.lastName}</div>
              </div>
            </div>

            <div className="row g-3 mb-3">
              <div className="col-md-6">
                <label className="form-label small">Email</label>
                <input name="email" type="email" className={`form-control ${backendErrors.email ? 'is-invalid' : ''}`} value={form.email} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.email}</div>
              </div>
              <div className="col-md-6">
                <label className="form-label small">Số điện thoại</label>
                <input name="mobileNumber" type="text" className={`form-control ${backendErrors.mobileNumber ? 'is-invalid' : ''}`} value={form.mobileNumber} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.mobileNumber}</div>
              </div>
            </div>

            {/* --- Địa chỉ --- */}
            <h6 className="fw-bold mt-4 mb-3"><i className="bi bi-geo-alt-fill me-2"></i>Địa chỉ liên hệ</h6>
            <div className="row g-3 mb-3">
              <div className="col-md-8">
                <label className="form-label small">Số nhà, tên đường</label>
                <input name="street" type="text" className={`form-control ${backendErrors.street ? 'is-invalid' : ''}`} value={form.street} onChange={handleChange} required />
              </div>
              <div className="col-md-4">
                <label className="form-label small">Tòa nhà/Căn hộ</label>
                <input name="buildingName" type="text" className="form-control" value={form.buildingName} onChange={handleChange} placeholder="Không bắt buộc" />
              </div>
            </div>

            <div className="row g-3 mb-3">
              <div className="col-md-4">
                <label className="form-label small">Thành phố</label>
                <input name="city" type="text" className={`form-control ${backendErrors.city ? 'is-invalid' : ''}`} value={form.city} onChange={handleChange} required />
              </div>
              <div className="col-md-4">
                <label className="form-label small">Tỉnh/Trạng thái</label>
                <input name="state" type="text" className="form-control" value={form.state} onChange={handleChange} required />
              </div>
              <div className="col-md-4">
                <label className="form-label small">Mã Pincode</label>
                <input name="pincode" type="text" className={`form-control ${backendErrors.pincode ? 'is-invalid' : ''}`} value={form.pincode} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.pincode}</div>
              </div>
            </div>

            {/* --- Bảo mật --- */}
            <h6 className="fw-bold mt-4 mb-3"><i className="bi bi-shield-lock-fill me-2"></i>Bảo mật</h6>
            <div className="row g-3 mb-4">
              <div className="col-md-6">
                <label className="form-label small">Mật khẩu</label>
                <input name="password" type="password" className={`form-control ${backendErrors.password ? 'is-invalid' : ''}`} value={form.password} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.password}</div>
              </div>
              <div className="col-md-6">
                <label className="form-label small">Xác nhận mật khẩu</label>
                <input name="confirmPassword" type="password" className={`form-control ${backendErrors.confirmPassword ? 'is-invalid' : ''}`} value={form.confirmPassword} onChange={handleChange} required />
                <div className="invalid-feedback">{backendErrors.confirmPassword}</div>
              </div>
            </div>

            <button type="submit" className="btn btn-primary w-100 fw-bold py-2" style={{ backgroundColor: "#ff6a00", border: "none" }} disabled={loading}>
              {loading ? (
                <><span className="spinner-border spinner-border-sm me-2"></span> Đang xử lý...</>
              ) : "HOÀN TẤT ĐĂNG KÝ"}
            </button>
          </form>
        </article>
      </div>
      <p className="text-center mt-3 mb-5">
        Đã có tài khoản? <Link to="/login" className="text-decoration-none fw-bold" style={{ color: "#ff6a00" }}>Đăng nhập</Link>
      </p>
    </section>
  );
};

export default Register;
