import React, { useState, useEffect } from 'react';
import bannerService from '../../services/bannerService';

const BannerThree = () => {
  const [leftBanner, setLeftBanner] = useState(null);
  const [rightBanner, setRightBanner] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBanners();
  }, []);

  const fetchBanners = async () => {
    try {
      // Lấy banner ID 7 và 8 từ bannerService
      const [banner7Data, banner8Data] = await Promise.all([
        bannerService.getBannerById(7),
        bannerService.getBannerById(8)
      ]);

      setLeftBanner(banner7Data);
      setRightBanner(banner8Data);
    } catch (err) {
      console.error('Lỗi lấy banners:', err);
      // Nếu ID 7,8 không tồn tại, lấy 2 banner đầu tiên
      try {
        const allBanners = await bannerService.getAllBanners();
        if (allBanners.length > 0) {
          setLeftBanner(allBanners[0]);
          if (allBanners.length > 1) {
            setRightBanner(allBanners[1]);
          }
        }
      } catch (fallbackErr) {
        console.error('Lỗi lấy banner fallback:', fallbackErr);
      }
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (banner) => {
    if (!banner?.imageUrl) return 'https://via.placeholder.com/600x400/2d3748/ffffff?text=Banner+Image';
    return `http://localhost:8080/api/public/banners/image/${banner.imageUrl}`;
  };

  // Component cho mỗi banner card với text overlay
  const BannerCard = ({ banner, position }) => {
    const defaultContent = position === 'left' 
      ? {
          title: "Big Deal on Electronics",
          description: "This is a wider card with text below and special offers on the latest gadgets, smartphones, and tech accessories. Limited time offer!",
          buttonText: "Discover"
        }
      : {
          title: "Tech Bundle for You", 
          description: "Card with text below and amazing bundle deals on electronics. Get the best combo packages with premium quality products.",
          buttonText: "Shop Now"
        };

    return (
      <div 
        className="position-relative rounded-3 overflow-hidden h-100"
        style={{
          height: '400px',
          backgroundImage: `url(${getImageUrl(banner)})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center'
        }}
      >
        {/* Gradient Overlay */}
        <div 
          className="position-absolute top-0 start-0 w-100 h-100"
          style={{
            background: 'linear-gradient(135deg, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 100%)'
          }}
        ></div>

        {/* Content */}
        <div className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center">
          <div className="p-4 p-md-5 text-white w-100">
            {/* Title */}
            <h2 className="fw-bold mb-3 display-6">
              {banner.title || defaultContent.title}
            </h2>

            {/* Description */}
            <p className="mb-4 fs-5" style={{ maxWidth: '500px', lineHeight: '1.6' }}>
              {banner.description || defaultContent.description}
            </p>

            {/* Divider Line */}
            <div className="mb-4">
              <div 
                className="bg-warning"
                style={{ 
                  width: '60px', 
                  height: '3px'
                }}
              ></div>
            </div>

            {/* Button */}
            <button className="btn btn-warning btn-lg px-4 py-2 fw-bold">
              {banner.buttonText || defaultContent.buttonText}
            </button>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <section className="banner-three-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="placeholder-glow">
                <div className="placeholder rounded-3" style={{ height: '400px' }}></div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (!leftBanner && !rightBanner) {
    return null;
  }

  return (
    <section className="banner-three-section">
      <div className="container-fluid">
        <div className="row g-3">
          {/* Banner trái - ID 7 */}
          {leftBanner && (
            <div className="col-md-6">
              <BannerCard banner={leftBanner} position="left" />
            </div>
          )}

          {/* Banner phải - ID 8 */}
          {rightBanner && (
            <div className="col-md-6">
              <BannerCard banner={rightBanner} position="right" />
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

export default BannerThree;
