      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y giá» hÃ ng:', error);
      throw error;
    }
  },

  // ThÃªm sáº£n pháº©m vÃ o giá» hÃ ng
  addToCart: async (cartId, productId, quantity = 1) => {
    try {
      const response = await api.post(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi thÃªm vÃ o giá» hÃ ng:', error);
      throw error;
    }
  },

  // Cáº­p nháº­t sá»‘ lÆ°á»£ng
  updateQuantity: async (cartId, productId, quantity) => {
    try {
      const response = await api.put(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi cáº­p nháº­t sá»‘ lÆ°á»£ng:', error);
      throw error;
    }
  },

  // XÃ³a sáº£n pháº©m
  removeFromCart: async (cartId, productId) => {
    try {
      const response = await api.delete(`/public/carts/${cartId}/products/${productId}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi xÃ³a sáº£n pháº©m:', error);
      throw error;
    }
  },

  // LÃ m rá»—ng giá» hÃ ng
  clearCart: async (email, cartId) => {
    try {
      console.log('ðŸ§¹ Clearing cart:', { email, cartId });

      const cart = await cartService.getCart(email, cartId);

      if (cart && cart.products) {
        for (const product of cart.products) {
          await cartService.removeFromCart(cartId, product.productId);
        }
      }

      return { success: true, message: 'Cart cleared successfully' };
    } catch (error) {
      console.error('âŒ Clear cart failed:', error);
      throw error;
    }
  },

  // ========== ORDER MANAGEMENT ==========

  createOrder: async (email, cartId, paymentMethod, shippingAddress) => {
    try {
      console.log('ðŸ›’ Creating order with:', { email, cartId, paymentMethod, shippingAddress });

      const response = await api.post(
        `/public/users/${email}/carts/${cartId}/place-order`,
        null,
        {
          params: {
            paymentMethod: paymentMethod,
            shippingAddress: shippingAddress
          }
        }
      );

      console.log('âœ… Order created successfully:', response.data);
      return response.data;
    } catch (error) {
      console.error('âŒ Order creation failed:', error);
      throw error;
    }
  },

  // ========== CART ID MANAGEMENT ==========

  // Láº¥y hoáº·c táº¡o cartId
  getOrCreateCartId: async (email) => {
    try {
      const activeCart = await cartService.getActiveCart(email);
      if (activeCart && activeCart.cartId) {
        console.log("âœ… ÄÃ£ cÃ³ cartId:", activeCart.cartId);
        return activeCart.cartId;
      }

      console.log("ðŸ†• ChÆ°a cÃ³ cartId, Ä‘ang táº¡o giá» hÃ ng má»›i...");
      const newCart = await cartService.createCart(email);
      console.log("âœ… ÄÃ£ táº¡o cartId má»›i:", newCart.cartId);
      return newCart.cartId;

    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y/táº¡o cartId:', error);
      throw error;
    }
  },

  // ========== UTILITIES ==========

  // Láº¥y sá»‘ lÆ°á»£ng sáº£n pháº©m trong giá» hÃ ng
  getCartCount: async (email) => {
    try {
      const cart = await cartService.getActiveCart(email);
      if (!cart || !cart.products) return 0;
      return cart.products.reduce((total, item) => total + (item.quantity || 1), 0);
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y sá»‘ lÆ°á»£ng giá» hÃ ng:', error);
      return 0;
    }
  },

  // TÃ­nh tá»•ng tiá»n
  calculateTotal: (cartItems) => {
    if (!cartItems || cartItems.length === 0) return 0;
    return cartItems.reduce((total, item) => {
      const price = item.specialPrice !== undefined && item.specialPrice !== null 
        ? item.specialPrice 
        : item.price || 0;
      const quantity = item.quantity || 1;
      return total + (price * quantity);
    }, 0);
  },

  // Kiá»ƒm tra sáº£n pháº©m cÃ³ trong giá» hÃ ng khÃ´ng
  isProductInCart: (cart, productId) => {
    if (!cart || !cart.products) return false;
    return cart.products.some(item => item.productId === productId);
  },

  // Láº¥y thÃ´ng tin sáº£n pháº©m trong giá»
  getCartItem: (cart, productId) => {
    if (!cart || !cart.products) return null;
    return cart.products.find(item => item.productId === productId);
  },

  // Format price hiá»ƒn thá»‹
  formatPrice: (price) => {
    return (Number(price) || 0).toLocaleString('vi-VN', {
      style: 'currency',
      currency: 'VND'
    });
  },

  // Äáº¿m tá»•ng sá»‘ sáº£n pháº©m trong giá»
  getTotalItems: (cartItems) => {
    if (!cartItems || cartItems.length === 0) return 0;
    return cartItems.reduce((total, item) => total + (item.quantity || 1), 0);
  }
};

export default cartService;
