// services/cartService.js
import api from './api';

const cartService = {
  // ========== ADMIN ENDPOINTS ==========

  // Láº¥y táº¥t cáº£ giá» hÃ ng (Admin)
  getAllCarts: async () => {
    try {
      const response = await api.get('/admin/carts');
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y danh sÃ¡ch giá» hÃ ng:', error);
      throw error;
    }
  },

  // ========== PUBLIC ENDPOINTS ==========

  // Láº¥y giá» hÃ ng theo user vÃ  cartId
  getCart: async (emailId, cartId) => {
    try {
      const response = await api.get(`/public/users/${emailId}/carts/${cartId}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y giá» hÃ ng:', error);
      throw error;
    }
  },

  // ThÃªm sáº£n pháº©m vÃ o giá» hÃ ng
  addToCart: async (cartId, productId, quantity) => {
    try {
      const response = await api.post(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi thÃªm vÃ o giá» hÃ ng:', error);
      throw error;
    }
  },

  // Cáº­p nháº­t sá»‘ lÆ°á»£ng sáº£n pháº©m
  updateQuantity: async (cartId, productId, quantity) => {
    try {
      const response = await api.put(`/public/carts/${cartId}/products/${productId}/quantity/${quantity}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi cáº­p nháº­t sá»‘ lÆ°á»£ng:', error);
      throw error;
    }
  },

  // XÃ³a sáº£n pháº©m khá»i giá» hÃ ng
  removeFromCart: async (cartId, productId) => {
    try {
      const response = await api.delete(`/public/carts/${cartId}/products/${productId}`);
      return response.data;
    } catch (error) {
      console.error('âŒ Lá»—i khi xÃ³a sáº£n pháº©m:', error);
      throw error;
    }
  },

  // ========== CART MANAGEMENT ==========

  // Láº¥y giá» hÃ ng active (cÃ³ thá»ƒ dÃ¹ng getCart thay tháº¿)
  getActiveCart: async (emailId) => {
    try {
      // Giáº£ sá»­ user chá»‰ cÃ³ 1 giá» hÃ ng active, láº¥y cartId tá»« user info hoáº·c tÃ¬m cÃ¡ch khÃ¡c
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      const cartId = user.cart?.cartId;

      if (!cartId) {
        throw new Error('KhÃ´ng tÃ¬m tháº¥y cartId');
      }

      return await cartService.getCart(emailId, cartId);
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y giá» hÃ ng active:', error);
      throw error;
    }
  },

  // LÃ m rá»—ng giá» hÃ ng (xÃ³a táº¥t cáº£ sáº£n pháº©m)
  clearCart: async (emailId, cartId) => {
    try {
      console.log('ðŸ§¹ Clearing cart:', { emailId, cartId });

      // Láº¥y giá» hÃ ng hiá»‡n táº¡i
      const cart = await cartService.getCart(emailId, cartId);

      if (!cart || !cart.products || cart.products.length === 0) {
        return { success: true, message: 'Giá» hÃ ng Ä‘Ã£ trá»‘ng' };
      }

      // XÃ³a tá»«ng sáº£n pháº©m
      const deletePromises = cart.products.map(product => 
        cartService.removeFromCart(cartId, product.productId)
      );

      await Promise.all(deletePromises);

      console.log('âœ… ÄÃ£ xÃ³a táº¥t cáº£ sáº£n pháº©m khá»i giá» hÃ ng');
      return { success: true, message: 'ÄÃ£ lÃ m rá»—ng giá» hÃ ng' };
    } catch (error) {
      console.error('âŒ Lá»—i khi lÃ m rá»—ng giá» hÃ ng:', error);
      throw error;
    }
  },

  // ========== ORDER MANAGEMENT ==========

  // Táº¡o Ä‘Æ¡n hÃ ng tá»« giá» hÃ ng
  createOrder: async (emailId, cartId, paymentMethod) => {
    try {
      console.log('ðŸ›’ Creating order with:', { emailId, cartId, paymentMethod });

      const response = await api.post(
        `/public/users/${emailId}/carts/${cartId}/payments/${paymentMethod}/order`
      );

      console.log('âœ… Order created successfully:', response.data);
      return response.data;
    } catch (error) {
      console.error('âŒ Order creation failed:', error);
      console.error('Error details:', {
        status: error.response?.status,
        data: error.response?.data,
        message: error.message
      });
      throw error;
    }
  },

  // ========== UTILITIES ==========

  // TÃ­nh tá»•ng tiá»n giá» hÃ ng
  calculateTotal: (cartItems) => {
    if (!cartItems || cartItems.length === 0) return 0;

    return cartItems.reduce((total, item) => {
      const price = item.specialPrice !== undefined && item.specialPrice !== null 
        ? item.specialPrice 
        : item.price || 0;
      const quantity = item.quantity || 1;
      return total + (price * quantity);
    }, 0);
  },

  // Äáº¿m tá»•ng sá»‘ sáº£n pháº©m trong giá»
  getTotalItems: (cartItems) => {
    if (!cartItems || cartItems.length === 0) return 0;
    return cartItems.reduce((total, item) => total + (item.quantity || 1), 0);
  },

  // Láº¥y sá»‘ lÆ°á»£ng sáº£n pháº©m trong giá» hÃ ng
  getCartCount: async (emailId) => {
    try {
      const cart = await cartService.getActiveCart(emailId);
      return cartService.getTotalItems(cart?.products || []);
    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y sá»‘ lÆ°á»£ng giá» hÃ ng:', error);
      return 0;
    }
  },

  // ========== LOCAL STORAGE MANAGEMENT ==========

  // Láº¥y giá» hÃ ng tá»« localStorage
  getCartFromStorage: () => {
    try {
      return JSON.parse(localStorage.getItem('cart') || 'null');
    } catch (error) {
      return null;
    }
  },

  // Láº¥y sá»‘ lÆ°á»£ng tá»« localStorage (nhanh)
  getCartCountFromStorage: () => {
    try {
      const cart = JSON.parse(localStorage.getItem('cart') || '{}');
      return cartService.getTotalItems(cart.products || []);
    } catch (error) {
      return 0;
    }
  },

  // Cáº­p nháº­t giá» hÃ ng trong localStorage
  updateCartInStorage: (cart) => {
    try {
      localStorage.setItem('cart', JSON.stringify(cart));
    } catch (error) {
      console.error('Error updating cart in storage:', error);
    }
  },

  // XÃ³a giá» hÃ ng khá»i localStorage
  clearCartFromStorage: () => {
    try {
      localStorage.removeItem('cart');
    } catch (error) {
      console.error('Error clearing cart from storage:', error);
    }
  },

  // ========== SYNC & HELPERS ==========

  // Äá»“ng bá»™ giá» hÃ ng local vá»›i server
  syncCartWithServer: async (emailId, localCartItems = []) => {
    try {
      if (localCartItems.length === 0) return null;

      const user = JSON.parse(localStorage.getItem('user') || '{}');
      const cartId = user.cart?.cartId;

      if (!cartId) {
        console.warn('âŒ KhÃ´ng cÃ³ cartId Ä‘á»ƒ Ä‘á»“ng bá»™');
        return null;
      }

      // ThÃªm tá»«ng sáº£n pháº©m tá»« localStorage vÃ o server
      for (const item of localCartItems) {
        try {
          await cartService.addToCart(cartId, item.productId, item.quantity || 1);
        } catch (itemError) {
          console.warn(`Cannot add product ${item.productId} to cart:`, itemError);
        }
      }

      // Láº¥y láº¡i giá» hÃ ng tá»« server
      const updatedCart = await cartService.getCart(emailId, cartId);
      cartService.updateCartInStorage(updatedCart);

      return updatedCart;
    } catch (error) {
      console.error('Error syncing cart with server:', error);
      throw error;
    }
  },

  // Kiá»ƒm tra sáº£n pháº©m cÃ³ trong giá» hÃ ng khÃ´ng
  isProductInCart: (cart, productId) => {
    if (!cart || !cart.products) return false;
    return cart.products.some(item => item.productId === productId);
  },

  // Láº¥y thÃ´ng tin sáº£n pháº©m trong giá»
  getCartItem: (cart, productId) => {
    if (!cart || !cart.products) return null;
    return cart.products.find(item => item.productId === productId);
  },

  // Format price hiá»ƒn thá»‹
  formatPrice: (price) => {
    return (Number(price) || 0).toLocaleString('vi-VN', {
      style: 'currency',
      currency: 'VND'
    });
  }
};

export default cartService;
