import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

 const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const res = await authService.login(form);
    console.log("‚úÖ LOGIN RESULT:", res.data);

    const loginData = res.data;

    // ‚úÖ DEBUG CHI TI·∫æT
    console.log("üîç FULL LOGIN RESPONSE:", loginData);
    console.log("üìä Response keys:", Object.keys(loginData));

    // ‚úÖ T·∫†O USER OBJECT - CH√ö √ù FIELD NAMES
    const userToStore = {
      userId: loginData.user_id,        // ‚úÖ user_id t·ª´ Java
      user_id: loginData.user_id,       // ‚úÖ user_id t·ª´ Java  
      email: loginData.email,
      first_name: loginData.first_name, // ‚úÖ first_name t·ª´ Java
      last_name: loginData.last_name    // ‚úÖ last_name t·ª´ Java
    };

    console.log("üíæ User data to save:", userToStore);

    // ‚úÖ L∆ØU V√ÄO LOCALSTORAGE
    localStorage.setItem("user", JSON.stringify(userToStore));

    // ‚úÖ L∆ØU TOKEN - CH√ö √ù FIELD NAME
    if (loginData['jwt-token']) {
      localStorage.setItem("token", loginData['jwt-token']);
      console.log("üîê Token saved");
    }

    // ‚úÖ KI·ªÇM TRA L·∫†I
    const savedUser = JSON.parse(localStorage.getItem("user"));
    console.log("üì¶ Saved user in localStorage:", savedUser);

    alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
    window.location.href = "/";

  } catch (err) {
    console.error("‚ùå LOGIN ERROR:", err);
    alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!");
  }
};
  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
