import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../assets/fonts/fontawesome/css/all.min.css";
import "../assets/css/style.css"; 

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/product?search=${searchTerm.trim()}`);
    }
  };

  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) { console.error(err); }
      }
    } else { setUser(null); setUserInfo(null); }
  }, []);

  const loadCartCount = async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      if (currentUser?.email) {
        const count = await cartService.getCartCount(currentUser.email);
        setCartCount(count);
      } else {
        const localCart = cartService.getCartFromStorage();
        setCartCount(cartService.getTotalItems(localCart?.products || []));
      }
    } catch (error) { setCartCount(0); }
  };

  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } catch (err) { console.error(err); } finally { setLoadingCategories(false); }
  };

  useEffect(() => {
    initUser();
    fetchCategories();
    window.addEventListener("authUpdated", initUser);
    return () => window.removeEventListener("authUpdated", initUser);
  }, [initUser]);

  useEffect(() => {
    loadCartCount();
    window.addEventListener('cartUpdated', loadCartCount);
    return () => window.removeEventListener('cartUpdated', loadCartCount);
  }, [user]);

  // FIX: Điều hướng ra link cụ thể cho từng danh mục
  const handleCategoryClick = (category) => {
    const id = category.categoryId || category.id;
    navigate(`/product/category/${id}`);
  };

  const handleLogout = () => {
    Swal.fire({
      title: 'Bạn muốn đăng xuất?',
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#ff6a00',
      confirmButtonText: 'Đăng xuất',
      cancelButtonText: 'Hủy'
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.clear();
        setUser(null);
        setUserInfo(null);
        setCartCount(0);
        window.dispatchEvent(new Event("authUpdated"));
        navigate("/login");
      }
    });
  };

  const renderUserName = () => {
    if (!user) return "Tài khoản";
    return userInfo?.firstName || user.firstName || user.email?.split('@')[0] || "User";
  };

  return (
    <header className="section-header sticky-top bg-white border-bottom shadow-sm">
      {/* Top Bar Nhỏ gọn */}
      <div className="py-1 text-center text-white small" style={{ background: '#333', fontSize: '12px' }}>
        Miễn phí vận chuyển cho đơn hàng từ 500k | Hotline: 1900 xxxx
      </div>

      <section className="header-main py-3">
        <div className="container">
          <div className="row align-items-center">
            {/* Logo */}
            <div className="col-lg-3 col-md-4 col-6">
              <Link to="/" className="text-decoration-none">
                <h2 className="fw-bold mb-0" style={{ color: '#222', letterSpacing: '-1.5px' }}>
                  Shop<span style={{ color: '#ff6a00' }}>BeAnh</span>
                </h2>
              </Link>
            </div>

            {/* Search Bar Hiện Đại */}
            <div className="col-lg-5 col-md-4 d-none d-md-block">
              <form className="search-wrap" onSubmit={handleSearchSubmit}>
                <div className="input-group overflow-hidden rounded-pill border shadow-sm" style={{ background: '#f5f5f5' }}>
                  <input 
                    type="text" 
                    className="form-control border-0 bg-transparent px-4 shadow-none" 
                    placeholder="Tìm kiếm sản phẩm..." 
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                  <button className="btn border-0 text-white px-3" type="submit" style={{ background: '#ff6a00' }}>
                    <i className="fa fa-search"></i>
                  </button>
                </div>
              </form>
            </div>

            {/* Icons User & Cart */}
            <div className="col-lg-4 col-md-4 col-6">
              <div className="d-flex justify-content-end align-items-center gap-4">

                {/* User */}
                <div className="dropdown position-relative user-nav-item">
                  <div className="d-flex align-items-center gap-2 pointer-cursor" style={{ cursor: 'pointer' }}>
                    <div className="icon-circle border d-flex align-items-center justify-content-center" style={{ width: '40px', height: '40px', borderRadius: '50%' }}>
                      <i className="fa fa-user"></i>
                    </div>
                    <div className="d-none d-lg-block">
                      <div className="small text-muted mb-0" style={{ fontSize: '10px', lineHeight: '1' }}>Tài khoản</div>
                      <span className="fw-bold small">{renderUserName()}</span>
                    </div>
                  </div>

                  <ul className="dropdown-menu-custom shadow-lg border-0 rounded-3">
                    {!user ? (
                      <>
                        <li><Link to="/login" className="dropdown-item-modern">Đăng nhập</Link></li>
                        <li><Link to="/register" className="dropdown-item-modern border-top">Đăng ký</Link></li>
                      </>
                    ) : (
                      <>
                        <li><Link to="/profile" className="dropdown-item-modern">Cá nhân</Link></li>
                        <li><Link to="/orders" className="dropdown-item-modern">Đơn hàng</Link></li>
                        <li><hr className="m-0" /></li>
                        <li><button onClick={handleLogout} className="dropdown-item-modern text-danger fw-bold border-0 bg-transparent w-100 text-start">Đăng xuất</button></li>
                      </>
                    )}
                  </ul>
                </div>

                {/* Giỏ hàng */}
                <Link to="/cart" className="text-dark text-decoration-none d-flex align-items-center gap-2">
                  <div className="icon-circle position-relative border d-flex align-items-center justify-content-center" style={{ width: '40px', height: '40px', borderRadius: '50%', background: '#fff5ee' }}>
                    <i className="fa fa-shopping-cart" style={{ color: '#ff6a00' }}></i>
                    <span className="badge rounded-pill bg-danger position-absolute top-0 start-100 translate-middle border border-white" style={{ fontSize: '10px' }}>
                      {cartCount}
                    </span>
                  </div>
                  <div className="d-none d-lg-block">
                      <div className="small text-muted mb-0" style={{ fontSize: '10px', lineHeight: '1' }}>Giỏ hàng</div>
                      <span className="fw-bold small">Sản phẩm</span>
                  </div>
                </Link>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Nav Menu */}
      <nav className="nav-bar-white bg-white border-top d-none d-lg-block">
        <div className="container">
          <ul className="nav d-flex align-items-center">
            <li className="nav-item dropdown-hover-container me-4">
              <Link className="nav-link text-dark fw-bold py-3 px-0 d-flex align-items-center" to="#">
                <i className="fa fa-bars me-2 text-orange"></i> TẤT CẢ DANH MỤC
              </Link>
              <div className="mega-menu-modern shadow-lg rounded-bottom-3">
                <div className="scrollable-content p-2">
                  {loadingCategories ? (
                    <div className="p-3 text-center small text-muted">Đang tải...</div>
                  ) : (
                    categories.map((cat, idx) => (
                      <button key={idx} className="category-link-item" onClick={() => handleCategoryClick(cat)}>
                        {cat.categoryName || cat.name}
                      </button>
                    ))
                  )}
                </div>
              </div>
            </li>

            {!loadingCategories && categories.slice(0, 7).map((cat, idx) => (
              <li className="nav-item" key={`nav-q-${idx}`}>
                <button className="nav-link-item-modern" onClick={() => handleCategoryClick(cat)}>
                  {cat.categoryName || cat.name}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </nav>

      <style>{`
        .text-orange { color: #ff6a00; }
        .dropdown-menu-custom {
          display: none; position: absolute; top: 100%; right: 0;
          background: #fff; min-width: 180px; z-index: 1000; padding: 5px 0;
        }
        .user-nav-item:hover .dropdown-menu-custom { display: block; animation: fadeIn 0.2s; }
        .dropdown-item-modern {
          display: block; padding: 10px 20px; color: #444; text-decoration: none; font-size: 14px;
        }
        .dropdown-item-modern:hover { background: #fff5ee; color: #ff6a00; }

        .mega-menu-modern {
          display: none; position: absolute; top: 100%; left: 0;
          background: #fff; width: 260px; z-index: 1000;
        }
        .dropdown-hover-container:hover .mega-menu-modern { display: block; animation: fadeIn 0.2s; }
        .category-link-item {
          display: block; width: 100%; text-align: left; border: none; background: none;
          padding: 10px 15px; font-size: 14px; color: #555; transition: 0.2s;
        }
        .category-link-item:hover { background: #fff5ee; color: #ff6a00; padding-left: 20px; }

        .nav-link-item-modern {
          border: none; background: none; padding: 15px 15px; font-size: 13px;
          font-weight: 600; color: #555; text-transform: uppercase; transition: 0.2s;
        }
        .nav-link-item-modern:hover { color: #ff6a00; }

        @keyframes fadeIn { from { opacity: 0; transform: translateY(5px); } to { opacity: 1; transform: translateY(0); } }
      `}</style>
    </header>
  );
};

export default Header;
