import React from "react";
import { BrowserRouter as Router } from "react-router-dom"; // ✅ Thêm Router
import 'bootstrap/dist/css/bootstrap.min.css';
import './assets/fonts/fontawesome/css/all.min.css';

import Header from "./layouts/Header";
import Footer from "./layouts/Footer";
import Main from "./layouts/Main";
import Login from "./pages/auth/Login";
import Register from "./pages/auth/Register";
import ProductList from "./pages/product/ProductList";
import ProductDetail from "./pages/product/ProductDetail";

import "./assets/sass/ui.scss";
import "./assets/sass/bootstrap.scss";
import "./assets/sass/responsive.scss";

function App() {
  return (
    <Router> {/* ✅ Bao bọc toàn bộ app bằng Router */}
      <div>
        <Header />
        <Router>
          <Route path="/" element={<Main />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/product" element={<ProductList />} /> {/* ✅ Sửa thành /products cho consistency */}
          <Route path="/product/:productId" element={<ProductDetail />} />
        </Router>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
