import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import Swal from 'sweetalert2';

const Checkout = () => {
    const [cart, setCart] = useState(null);
    const [loading, setLoading] = useState(true);
    const [processing, setProcessing] = useState(false);
    const [user, setUser] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState('COD');
    const [shippingAddress, setShippingAddress] = useState('');
    const navigate = useNavigate();

    // Lấy thông tin user từ localStorage
    const currentUser = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        loadCartAndUser();
    }, []);

    const loadCartAndUser = async () => {
        if (!currentUser || !currentUser.email) {
            Swal.fire('Thông báo', 'Vui lòng đăng nhập để tiếp tục thanh toán', 'info');
            navigate('/login');
            return;
        }

        try {
            setLoading(true);
            const cartData = await cartService.getActiveCart(currentUser.email);

            if (!cartData || !cartData.products || cartData.products.length === 0) {
                await Swal.fire({
                    icon: 'warning',
                    title: 'Giỏ hàng trống',
                    text: 'Bạn không có sản phẩm nào để thanh toán.',
                });
                navigate('/cart');
                return;
            }

            setCart(cartData);
            setUser(currentUser);
            setShippingAddress(localStorage.getItem('shippingAddress') || '');

        } catch (error) {
            console.error('❌ Lỗi tải thông tin:', error);
            Swal.fire('Lỗi', 'Không thể tải dữ liệu giỏ hàng', 'error');
        } finally {
            setLoading(false);
        }
    };

    const handlePlaceOrder = async () => {
        if (!shippingAddress.trim()) {
            Swal.fire('Thông tin trống', 'Vui lòng nhập địa chỉ giao hàng', 'warning');
            return;
        }

        const result = await Swal.fire({
            title: 'Xác nhận đặt hàng?',
            text: `Tổng số tiền cần thanh toán là ${finalAmount.toLocaleString()}₫`,
            icon: 'question',
            showCancelButton: true,
            confirmButtonColor: '#ffc107',
            cancelButtonColor: '#6c757d',
            confirmButtonText: 'Đồng ý đặt hàng',
            cancelButtonText: 'Hủy'
        });

        if (result.isConfirmed) {
            try {
                setProcessing(true);
                localStorage.setItem('shippingAddress', shippingAddress);

                const order = await cartService.createOrder(
                    user.email, 
                    cart.cartId, 
                    paymentMethod, 
                    shippingAddress
                );

                await cartService.clearCart(user.email, cart.cartId);

                await Swal.fire({
                    icon: 'success',
                    title: 'Thành công!',
                    text: 'Đơn hàng của bạn đã được tiếp nhận.',
                    timer: 2000,
                    showConfirmButton: false
                });

                navigate('/', { state: { message: 'Đặt hàng thành công!' } });

            } catch (error) {
                Swal.fire('Thất bại', error.response?.data?.message || 'Có lỗi xảy ra khi đặt hàng', 'error');
            } finally {
                setProcessing(false);
            }
        }
    };

    const totalAmount = cart ? cartService.calculateTotal(cart.products) : 0;
    const finalAmount = totalAmount; // Phí ship = 0

    if (loading) {
        return (
            <div className="container mt-5 text-center">
                <div className="spinner-border text-primary" role="status"></div>
                <p className="mt-2">Đang tải thông tin thanh toán...</p>
            </div>
        );
    }

    return (
        <div className="container mt-4 mb-5">
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><Link to="/">Trang chủ</Link></li>
                    <li className="breadcrumb-item"><Link to="/cart">Giỏ hàng</Link></li>
                    <li className="breadcrumb-item active">Thanh toán</li>
                </ol>
            </nav>

            <h2 className="text-primary mb-4 fw-bold">Thanh toán đơn hàng</h2>

            <div className="row">
                {/* Cột trái: Form thông tin */}
                <div className="col-lg-8">
                    <div className="card shadow-sm border-0 mb-4">
                        <div className="card-header bg-white fw-bold py-3">
                            <i className="fas fa-shipping-fast me-2"></i>Thông tin nhận hàng
                        </div>
                        <div className="card-body">
                            <div className="row g-3">
                                <div className="col-md-6">
                                    <label className="form-label small fw-bold">Họ và tên</label>
                                    <input type="text" className="form-control bg-light" value={`${user?.firstName || ''} ${user?.lastName || ''}`} readOnly />
                                </div>
                                <div className="col-md-6">
                                    <label className="form-label small fw-bold">Số điện thoại</label>
                                    <input type="text" className="form-control bg-light" value={user?.mobileNumber || ''} readOnly />
                                </div>
                                <div className="col-12">
                                    <label className="form-label small fw-bold">Địa chỉ giao hàng chi tiết *</label>
                                    <textarea 
                                        className="form-control" 
                                        rows="3" 
                                        placeholder="Số nhà, tên đường, phường/xã..."
                                        value={shippingAddress}
                                        onChange={(e) => setShippingAddress(e.target.value)}
                                    ></textarea>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="card shadow-sm border-0">
                        <div className="card-header bg-white fw-bold py-3">
                            <i className="fas fa-wallet me-2"></i>Phương thức thanh toán
                        </div>
                        <div className="card-body">
                            {['COD', 'BANKING', 'MOMO'].map((method) => (
                                <div className="form-check mb-3 p-3 border rounded" key={method}>
                                    <input 
                                        className="form-check-input ms-0 me-3" 
                                        type="radio" 
                                        name="payment" 
                                        id={method}
                                        checked={paymentMethod === method}
                                        onChange={() => setPaymentMethod(method)}
                                    />
                                    <label className="form-check-label fw-bold" htmlFor={method}>
                                        {method === 'COD' && "Thanh toán khi nhận hàng (COD)"}
                                        {method === 'BANKING' && "Chuyển khoản ngân hàng"}
                                        {method === 'MOMO' && "Ví điện tử MoMo"}
                                    </label>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>

                {/* Cột phải: Tóm tắt đơn hàng */}
                <div className="col-lg-4">
                    <div className="card shadow-sm border-0 sticky-top" style={{ top: '20px' }}>
                        <div className="card-header bg-white fw-bold py-3">Đơn hàng của bạn</div>
                        <div className="card-body">
                            {cart.products.map((item, idx) => (
                                <div key={idx} className="d-flex justify-content-between mb-2 small">
                                    <span className="text-truncate" style={{maxWidth: '200px'}}>{item.productName} x{item.quantity}</span>
                                    <span>{((item.specialPrice || item.price) * item.quantity).toLocaleString()}₫</span>
                                </div>
                            ))}
                            <hr />
                            <div className="d-flex justify-content-between mb-2">
                                <span>Tạm tính:</span>
                                <span>{totalAmount.toLocaleString()}₫</span>
                            </div>
                            <div className="d-flex justify-content-between mb-4">
                                <span>Phí vận chuyển:</span>
                                <span className="text-success">Miễn phí</span>
                            </div>
                            <div className="d-flex justify-content-between mb-4">
                                <span className="fw-bold">Tổng cộng:</span>
                                <span className="text-danger fs-4 fw-bold">{finalAmount.toLocaleString()}₫</span>
                            </div>

                            <button 
                                className="btn btn-warning w-100 py-3 fw-bold shadow-sm"
                                onClick={handlePlaceOrder}
                                disabled={processing}
                            >
                                {processing ? 'ĐANG XỬ LÝ...' : 'XÁC NHẬN ĐẶT HÀNG'}
                            </button>
                            <Link to="/cart" className="btn btn-link w-100 text-muted mt-2 text-decoration-none small">
                                ← Quay lại giỏ hàng
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Checkout;
