import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CategoryList = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showAll, setShowAll] = useState(false);


  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    setLoading(true);
    try {
      setCategories(response.data.content || response.data);
    } catch (error) {
      console.error("Lỗi tải danh mục:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="category-section container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h4 className="fw-bold mb-0 text-uppercase">Danh mục quan tâm</h4>
        <button 
          className="btn btn-orange btn-sm rounded-pill px-4 shadow-sm"
          onClick={() => setShowAll(!showAll)}
        >
          {showAll ? 'Thu gọn' : 'Xem tất cả'} <i className={`fa fa-chevron-${showAll ? 'up' : 'right'} ms-1`}></i>
        </button>
      </div>

      {/* HIỂN THỊ DẠNG TRƯỢT NGANG HOẶC LƯỚI */}
      <div className={showAll ? "row g-3" : "d-flex overflow-auto pb-3 custom-scrollbar"}>
        {categories.map((cat) => (
          <div 
            key={cat.categoryId} 
            className={showAll ? "col-lg-2 col-md-3 col-4" : "flex-shrink-0 me-3"}
            style={!showAll ? { width: '140px' } : {}}
          >
            <Link to={`/category/${cat.categoryId}`} className="text-decoration-none">
              <div className="cate-card text-center p-3 shadow-sm bg-white rounded-3 hover-up h-100 border">
                <div className="icon-wrap mb-2">
                  {/* Nếu DB không có ảnh cate, dùng ảnh mặc định dựa trên tên */}
                  <img 
                    src={cat.image || `https://ui-avatars.com/api/?name=${cat.categoryName}&background=random&color=fff`} 
                    alt={cat.categoryName}
                    className="img-fluid rounded-circle"
                    style={{ width: '60px', height: '60px', objectFit: 'cover' }}
                  />
                </div>
                <small className="fw-bold text-dark d-block text-truncate">
                  {cat.categoryName}
                </small>
              </div>
            </Link>
          </div>
        ))}
      </div>

      <style>{`
        .hover-up { transition: 0.3s; }
        .hover-up:hover { transform: translateY(-5px); border-color: #fd7e14 !important; }
        .btn-orange { background-color: #fd7e14; color: white; border: none; }
        .btn-orange:hover { background-color: #e8590c; color: white; }
        
        /* Thanh cuộn ngang tinh tế */
        .custom-scrollbar::-webkit-scrollbar { height: 4px; }
        .custom-scrollbar::-webkit-scrollbar-track { background: #f1f1f1; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: #fd7e14; border-radius: 10px; }
        
        .custom-scrollbar {
          scrollbar-width: thin;
          scrollbar-color: #fd7e14 #f1f1f1;
          -webkit-overflow-scrolling: touch;
        }
      `}</style>
    </section>
  );
};

export default CategoryList;
