import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productService from "../../services/productService";

const ProductDetail = () => {
  const { id } = useParams(); // ✅ Lấy ID từ URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    if (!id) return; // tránh undefined
    productService
      .getProductById(id)
      .then((data) => setProduct(data))
      .catch((err) => console.error("❌ Không tìm thấy sản phẩm:", err));
  }, [id]);

  if (!product) return <p>Đang tải sản phẩm...</p>;

  return (
    <div>
      <h2>{product.product_name}</h2>
      <img
        src={productService.getProductImage(product.image)}
        alt={product.product_name}
        style={{ width: "300px" }}
      />
      <p>{product.description}</p>
      <p>Giá: {product.price.toLocaleString()} ₫</p>
      {product.special_price && <p>Giá khuyến mãi: {product.special_price.toLocaleString()} ₫</p>}
    </div>
  );
};

export default ProductDetail;
