import { useEffect, useState } from "react";
import productService from "../../services/productService";

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    productService
      .getAllProducts()
      .then((data) => {
        setProducts(data.content || data); // nếu API trả về dạng Page
      })
      .catch((err) => console.error("❌ Lỗi khi tải sản phẩm:", err));
  }, []);

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4">
        <h3 className="title-section">Recommended items</h3>
      </header>

      <div className="row">
        {products.map((p) => (
          <div key={p.productId} className="col-xl-3 col-lg-3 col-md-4 col-6">
            <div className="card card-product-grid shadow-sm">
              <a href="#" className="img-wrap">
                <img
                  src={productService.getProductImage(p.imageName)}
                  alt={p.productName}
                  onError={(e) =>
                    (e.target.src =
                      "https://cdn-icons-png.flaticon.com/512/679/679922.png")
                  }
                  style={{
                    width: "100%",
                    height: "250px",
                    objectFit: "cover",
                    borderRadius: "10px",
                  }}
                />
              </a>

              <figcaption className="info-wrap">
                <ul className="rating-stars mb-1">
                  <li style={{ width: "80%" }} className="stars-active">
                    <img
                      src={require("../../assets/images/icons/stars-active.svg")}
                      alt=""
                    />
                  </li>
                  <li>
                    <img
                      src={require("../../assets/images/icons/starts-disable.svg")}
                      alt=""
                    />
                  </li>
                </ul>

                <div>
                  <a href="#" className="text-muted">
                    {p.category?.categoryName || "Category"}
                  </a>
                  <a href="#" className="title d-block text-truncate">
                    {p.productName}
                  </a>
                </div>

                <div className="price h5 mt-2 text-danger">
                  {p.discountedPrice
                    ? `${p.discountedPrice.toLocaleString()} ₫`
                    : `${p.price.toLocaleString()} ₫`}
                </div>
              </figcaption>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProductList;
