import React, { useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import Swal from 'sweetalert2';

const PaymentSuccess = () => {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();

    useEffect(() => {
        const finalizeOrder = async () => {
            const code = searchParams.get('vnp_ResponseCode');
            const pending = JSON.parse(localStorage.getItem('pendingOrder'));

            if (code === "00" && pending) {
                try {
                    // Gọi API Java để lưu đơn hàng chính thức
                    await cartService.createOrder(
                        pending.email, pending.cartId, 'BANKING', pending.address, pending.phone
                    );
                    await cartService.clearCart(pending.email, pending.cartId);
                    localStorage.removeItem('pendingOrder');
                    
                    Swal.fire('Thành công', 'Đã lưu đơn hàng thành công!', 'success');
                    navigate('/orders');
                } catch (e) {
                    Swal.fire('Lỗi', 'Thanh toán xong nhưng không lưu được đơn!', 'error');
                }
            } else {
                Swal.fire('Thất bại', 'Giao dịch bị hủy hoặc lỗi', 'error');
                navigate('/cart');
            }
        };
        finalizeOrder();
    }, []);

    return <div className="text-center mt-5">Đang xác nhận thanh toán...</div>;
};

export default PaymentSuccess;
