// services/bannerService.js
import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

class BannerService {
  async getActiveBanners() {
    try {
      console.log('üîÑ ƒêang g·ªçi API banners...');
      const response = await axios.get(`${API_URL}/public/banners/active`);
      console.log('üì¶ Response t·ª´ API:', response.data);

      // ‚úÖ X·ª≠ l√Ω nhi·ªÅu c·∫•u tr√∫c response kh√°c nhau
      let banners = [];

      if (Array.isArray(response.data)) {
        // N·∫øu response l√† array tr·ª±c ti·∫øp
        banners = response.data;
      } else if (response.data && Array.isArray(response.data.content)) {
        // N·∫øu response c√≥ c·∫•u tr√∫c Spring Page (content array)
        banners = response.data.content;
      } else if (response.data && Array.isArray(response.data.data)) {
        // N·∫øu response c√≥ c·∫•u tr√∫c data array
        banners = response.data.data;
      } else if (response.data && typeof response.data === 'object') {
        // N·∫øu response l√† object ƒë∆°n l·∫ª, chuy·ªÉn th√†nh array
        banners = [response.data];
      } else {
        console.warn('‚ö†Ô∏è C·∫•u tr√∫c response kh√¥ng x√°c ƒë·ªãnh:', response.data);
        banners = [];
      }

      console.log('‚úÖ Banners sau khi x·ª≠ l√Ω:', banners);
      return banners;

    } catch (error) {
      console.error('‚ùå L·ªói khi l·∫•y banners:', error);
      throw error;
    }
  }

  async getAllBanners() {
    try {
      const response = await axios.get(`${API_URL}/public/banners`);

      let banners = [];
      if (Array.isArray(response.data)) {
        banners = response.data;
      } else if (response.data && Array.isArray(response.data.content)) {
        banners = response.data.content;
      } else {
        banners = [];
      }

      return banners;
    } catch (error) {
      console.error('‚ùå L·ªói khi l·∫•y t·∫•t c·∫£ banners:', error);
      throw error;
    }
  }
}

export default new BannerService();
