import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: "",
    password: "",
    confirmPassword: "",
    street: "",
    buildingName: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    if (backendErrors[e.target.name]) {
      setBackendErrors({ ...backendErrors, [e.target.name]: "" });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setBackendErrors({});

    if (form.password !== form.confirmPassword) {
      setBackendErrors({ confirmPassword: "Mật khẩu nhập lại không khớp!" });
      Swal.fire({
        icon: "error",
        title: "Lỗi mật khẩu",
        text: "Mật khẩu xác nhận phải giống với mật khẩu đã nhập.",
      });
      return;
    }

    setLoading(true);

    try {
      const payload = {
        firstName: form.first_name,
        lastName: form.last_name,
        mobileNumber: form.mobile_number,
        email: form.email,
        password: form.password,
        role: [{ roleId: 102, roleName: "USER" }],
      };

      if (
        form.street || form.buildingName || form.city ||
        form.state || form.country || form.pincode
      ) {
        payload.address = {
          street: form.street,
          buildingName: form.buildingName,
          city: form.city,
          state: form.state,
          country: form.country,
          pincode: form.pincode,
        };
      }

      await authService.register(payload);

      Swal.fire({
        icon: "success",
        title: "Đăng ký thành công!",
        confirmButtonText: "Đăng nhập",
      }).then(() => navigate("/login"));

    } catch (error) {
      const errorData = error.response?.data;
      if (typeof errorData === "object") {
        setBackendErrors({
          first_name: errorData.firstName,
          last_name: errorData.lastName,
          email: errorData.email,
          mobile_number: errorData.mobileNumber,
          password: errorData.password
        });
      } else {
        Swal.fire("Lỗi", "Đăng ký thất bại", "error");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="section-content padding-y bg-light">
      <div className="card mx-auto shadow-sm" style={{ maxWidth: "600px", marginTop: 40 }}>
        <article className="card-body p-4">
          <h4 className="text-center mb-3 text-warning fw-bold">TẠO TÀI KHOẢN</h4>

          <form onSubmit={handleSubmit}>

            {/* USER INFO */}
            <div className="row">
              <div className="col-md-6 mb-3">
                <input className="form-control" name="first_name" placeholder="Họ"
                  value={form.first_name} onChange={handleChange} required />
              </div>
              <div className="col-md-6 mb-3">
                <input className="form-control" name="last_name" placeholder="Tên"
                  value={form.last_name} onChange={handleChange} required />
              </div>
            </div>

            <input className="form-control mb-3" name="email" placeholder="Email"
              value={form.email} onChange={handleChange} required />

            <input className="form-control mb-3" name="mobile_number" placeholder="Số điện thoại"
              value={form.mobile_number} onChange={handleChange} required />

            <div className="row mb-3">
              <div className="col-md-6">
                <input className="form-control" type="password" name="password"
                  placeholder="Mật khẩu" value={form.password} onChange={handleChange} required />
              </div>
              <div className="col-md-6">
                <input className="form-control" type="password" name="confirmPassword"
                  placeholder="Nhập lại mật khẩu" value={form.confirmPassword} onChange={handleChange} required />
              </div>
            </div>

            {/* ADDRESS INFO */}
            <h6 className="fw-bold text-muted mt-3">Địa chỉ (không bắt buộc)</h6>

            <input className="form-control mb-2" name="street" placeholder="Đường"
              value={form.street} onChange={handleChange} />

            <input className="form-control mb-2" name="buildingName" placeholder="Tòa nhà / Khu"
              value={form.buildingName} onChange={handleChange} />

            <div className="row">
              <div className="col-md-4">
                <input className="form-control mb-2" name="city" placeholder="Thành phố"
                  value={form.city} onChange={handleChange} />
              </div>
              <div className="col-md-4">
                <input className="form-control mb-2" name="state" placeholder="Tỉnh"
                  value={form.state} onChange={handleChange} />
              </div>
              <div className="col-md-4">
                <input className="form-control mb-2" name="pincode" placeholder="Mã bưu điện"
                  value={form.pincode} onChange={handleChange} />
              </div>
            </div>

            <input className="form-control mb-3" name="country" placeholder="Quốc gia"
              value={form.country} onChange={handleChange} />

            <button className="btn btn-warning w-100 fw-bold" disabled={loading}>
              {loading ? "Đang xử lý..." : "ĐĂNG KÝ"}
            </button>

          </form>

          <p className="text-center mt-3">
            Đã có tài khoản? <Link to="/login">Đăng nhập</Link>
          </p>
        </article>
      </div>
    </section>
  );
};

export default Register;
