import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import userService from "../../services/userService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await authService.login(form);
      console.log("‚úÖ LOGIN RESULT:", res.data);

      const loginData = res.data;

      // ‚úÖ TR∆Ø·ªúNG H·ª¢P 1: N·∫øu API login tr·∫£ v·ªÅ ƒë·∫ßy ƒë·ªß user info
      if (loginData.user_id || loginData.userId) {
        const userToStore = {
          userId: loginData.user_id || loginData.userId,
          user_id: loginData.user_id || loginData.userId, // ‚úÖ Th√™m c·∫£ 2
          email: loginData.email,
          first_name: loginData.first_name || "", // ‚úÖ CH·ªà D√ôNG first_name
          last_name: loginData.last_name || ""    // ‚úÖ CH·ªà D√ôNG last_name
        };

        console.log("üíæ Saving user from login response:", userToStore);
        localStorage.setItem("user", JSON.stringify(userToStore));

      } 
      // ‚úÖ TR∆Ø·ªúNG H·ª¢P 2: N·∫øu ch·ªâ tr·∫£ v·ªÅ token ho·∫∑c userId, c·∫ßn g·ªçi API user
      else if (loginData.userId || loginData.token) {
        const userId = loginData.userId;

        // G·ªçi API ƒë·ªÉ l·∫•y th√¥ng tin user ƒë·∫ßy ƒë·ªß
        try {
          const userProfile = await userService.getUserProfile(userId);
          console.log("‚úÖ User profile from API:", userProfile);

          const userToStore = {
            userId: userProfile.user_id || userProfile.userId,
            user_id: userProfile.user_id || userProfile.userId, // ‚úÖ Th√™m c·∫£ 2
            email: userProfile.email,
            first_name: userProfile.first_name || "", // ‚úÖ CH·ªà D√ôNG first_name
            last_name: userProfile.last_name || ""    // ‚úÖ CH·ªà D√ôNG last_name
          };

          console.log("üíæ Saving user from profile API:", userToStore);
          localStorage.setItem("user", JSON.stringify(userToStore));

        } catch (profileError) {
          console.error("‚ùå Error fetching user profile:", profileError);
          // Fallback: l∆∞u v·ªõi th√¥ng tin c∆° b·∫£n
          const userToStore = {
            userId: userId,
            user_id: userId, // ‚úÖ Th√™m c·∫£ 2
            email: form.email,
            first_name: "", // ‚úÖ CH·ªà D√ôNG first_name
            last_name: ""   // ‚úÖ CH·ªà D√ôNG last_name
          };
          localStorage.setItem("user", JSON.stringify(userToStore));
        }
      }
      // ‚úÖ TR∆Ø·ªúNG H·ª¢P 3: Kh√¥ng c√≥ th√¥ng tin user
      else {
        console.warn("‚ö†Ô∏è Login response kh√¥ng c√≥ user info, s·ª≠ d·ª•ng t·∫°m th·ªùi");
        const userToStore = {
          userId: `temp_${Date.now()}`,
          user_id: `temp_${Date.now()}`, // ‚úÖ Th√™m c·∫£ 2
          email: form.email,
          first_name: "", // ‚úÖ CH·ªà D√ôNG first_name
          last_name: ""   // ‚úÖ CH·ªà D√ôNG last_name
        };
        localStorage.setItem("user", JSON.stringify(userToStore));
      }

      // ‚úÖ L∆∞u token n·∫øu c√≥
      if (loginData.token) {
        localStorage.setItem("token", loginData.token);
      }

      alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
      window.location.href = "/"; // ‚úÖ Reload ƒë·ªÉ c·∫≠p nh·∫≠t Header

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i! Vui l√≤ng ki·ªÉm tra email v√† m·∫≠t kh·∫©u.");
    }
  };

  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              name="email"
              className="form-control"
              placeholder="Email"
              type="email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <input
              name="password"
              className="form-control"
              placeholder="Password"
              type="password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-block">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
