import { useEffect, useState, useCallback } from "react";
import { Link, useLocation } from "react-router-dom";
import productService from "../../services/productService";
import categoryService from "../../services/categoryService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [pageNumber, setPageNumber] = useState(0);
  const [pageSize] = useState(8); // C·ªë ƒë·ªãnh 8 s·∫£n ph·∫©m/trang
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  const [filters, setFilters] = useState({
    categoryId: location.state?.selectedCategory || "",
    searchKeyword: ""
  });

  // 1. T·∫£i danh m·ª•c (Ch·ªâ ch·∫°y 1 l·∫ßn)
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await categoryService.getAllCategories();
        setCategories(response || []);
      } catch (err) {
        console.error("‚ùå L·ªói khi t·∫£i danh m·ª•c:", err);
      }
    };
    fetchCategories();
  }, []);

  // 2. H√†m t·∫£i s·∫£n ph·∫©m (D√πng useCallback ƒë·ªÉ tr√°nh render d∆∞ th·ª´a)
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    try {
      let response;
      const { categoryId, searchKeyword } = filters;

      // Backend th∆∞·ªùng d√πng 0-based index (Trang 0 l√† trang 1)
      const currentPage = pageNumber; 

      if (categoryId && searchKeyword) {
        response = await productService.getProductsByCategoryAndSearch(categoryId, searchKeyword, currentPage, pageSize);
      } else if (categoryId) {
        response = await productService.getProductsByCategory(categoryId, currentPage, pageSize);
      } else if (searchKeyword) {
        response = await productService.searchProducts(searchKeyword, currentPage, pageSize);
      } else {
        response = await productService.getAllProducts(currentPage, pageSize);
      }

      setProducts(response?.content || []);
      setTotalPages(response?.totalPages || 0);
    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
      setProducts([]);
    } finally {
      setLoading(false);
    }
  }, [pageNumber, pageSize, filters]);

  // 3. Theo d√µi s·ª± thay ƒë·ªïi c·ªßa filters/page ƒë·ªÉ t·∫£i d·ªØ li·ªáu
  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  // 4. X·ª≠ l√Ω khi Category ƒë∆∞·ª£c g·ª≠i t·ª´ Header
  useEffect(() => {
    if (location.state?.selectedCategory) {
      setFilters(prev => ({
        ...prev,
        categoryId: location.state.selectedCategory
      }));
      setPageNumber(0);
      // X√≥a state ƒë·ªÉ kh√¥ng b·ªã l·∫∑p l·∫°i khi F5
      window.history.replaceState({}, document.title);
    }
  }, [location.state]);

  // --- Handlers ---
  const handleCategoryChange = (e) => {
    setFilters(prev => ({ ...prev, categoryId: e.target.value }));
    setPageNumber(0);
  };

  const handleSearchChange = (e) => {
    setFilters(prev => ({ ...prev, searchKeyword: e.target.value }));
    setPageNumber(0);
  };

  const handleClearFilters = () => {
    setFilters({ categoryId: "", searchKeyword: "" });
    setPageNumber(0);
  };

  const getCurrentCategoryName = () => {
    const found = categories.find(c => (c.categoryId || c.id) == filters.categoryId);
    return found?.categoryName || found?.name || "Danh m·ª•c";
  };

  return (
    <section className="padding-bottom container mt-5">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase fw-bold text-primary">
          {filters.categoryId ? `üìÇ ${getCurrentCategoryName()}` : "üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m"}
        </h3>
        <p className="text-muted">
          {loading ? "ƒêang t√¨m ki·∫øm..." : `T√¨m th·∫•y ${products.length} s·∫£n ph·∫©m ph√π h·ª£p`}
        </p>
      </header>

      {/* B·ªò L·ªåC */}
      <div className="card shadow-sm border-0 mb-4 bg-light">
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-4">
              <select value={filters.categoryId} onChange={handleCategoryChange} className="form-select border-primary">
                <option value="">T·∫•t c·∫£ danh m·ª•c</option>
                {categories.map((cat) => (
                  <option key={cat.categoryId || cat.id} value={cat.categoryId || cat.id}>
                    {cat.categoryName || cat.name}
                  </option>
                ))}
              </select>
            </div>
            <div className="col-md-5">
              <div className="input-group">
                <span className="input-group-text bg-white border-primary"><i className="fa fa-search"></i></span>
                <input
                  type="text"
                  value={filters.searchKeyword}
                  onChange={handleSearchChange}
                  className="form-control border-primary shadow-none"
                  placeholder="T√¨m s·∫£n ph·∫©m m·ªπ ph·∫©m, skincare..."
                />
              </div>
            </div>
            <div className="col-md-3">
              <button onClick={handleClearFilters} className="btn btn-outline-danger w-100 shadow-none">
                <i className="fa fa-times"></i> X√≥a l·ªçc
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* DANH S√ÅCH S·∫¢N PH·∫®M */}
      <div className="row">
        {loading ? (
           <p className="text-center py-5">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        ) : products.length > 0 ? (
          products.map((p) => (
            <div key={p.productId || p.id} className="col-xl-3 col-lg-4 col-md-6 mb-4">
              <div className="card h-100 shadow-sm border-0 hover-up">
                <div className="position-relative">
                    {p.discount > 0 && <span className="badge bg-danger position-absolute m-2">-{p.discount}%</span>}
                    <Link to={`/product/${p.productId || p.id}`}>
                        <img 
                            src={`${IMAGE_BASE_URL}/${p.image}`} 
                            className="card-img-top p-3" 
                            style={{ height: '220px', objectFit: 'contain' }}
                            onError={(e) => e.target.src = "https://via.placeholder.com/250"}
                            alt={p.productName}
                        />
                    </Link>
                </div>
                <div className="card-body d-flex flex-column">
                  <h6 className="card-title fw-bold text-truncate">{p.productName}</h6>
                  <div className="mb-2 mt-auto">
                    <span className="text-danger fw-bold h5">{(p.specialPrice || p.price).toLocaleString()} ‚Ç´</span>
                    {p.specialPrice && <del className="text-muted small ms-2">{p.price.toLocaleString()} ‚Ç´</del>}
                  </div>
                  <Link to={`/product/${p.productId || p.id}`} className="btn btn-primary btn-sm rounded-pill mt-2">
                    üõí Mua ngay
                  </Link>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center py-5">
             <h4>Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m n√†o!</h4>
             <button className="btn btn-link" onClick={handleClearFilters}>Quay l·∫°i t·∫•t c·∫£ s·∫£n ph·∫©m</button>
          </div>
        )}
      </div>

      {/* PH√ÇN TRANG */}
      {totalPages > 1 && (
        <nav className="mt-4">
          <ul className="pagination justify-content-center">
            <li className={`page-item ${pageNumber === 0 ? 'disabled' : ''}`}>
              <button className="page-link" onClick={() => setPageNumber(pageNumber - 1)}>‚Üê</button>
            </li>
            {[...Array(totalPages)].map((_, i) => (
              <li key={i} className={`page-item ${i === pageNumber ? 'active' : ''}`}>
                <button className="page-link" onClick={() => setPageNumber(i)}>{i + 1}</button>
              </li>
            ))}
            <li className={`page-item ${pageNumber === totalPages - 1 ? 'disabled' : ''}`}>
              <button className="page-link" onClick={() => setPageNumber(pageNumber + 1)}>‚Üí</button>
            </li>
          </ul>
        </nav>
      )}
    </section>
  );
};

