import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [pagination, setPagination] = useState({
    currentPage: 0,
    totalPages: 0,
    totalElements: 0,
    pageSize: 5 // C√≥ th·ªÉ backend ƒëang set m·∫∑c ƒë·ªãnh 5
  });

  useEffect(() => {
    loadProducts(0); // Load trang ƒë·∫ßu ti√™n
  }, []);

  const loadProducts = async (page = 0) => {
    try {
      setLoading(true);
      setError(null);

      const data = await productService.getAllProducts(page);
      console.log("üì¶ Full API response:", data);

      // X·ª≠ l√Ω ph√¢n trang
      let productsArray = [];
      let paginationInfo = {
        currentPage: page,
        totalPages: 1,
        totalElements: 0,
        pageSize: 5
      };

      if (data && data.content && Array.isArray(data.content)) {
        // Spring Pageable format
        productsArray = data.content;
        paginationInfo = {
          currentPage: data.number || page,
          totalPages: data.totalPages || 1,
          totalElements: data.totalElements || data.content.length,
          pageSize: data.size || 5
        };
      } else if (Array.isArray(data)) {
        // M·∫£ng tr·ª±c ti·∫øp
        productsArray = data;
        paginationInfo.totalElements = data.length;
      }

      console.log("üõçÔ∏è Processed products:", productsArray);
      console.log("üìä Pagination info:", paginationInfo);

      setProducts(productsArray);
      setPagination(paginationInfo);

    } catch (err) {
      console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
      setError("Kh√¥ng th·ªÉ t·∫£i danh s√°ch s·∫£n ph·∫©m");
    } finally {
      setLoading(false);
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < pagination.totalPages) {
      loadProducts(newPage);
    }
  };

  // Hi·ªÉn th·ªã loading
  if (loading) {
    return (
      <section className="padding-bottom">
        <header className="section-heading mb-4 text-center">
          <h3 className="title-section text-uppercase font-bold text-primary">
            üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
          </h3>
        </header>
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      </section>
    );
  }

  // Hi·ªÉn th·ªã l·ªói
  if (error) {
    return (
      <section className="padding-bottom">
        <header className="section-heading mb-4 text-center">
          <h3 className="title-section text-uppercase font-bold text-primary">
            üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
          </h3>
        </header>
        <div className="alert alert-danger text-center">
          {error}
        </div>
      </section>
    );
  }

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase font-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m 
        </h3>
        <p className="text-muted">
          Hi·ªÉn th·ªã {products.length} s·∫£n ph·∫©m (T·ªïng c·ªông: {pagination.totalElements} s·∫£n ph·∫©m)
        </p>
      </header>

      <div className="row">
        {products.map((p, index) => {
          const productId = p.productId || p.product_id || p.id;

          return (
            <div key={productId || index} className="col-xl-3 col-lg-3 col-md-4 col-6 mb-4">
              <div className="card card-product-grid shadow-sm h-100">
                <div className="img-wrap">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <img
                      src={`${IMAGE_BASE_URL}/${p.image}`}
                      alt={p.productName || p.product_name || p.name}
                      onError={(e) => {
                        e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png";
                      }}
                      style={{
                        width: "100%",
                        height: "250px",
                        objectFit: "cover",
                        borderRadius: "10px",
                      }}
                    />
                  </Link>
                </div>

                <div className="info-wrap p-3 d-flex flex-column">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <h6 className="text-truncate text-dark font-weight-bold">
                      {p.productName || p.product_name || p.name || "T√™n s·∫£n ph·∫©m"}
                    </h6>
                  </Link>

                  <p className="text-muted small mb-2 flex-grow-1">
                    {(p.description || p.desc || "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£...").substring(0, 40)}...
                  </p>

                  <div className="price-wrapper">
                    {p.special_price || p.specialPrice ? (
                      <>
                        <span className="price h5 text-danger mb-0">
                          {(p.special_price || p.specialPrice)?.toLocaleString()} ‚Ç´
                        </span>
                        <span className="original-price text-muted text-decoration-line-through small ms-2">
                          {p.price?.toLocaleString()} ‚Ç´
                        </span>
                      </>
                    ) : (
                      <span className="price h5 text-danger mb-0">
                        {p.price?.toLocaleString()} ‚Ç´
                      </span>
                    )}
                  </div>

                  {(p.discount > 0 || p.discountPercent > 0) && (
                    <small className="text-success mt-1">
                      üî• Gi·∫£m {p.discount || p.discountPercent}%
                    </small>
                  )}

                  <small className={`mt-1 ${(p.quantity === 0 || p.stock === 0) ? 'text-danger' : 'text-success'}`}>
                    {p.quantity === 0 || p.stock === 0 ? 
                      '‚ùå H·∫øt h√†ng' : 
                      `üì¶ C√≤n ${p.quantity || p.stock} s·∫£n ph·∫©m`
                    }
                  </small>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Hi·ªÉn th·ªã ph√¢n trang */}
      {pagination.totalPages > 1 && (
        <nav aria-label="Page navigation" className="mt-4">
          <ul className="pagination justify-content-center">
            <li className={`page-item ${pagination.currentPage === 0 ? 'disabled' : ''}`}>
              <button 
                className="page-link" 
                onClick={() => handlePageChange(pagination.currentPage - 1)}
                disabled={pagination.currentPage === 0}
              >
                Tr∆∞·ªõc
              </button>
            </li>

            {[...Array(pagination.totalPages)].map((_, index) => (
              <li 
                key={index} 
                className={`page-item ${pagination.currentPage === index ? 'active' : ''}`}
              >
                <button 
                  className="page-link" 
                  onClick={() => handlePageChange(index)}
                >
                  {index + 1}
                </button>
              </li>
            ))}

            <li className={`page-item ${pagination.currentPage === pagination.totalPages - 1 ? 'disabled' : ''}`}>
              <button 
                className="page-link" 
                onClick={() => handlePageChange(pagination.currentPage + 1)}
                disabled={pagination.currentPage === pagination.totalPages - 1}
              >
                Sau
              </button>
            </li>
          </ul>
        </nav>
      )}

      {/* Hi·ªÉn th·ªã n·∫øu kh√¥ng c√≥ s·∫£n ph·∫©m */}
      {products.length === 0 && (
