import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const GeminiChat = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([{ role: 'ai', text: 'Ch√†o n√†ng! ‚ù§Ô∏è' }]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const scrollRef = useRef(null);

    useEffect(() => {
        scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const handleSend = async () => {
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setInput('');
        setLoading(true);

        console.log(">>> ƒêang g·ª≠i tin nh·∫Øn t·ªõi Backend: ", userMsg);

        try {
            const res = await axios.post('http://localhost:8080/api/ai/ask', {
                message: userMsg,
                cartId: localStorage.getItem('cartId')
            });

            console.log("<<< Backend ph·∫£n h·ªìi: ", res.data);

            setMessages(prev => [...prev, { 
                role: 'ai', 
                text: res.data.reply 
            }]);
        } catch (error) {
            console.error("L·ªói FE: ", error);
            setMessages(prev => [...prev, { role: 'ai', text: 'Hic, em b·∫≠n x√≠u n√†ng ∆°i! üòÖ' }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1050 }}>
            <button onClick={() => setIsOpen(!isOpen)} className="btn btn-warning rounded-circle shadow-lg" style={{ width: '60px', height: '60px' }}>
                {isOpen ? 'X' : 'AI'}
            </button>

            {isOpen && (
                <div className="card shadow-lg mt-2" style={{ width: '350px', height: '500px', display: 'flex', flexDirection: 'column' }}>
                    <div className="card-header bg-warning fw-bold">Ng·ªçc Anh Beauty AI</div>
                    <div className="card-body overflow-auto" style={{ flex: 1 }}>
                        {messages.map((msg, i) => (
                            <div key={i} className={`mb-2 ${msg.role === 'user' ? 'text-end' : 'text-start'}`}>
                                <div className={`p-2 rounded d-inline-block ${msg.role === 'user' ? 'bg-warning' : 'bg-light border'}`} style={{ whiteSpace: 'pre-line' }}>
                                    {msg.text}
                                </div>
                            </div>
                        ))}
                        {loading && <div className="small text-muted">ƒêang tr·∫£ l·ªùi...</div>}
                        <div ref={scrollRef} />
                    </div>
                    <div className="card-footer">
                        <div className="input-group">
                            <input type="text" className="form-control" value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && handleSend()} />
                            <button onClick={handleSend} className="btn btn-warning">G·ª≠i</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default GeminiChat;
