import React, { useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import Swal from 'sweetalert2';
import axios from 'axios';

const PaymentSuccess = () => {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();

    useEffect(() => {
    const finalize = async () => {
        const vnpCode = searchParams.get('vnp_ResponseCode');
        const pending = JSON.parse(localStorage.getItem('pendingOrder'));
        const currentUser = JSON.parse(localStorage.getItem('user')); // Lấy user để lấy token

        if (vnpCode === "00" && pending) {
            try {
                // Lấy Token từ user đã đăng nhập
                const token = currentUser?.token; 

                // Gọi API lưu đơn hàng vào SQL (Java Backend)
                // Đảm bảo cartService.createOrder đã hỗ trợ nhận header hoặc dùng axios trực tiếp
                await axios.post(`http://localhost:8080/api/cart/order`, {
                    email: pending.email,
                    cartId: pending.cartId,
                    paymentMethod: 'BANKING',
                    address: pending.address,
                    phone: pending.phone
                }, {
                    headers: { 'Authorization': `Bearer ${token}` } // QUAN TRỌNG: Phải có dòng này
                });

                await cartService.clearCart(pending.email, pending.cartId);
                
                localStorage.removeItem('pendingOrder');
                Swal.fire('Thành công', 'Đã lưu đơn hàng vào hệ thống!', 'success');
                navigate('/orders');
            } catch (e) {
                console.error("Lỗi lưu SQL:", e.response?.data);
                Swal.fire('Lỗi lưu đơn', 'Thanh toán OK nhưng không lưu được vào SQL. Kiểm tra Token/API!', 'error');
            }
        }
    };
    finalize();
}, []);

    return (
        <div className="container text-center py-5">
            <div className="spinner-border text-warning" role="status"></div>
            <h4 className="mt-3">Đang xác thực giao dịch với VNPay...</h4>
        </div>
    );
};

export default PaymentSuccess;
