import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cartService from '../../services/cartService';
import orderService from '../../services/orderService';
import Swal from 'sweetalert2'; // N√™n d√πng SweetAlert2 ƒë·ªÉ th√¥ng b√°o chuy√™n nghi·ªáp h∆°n

const Checkout = () => {
    const [cart, setCart] = useState(null);
    const [loading, setLoading] = useState(true);
    const [processing, setProcessing] = useState(false);
    const [user, setUser] = useState(null);
    const [paymentMethod, setPaymentMethod] = useState('COD');
    const [shippingAddress, setShippingAddress] = useState('');
    const navigate = useNavigate();

    // L·∫•y th√¥ng tin user hi·ªán t·∫°i t·ª´ localStorage
    const currentUser = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        if (!currentUser || !currentUser.email) {
            alert('Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th·ª±c hi·ªán thanh to√°n!');
            navigate('/login');
            return;
        }
        loadData();
    }, []);

    const loadData = async () => {
        try {
            setLoading(true);
            // 1. T·∫£i gi·ªè h√†ng
            const cartData = await cartService.getActiveCart(currentUser.email);

            if (!cartData || !cartData.products || cartData.products.length === 0) {
                alert('Gi·ªè h√†ng c·ªßa b·∫°n ƒëang tr·ªëng!');
                navigate('/cart');
                return;
            }
            setCart(cartData);

            // 2. T·∫£i th√¥ng tin User
            setUser(currentUser);

            // 3. L·∫•y ƒë·ªãa ch·ªâ c≈© (n·∫øu c√≥)
            const savedAddress = localStorage.getItem('shippingAddress') || '';
            setShippingAddress(savedAddress);

        } catch (error) {
            console.error('‚ùå L·ªói t·∫£i th√¥ng tin:', error);
            alert('Kh√¥ng th·ªÉ t·∫£i th√¥ng tin thanh to√°n.');
        } finally {
            setLoading(false);
        }
    };

    const handlePlaceOrder = async () => {
        // Validation
        if (!shippingAddress.trim()) {
            alert('Vui l√≤ng nh·∫≠p ƒë·ªãa ch·ªâ giao h√†ng!');
            return;
        }

        if (!window.confirm('X√°c nh·∫≠n ƒë·∫∑t h√†ng?')) return;

        try {
            setProcessing(true);

            // ‚úÖ G·ªåI ƒê√öNG H√ÄM T·ª™ orderService.js
            // API: /public/users/{email}/carts/{cartId}/place-order?paymentMethod=...&shippingAddress=...
            const orderResponse = await orderService.createOrderFromCart(
                user.email,
                cart.cartId,
                paymentMethod,
                shippingAddress
            );

            console.log('‚úÖ ƒê∆°n h√†ng th√†nh c√¥ng:', orderResponse);

            // L∆∞u ƒë·ªãa ch·ªâ cho l·∫ßn sau
            localStorage.setItem('shippingAddress', shippingAddress);

