// src/pages/order/Checkout.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import cartService from '../../services/cartService';
import orderService from '../../services/orderService';

const Checkout = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(false);
  const [user, setUser] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState('CASH'); // Äá»•i máº·c Ä‘á»‹nh thÃ nh CASH
  const [shippingAddress, setShippingAddress] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: 'guest@example.com' };

  useEffect(() => {
    loadCartAndUser();
  }, []);

  const loadCartAndUser = async () => {
    try {
      setLoading(true);

      // Láº¥y giá» hÃ ng
      const cartData = await cartService.getActiveCart(currentUser.email);
      console.log("ğŸ“¦ Giá» hÃ ng thanh toÃ¡n:", cartData);

      if (!cartData || !cartData.products || cartData.products.length === 0) {
        alert('âŒ Giá» hÃ ng trá»‘ng!');
        navigate('/cart');
        return;
      }

      setCart(cartData);

      // Láº¥y thÃ´ng tin user tá»« localStorage hoáº·c API
      const userData = JSON.parse(localStorage.getItem('user')) || {};
      setUser(userData);

      // Láº¥y Ä‘á»‹a chá»‰ tá»« localStorage náº¿u cÃ³
      const savedAddress = localStorage.getItem('shippingAddress') || '';
      setShippingAddress(savedAddress);

    } catch (error) {
      console.error('âŒ Lá»—i khi táº£i thÃ´ng tin:', error);
      alert('âŒ Lá»—i khi táº£i thÃ´ng tin: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  // âœ… THÃŠM: HÃ m xá»­ lÃ½ chuyá»ƒn Ä‘á»•i payment method
  const handlePaymentMethodChange = (method) => {
    // Map cÃ¡c giÃ¡ trá»‹ ngáº¯n thÃ nh giÃ¡ trá»‹ dÃ i (â‰¥ 4 kÃ½ tá»±)
    const paymentMethodMap = {
      'COD': 'CASH',           // COD â†’ CASH (4 kÃ½ tá»±)
      'BANKING': 'BANK_TRANSFER', // BANKING â†’ BANK_TRANSFER (13 kÃ½ tá»±)
      'MOMO': 'MOMO_WALLET',   // MOMO â†’ MOMO_WALLET (10 kÃ½ tá»±)
      'CASH': 'CASH',
      'CREDIT_CARD': 'CREDIT_CARD',
      'BANK_TRANSFER': 'BANK_TRANSFER',
      'MOMO_WALLET': 'MOMO_WALLET'
    };

    const finalMethod = paymentMethodMap[method] || method;
    console.log(`ğŸ’° Chuyá»ƒn Ä‘á»•i payment method: ${method} â†’ ${finalMethod}`);
    setPaymentMethod(finalMethod);
  };

  const handlePlaceOrder = async () => {
    if (!cart || !cart.cartId) {
      alert('âŒ KhÃ´ng tÃ¬m tháº¥y giá» hÃ ng');
      return;
    }

    if (!user || !user.email) {
      alert('âŒ Vui lÃ²ng Ä‘Äƒng nháº­p Ä‘á»ƒ Ä‘áº·t hÃ ng');
      navigate('/login');
      return;
    }

    if (!shippingAddress.trim()) {
      alert('âŒ Vui lÃ²ng nháº­p Ä‘á»‹a chá»‰ giao hÃ ng');
      return;
    }

    // âœ… THÃŠM: Validate payment method cÃ³ Ã­t nháº¥t 4 kÃ½ tá»±
    if (paymentMethod.length < 4) {
      alert('âŒ Lá»—i: PhÆ°Æ¡ng thá»©c thanh toÃ¡n khÃ´ng há»£p lá»‡. Vui lÃ²ng chá»n láº¡i.');
      return;
    }

    if (!window.confirm('Báº¡n cÃ³ cháº¯c muá»‘n Ä‘áº·t hÃ ng?')) {
      return;
    }

    try {
      setProcessing(true);
      console.log(`ğŸ’° Äáº·t hÃ ng vá»›i phÆ°Æ¡ng thá»©c: ${paymentMethod} (${paymentMethod.length} kÃ½ tá»±)`);
      console.log(`ğŸ“¦ Äá»‹a chá»‰ giao hÃ ng: ${shippingAddress}`);

      // LÆ°u Ä‘á»‹a chá»‰ vÃ o localStorage
      localStorage.setItem('shippingAddress', shippingAddress);

      // Gá»i API Ä‘áº·t hÃ ng - Sá»¬A: chá»‰ truyá»n 3 tham sá»‘ theo API
      const order = await cartService.createOrder(user.email, cart.cartId, paymentMethod);
      console.log('âœ… ÄÆ¡n hÃ ng Ä‘Ã£ táº¡o:', order);

      // âœ… THÃŠM: LÃ m rá»—ng giá» hÃ ng sau khi Ä‘áº·t hÃ ng thÃ nh cÃ´ng
      await clearCartAfterOrder(user.email, cart.cartId);

      alert('âœ… Äáº·t hÃ ng thÃ nh cÃ´ng! Cáº£m Æ¡n báº¡n Ä‘Ã£ mua sáº¯m.');

      // Chuyá»ƒn hÆ°á»›ng Ä‘áº¿n trang chi tiáº¿t Ä‘Æ¡n hÃ ng hoáº·c trang chá»§
      navigate('/order-success', { 
        state: { 
          order: order,
          message: 'ÄÆ¡n hÃ ng cá»§a báº¡n Ä‘Ã£ Ä‘Æ°á»£c xÃ¡c nháº­n!' 
        } 
      });

    } catch (error) {
      console.error('âŒ Lá»—i khi Ä‘áº·t hÃ ng:', error);

      // âœ… THÃŠM: Xá»­ lÃ½ lá»—i cá»¥ thá»ƒ cho payment method
      if (error.response?.status === 400) {
        const errorMessage = error.response.data?.message || '';
        if (errorMessage.includes('payment method') || errorMessage.includes('Payment method')) {
          alert('âŒ Lá»—i phÆ°Æ¡ng thá»©c thanh toÃ¡n: PhÆ°Æ¡ng thá»©c thanh toÃ¡n pháº£i cÃ³ Ã­t nháº¥t 4 kÃ½ tá»±. Vui lÃ²ng chá»n láº¡i.');
        } else {
          alert('âŒ Lá»—i dá»¯ liá»‡u: ' + errorMessage);
        }
      } else {
        alert('âŒ Lá»—i khi Ä‘áº·t hÃ ng: ' + (error.response?.data?.message || error.message));
      }
    } finally {
      setProcessing(false);
    }
  };

  // âœ… THÃŠM: HÃ m lÃ m rá»—ng giá» hÃ ng sau khi Ä‘áº·t hÃ ng
  const clearCartAfterOrder = async (email, cartId) => {
    try {
      console.log(`ğŸ§¹ Äang lÃ m rá»—ng giá» hÃ ng: ${cartId}`);

      // CÃ¡ch 1: Gá»i API xÃ³a toÃ n bá»™ giá» hÃ ng
      await cartService.clearCart(email, cartId);

      console.log('âœ… ÄÃ£ lÃ m rá»—ng giá» hÃ ng thÃ nh cÃ´ng');

      // Cáº­p nháº­t state local
      setCart(prevCart => ({
        ...prevCart,
        products: [],
        totalPrice: 0
      }));

    } catch (error) {
      console.error('âŒ Lá»—i khi lÃ m rá»—ng giá» hÃ ng:', error);
      // KhÃ´ng throw error á»Ÿ Ä‘Ã¢y vÃ¬ Ä‘Æ¡n hÃ ng Ä‘Ã£ Ä‘Æ°á»£c táº¡o thÃ nh cÃ´ng
    }
  };

  // TÃ­nh tá»•ng tiá»n
