import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const Register = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [backendErrors, setBackendErrors] = useState({});

  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobileNumber: "",
    password: "",
    confirmPassword: "",
    street: "",
    buildingName: "",
    city: "",
    state: "",
    country: "Việt Nam",
    pincode: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    // Xóa lỗi ngay dưới ô input khi người dùng bắt đầu sửa
    if (backendErrors[name]) {
      setBackendErrors((prev) => ({ ...prev, [name]: "" }));
    }
  };

  const validateForm = () => {
    let errors = {};
    const nameRegex = /^[a-zA-ZÀ-ỹ\s]*$/;
    const phoneRegex = /^\d{10}$/;

    if (form.firstName.length < 5 || form.firstName.length > 20) {
      errors.firstName = "Họ phải từ 5 đến 20 ký tự (theo yêu cầu hệ thống).";
    } else if (!nameRegex.test(form.firstName)) {
      errors.firstName = "Họ không được chứa số hoặc ký tự đặc biệt.";
    }

    if (form.lastName.length < 5 || form.lastName.length > 20) {
      errors.lastName = "Tên phải từ 5 đến 20 ký tự.";
    } else if (!nameRegex.test(form.lastName)) {
      errors.lastName = "Tên không được chứa số hoặc ký tự đặc biệt.";
    }

    if (!phoneRegex.test(form.mobileNumber)) {
      errors.mobileNumber = "Số điện thoại phải chính xác 10 chữ số.";
    }

    if (form.password.length < 6) {
      errors.password = "Mật khẩu phải từ 6 ký tự trở lên.";
    }

    if (form.password !== form.confirmPassword) {
      errors.confirmPassword = "Mật khẩu xác nhận không khớp!";
    }

    setBackendErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 1. Kiểm tra lỗi phía Client trước
    if (!validateForm()) {
      Swal.fire({
        icon: "warning",
        title: "Thông tin chưa đúng",
        text: "Vui lòng kiểm tra các ô báo đỏ bên dưới.",
        confirmButtonColor: "#ff6a00"
      });
      return;
    }

    setLoading(true);
    try {
      const payload = {
        firstName: form.firstName,
        lastName: form.lastName,
        mobileNumber: form.mobileNumber,
        email: form.email,
        password: form.password,
        roles: [{ roleId: 102, roleName: "USER" }],
        addresses: [
          {
            street: form.street,
            buildingName: form.buildingName,
            city: form.city,
            state: form.state,
            country: form.country,
            pincode: form.pincode,
          }
        ]
      };

      await authService.register(payload);

      // Thông báo Popup thành công
      Swal.fire({
        icon: "success",
        title: "Đăng ký thành công!",
        text: "Chào mừng bạn gia nhập Guardian Store.",
        confirmButtonColor: "#ff6a00",
      }).then(() => navigate("/login"));

    } catch (error) {
      const errorData = error.response?.data;

      if (typeof errorData === "object") {
        // Gán lỗi chi tiết từ Backend vào từng ô
        setBackendErrors(errorData);
        Swal.fire({
          icon: "error",
          title: "Dữ liệu không hợp lệ",
          text: "Backend từ chối dữ liệu, vui lòng sửa các ô báo đỏ.",
          confirmButtonColor: "#d33"
        });
      } else {
        Swal.fire({
          icon: "error",
          title: "Lỗi đăng ký",
          text: errorData || "Email/SĐT đã tồn tại hoặc Server gặp sự cố.",
          confirmButtonColor: "#d33"
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="bg-light py-4" style={{ minHeight: "100vh" }}>
      <div className="container">
        <div className="card mx-auto shadow-sm border-0" style={{ maxWidth: "700px", borderRadius: "12px" }}>
          <div className="card-body p-4 p-md-5">
            <h3 className="text-center fw-bold mb-4" style={{ color: "#ff6a00" }}>ĐĂNG KÝ TÀI KHOẢN</h3>

            <form onSubmit={handleSubmit} noValidate>
              {/* PHẦN 1: CÁ NHÂN */}
              <div className="mb-4">
                <p className="fw-bold text-uppercase small text-muted border-bottom pb-2">1. Thông tin cá nhân</p>
                <div className="row g-3">
                  <div className="col-md-6">
                    <label className="form-label small fw-bold">Họ & Tên đệm</label>
                    <input name="firstName" type="text" className={`form-control ${backendErrors.firstName ? 'is-invalid' : ''}`} value={form.firstName} onChange={handleChange} placeholder="Nguyễn Văn" required />
                    <div className="invalid-feedback">{backendErrors.firstName}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small fw-bold">Tên</label>
                    <input name="lastName" type="text" className={`form-control ${backendErrors.lastName ? 'is-invalid' : ''}`} value={form.lastName} onChange={handleChange} placeholder="A" required />
                    <div className="invalid-feedback">{backendErrors.lastName}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small fw-bold">Email</label>
                    <input name="email" type="email" className={`form-control ${backendErrors.email ? 'is-invalid' : ''}`} value={form.email} onChange={handleChange} placeholder="example@gmail.com" required />
                    <div className="invalid-feedback">{backendErrors.email}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small fw-bold">Số điện thoại</label>
                    <input name="mobileNumber" type="text" className={`form-control ${backendErrors.mobileNumber ? 'is-invalid' : ''}`} value={form.mobileNumber} onChange={handleChange} placeholder="10 chữ số" required />
                    <div className="invalid-feedback">{backendErrors.mobileNumber}</div>
                  </div>
                </div>
              </div>

              {/* PHẦN 2: ĐỊA CHỈ */}
              <div className="mb-4">
                <p className="fw-bold text-uppercase small text-muted border-bottom pb-2">2. Địa chỉ giao hàng</p>
                <div className="row g-3">
                  <div className="col-md-12">
                    <label className="form-label small fw-bold">Số nhà, tên đường</label>
                    <input name="street" type="text" className={`form-control ${backendErrors.street ? 'is-invalid' : ''}`} value={form.street} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.street}</div>
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small fw-bold">Thành phố</label>
                    <input name="city" type="text" className="form-control" value={form.city} onChange={handleChange} required />
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small fw-bold">Tỉnh/Thành</label>
                    <input name="state" type="text" className="form-control" value={form.state} onChange={handleChange} required />
                  </div>
                  <div className="col-md-4">
                    <label className="form-label small fw-bold">Mã Pincode</label>
                    <input name="pincode" type="text" className={`form-control ${backendErrors.pincode ? 'is-invalid' : ''}`} value={form.pincode} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.pincode}</div>
                  </div>
                </div>
              </div>

              {/* PHẦN 3: BẢO MẬT */}
              <div className="mb-4">
                <p className="fw-bold text-uppercase small text-muted border-bottom pb-2">3. Mật khẩu</p>
                <div className="row g-3">
                  <div className="col-md-6">
                    <label className="form-label small fw-bold">Mật khẩu mới</label>
                    <input name="password" type="password" className={`form-control ${backendErrors.password ? 'is-invalid' : ''}`} value={form.password} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.password}</div>
                  </div>
                  <div className="col-md-6">
                    <label className="form-label small fw-bold">Nhập lại mật khẩu</label>
                    <input name="confirmPassword" type="password" className={`form-control ${backendErrors.confirmPassword ? 'is-invalid' : ''}`} value={form.confirmPassword} onChange={handleChange} required />
                    <div className="invalid-feedback">{backendErrors.confirmPassword}</div>
                  </div>
                </div>
              </div>

              <button type="submit" className="btn btn-lg w-100 fw-bold text-white shadow-sm mt-3" style={{ backgroundColor: "#ff6a00", border: "none" }} disabled={loading}>
                {loading ? (
                  <><span className="spinner-border spinner-border-sm me-2"></span> Đang tạo tài khoản...</>
                ) : "ĐĂNG KÝ NGAY"}
              </button>
            </form>

            <div className="text-center mt-4">
              <span className="text-muted">Bạn đã thành viên? </span>
              <Link to="/login" className="fw-bold text-decoration-none" style={{ color: "#ff6a00" }}>Đăng nhập tại đây</Link>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Register;
