import { useEffect, useState, useCallback } from "react";
import { useLocation } from "react-router-dom";
import productService from "../../services/productService";
import categoryService from "../../services/categoryService";
import ProductCard from "../product/ProductCard";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    categoryId: "",
    searchKeyword: "",
    sortBy: "price",
    sortOrder: "asc"
  });

  // 1. LẮNG NGHE DỮ LIỆU TỪ HEADER TRUYỀN SANG
  useEffect(() => {
    if (location.state) {
      const { searchKeyword, selectedCategory } = location.state;
      setFilters(prev => ({
        ...prev,
        searchKeyword: searchKeyword || "",
        categoryId: selectedCategory || ""
      }));
      if (searchKeyword) setSearchTerm(searchKeyword);

      // Xóa state để tránh việc load lại khi F5 trang
      window.history.replaceState({}, document.title);
    }
  }, [location.state]);

  // 2. Debounce tìm kiếm tại chỗ (trong trang ProductList)
  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      setFilters(prev => ({ ...prev, searchKeyword: searchTerm }));
    }, 500);
    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  // 3. Tải danh mục ban đầu
  useEffect(() => {
    categoryService.getAllCategories().then(data => setCategories(data || []));
  }, []);

  // 4. HÀM TẢI SẢN PHẨM CHÍNH
  const fetchProducts = useCallback(async () => {
    setLoading(true);
    const PAGE_SIZE = 1000;
    try {
      let response;
      const { categoryId, searchKeyword, sortBy, sortOrder } = filters;

      if (searchKeyword) {
        // Gọi hàm searchProducts trong service
        response = await productService.searchProducts(searchKeyword, 0, PAGE_SIZE, sortBy, sortOrder);
      } else if (categoryId) {
        response = await productService.getProductsByCategory(categoryId, 0, PAGE_SIZE, sortBy, sortOrder);
      } else {
        response = await productService.getAllProducts(0, PAGE_SIZE, sortBy, sortOrder);
      }
      setProducts(response?.content || []);
    } catch (err) {
      setProducts([]);
    } finally {
      setTimeout(() => setLoading(false), 300);
    }
  }, [filters]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  return (
    <section className="container mt-5 pb-5">
      <div className="row g-3 mb-4 bg-light p-3 rounded-4 align-items-center">
        <div className="col-md-3">
          <select 
            value={filters.categoryId} 
            onChange={(e) => setFilters(p => ({ ...p, categoryId: e.target.value }))}
            className="form-select rounded-pill border-0 shadow-sm"
          >
            <option value="">Tất cả danh mục</option>
            {categories.map(c => <option key={c.id || c.categoryId} value={c.id || c.categoryId}>{c.categoryName || c.name}</option>)}
          </select>
        </div>
        <div className="col-md-6">
          <input 
            type="text" 
            className="form-control rounded-pill border-0 shadow-sm px-4"
            placeholder="Tìm kiếm nhanh sản phẩm..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="col-md-3">
          <select 
            onChange={(e) => setFilters(p => ({ ...p, sortOrder: e.target.value }))}
            className="form-select rounded-pill border-0 shadow-sm"
          >
            <option value="asc">Giá: Thấp đến Cao</option>
            <option value="desc">Giá: Cao đến Thấp</option>
          </select>
        </div>
      </div>

      <div className={`row transition-all ${loading ? 'opacity-50' : ''}`}>
        {products.length > 0 ? (
          products.map(p => <ProductCard key={p.id || p.productId} product={p} />)
        ) : !loading && (
          <div className="text-center py-5">
            <i className="fa fa-search-minus fa-4x text-light mb-3"></i>
            <h4 className="text-muted">Không tìm thấy sản phẩm nào phù hợp</h4>
          </div>
        )}
      </div>
    </section>
  );
};

export default ProductList;
