import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import bannerService from "../../services/bannerService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/banners/image";

const Banner = () => {
  const [banner, setBanner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    loadBanner();
  }, []);

  const loadBanner = async () => {
    try {
      setLoading(true);
      setError("");

      console.log("üîÑ Fetching banner...");
      const data = await bannerService.getActiveBanners();
      console.log("‚úÖ API Response:", data);

      const bannersArray = Array.isArray(data?.content) ? data.content : [];

      if (bannersArray.length > 0) {
        setBanner(bannersArray[0]);  // ‚úÖ Only first banner
      } else {
        setBanner(null);
      }
    } catch (err) {
      console.error("‚ùå L·ªói load banner:", err);
      setError("Kh√¥ng th·ªÉ t·∫£i banner");
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (banner) => {
    if (!banner?.imageUrl) {
      return "https://via.placeholder.com/800x400/667eea/ffffff?text=No+Image";
    }
    if (banner.imageUrl.startsWith("http")) return banner.imageUrl;
    return `${IMAGE_BASE_URL}/${banner.imageUrl}`;
  };

  if (loading) {
    return (
      <section className="banner-section mb-5">
        <div className="text-center py-5">
          <div className="spinner-border text-primary"></div>
          <p>ƒêang t·∫£i banner...</p>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="banner-section mb-5">
        <div className="text-center text-danger py-5">
          {error}
        </div>
      </section>
    );
  }

  if (!banner) {
    return (
      <section className="banner-section mb-5">
        <div className="text-center text-muted py-5">
          Ch∆∞a c√≥ banner ƒë·ªÉ hi·ªÉn th·ªã
        </div>
      </section>
    );
  }

  return (
    <section className="banner-section mb-5">
      <div className="container-fluid px-0">
        <div
          className="w-100 d-flex align-items-center justify-content-center text-dark"
          style={{
            background: banner.backgroundColor || "#FFEBCC",
            minHeight: "400px",
          }}
        >
          <div className="row w-100 m-0 p-4 align-items-center">

            {/* LEFT: Banner Content */}
            <div className="col-lg-6">
              <h1 className="fw-bold mb-3">
                {banner.title || "Banner"}
              </h1>

              {banner.description && (
                <p className="mb-4">{banner.description}</p>
              )}

              {banner.buttonText && (
                <Link
                  to={banner.targetUrl || "/products"}
                  className="btn btn-primary btn-lg"
                >
                  {banner.buttonText}
                </Link>
              )}
            </div>

            {/* RIGHT: Banner Image */}
            <div className="col-lg-6 text-center">
              <img
                src={getImageUrl(banner)}
                alt={banner.title || "banner"}
                className="img-fluid rounded shadow"
                style={{
                  maxHeight: "350px",
                  objectFit: "cover",
                }}
                onError={(e) => {
                  e.target.src =
                    "https://via.placeholder.com/800x400/667eea/ffffff?text=Banner+Not+Found";
                }}
              />
            </div>

          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
