import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import productService from "../../services/productService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadProducts = async () => {
      try {
        setLoading(true);
        setError(null);

        const data = await productService.getAllProducts();
        console.log("üì¶ Full API response:", data);

        // X·ª≠ l√Ω nhi·ªÅu ƒë·ªãnh d·∫°ng response kh√°c nhau
        let productsArray = [];
        let totalProducts = 0;

        if (Array.isArray(data)) {
          // N·∫øu data l√† m·∫£ng tr·ª±c ti·∫øp
          productsArray = data;
          totalProducts = data.length;
        } else if (data && data.content && Array.isArray(data.content)) {
          // N·∫øu c√≥ structure Spring Pageable
          productsArray = data.content;
          totalProducts = data.totalElements || data.content.length;
          console.log("üìä Pagination info:", {
            totalElements: data.totalElements,
            totalPages: data.totalPages,
            pageSize: data.size,
            currentPage: data.number,
            contentLength: data.content.length
          });
        } else if (data && data.products && Array.isArray(data.products)) {
          // N·∫øu c√≥ field products
          productsArray = data.products;
          totalProducts = data.total || data.products.length;
        } else if (data && data.data && Array.isArray(data.data)) {
          // N·∫øu c√≥ field data
          productsArray = data.data;
          totalProducts = data.total || data.data.length;
        } else {
          // Fallback: th·ª≠ truy c·∫≠p tr·ª±c ti·∫øp
          productsArray = data || [];
          totalProducts = productsArray.length;
        }

        console.log("üõçÔ∏è Processed products:", productsArray);
        console.log("üìä Total products in DB:", totalProducts);
        console.log("üì¶ Products displayed:", productsArray.length);

        setProducts(productsArray);

        // C·∫£nh b√°o n·∫øu s·ªë l∆∞·ª£ng hi·ªÉn th·ªã √≠t h∆°n t·ªïng s·ªë
        if (productsArray.length < totalProducts) {
          console.warn(`‚ö†Ô∏è Ch·ªâ hi·ªÉn th·ªã ${productsArray.length}/${totalProducts} s·∫£n ph·∫©m (c√≥ th·ªÉ do ph√¢n trang)`);
        }

      } catch (err) {
        console.error("‚ùå L·ªói khi t·∫£i s·∫£n ph·∫©m:", err);
        setError("Kh√¥ng th·ªÉ t·∫£i danh s√°ch s·∫£n ph·∫©m");
      } finally {
        setLoading(false);
      }
    };

    loadProducts();
  }, []);

  // Hi·ªÉn th·ªã loading
  if (loading) {
    return (
      <section className="padding-bottom">
        <header className="section-heading mb-4 text-center">
          <h3 className="title-section text-uppercase font-bold text-primary">
            üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
          </h3>
        </header>
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i s·∫£n ph·∫©m...</p>
        </div>
      </section>
    );
  }

  // Hi·ªÉn th·ªã l·ªói
  if (error) {
    return (
      <section className="padding-bottom">
        <header className="section-heading mb-4 text-center">
          <h3 className="title-section text-uppercase font-bold text-primary">
            üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m
          </h3>
        </header>
        <div className="alert alert-danger text-center">
          {error}
        </div>
      </section>
    );
  }

  return (
    <section className="padding-bottom">
      <header className="section-heading mb-4 text-center">
        <h3 className="title-section text-uppercase font-bold text-primary">
          üõçÔ∏è T·∫•t c·∫£ s·∫£n ph·∫©m ({products.length} s·∫£n ph·∫©m)
        </h3>
        {products.length < 7 && (
          <p className="text-warning small">
            ‚ö†Ô∏è ƒêang hi·ªÉn th·ªã {products.length}/7 s·∫£n ph·∫©m (c√≥ th·ªÉ do ph√¢n trang)
          </p>
        )}
      </header>

      <div className="row">
        {products.map((p, index) => {
          const productId = p.productId || p.product_id || p.id;

          return (
            <div key={productId || index} className="col-xl-3 col-lg-3 col-md-4 col-6 mb-4">
              <div className="card card-product-grid shadow-sm h-100">
                <div className="img-wrap">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <img
                      src={`${IMAGE_BASE_URL}/${p.image}`}
                      alt={p.productName || p.product_name || p.name}
                      onError={(e) => {
                        e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png";
                      }}
                      style={{
                        width: "100%",
                        height: "250px",
                        objectFit: "cover",
                        borderRadius: "10px",
                      }}
                    />
                  </Link>
                </div>

                <div className="info-wrap p-3 d-flex flex-column">
                  <Link to={`/product/${productId}`} className="text-decoration-none">
                    <h6 className="text-truncate text-dark font-weight-bold">
                      {p.productName || p.product_name || p.name || "T√™n s·∫£n ph·∫©m"}
                    </h6>
                  </Link>

                  <p className="text-muted small mb-2 flex-grow-1">
                    {(p.description || p.desc || "ƒêang c·∫≠p nh·∫≠t m√¥ t·∫£...").substring(0, 40)}...
                  </p>

                  <div className="price-wrapper">
                    {p.special_price || p.specialPrice ? (
                      <>
                        <span className="price h5 text-danger mb-0">
                          {(p.special_price || p.specialPrice)?.toLocaleString()} ‚Ç´
                        </span>
                        <span className="original-price text-muted text-decoration-line-through small ms-2">
                          {p.price?.toLocaleString()} ‚Ç´
                        </span>
                      </>
                    ) : (
                      <span className="price h5 text-danger mb-0">
                        {p.price?.toLocaleString()} ‚Ç´
                      </span>
                    )}
                  </div>

                  {(p.discount > 0 || p.discountPercent > 0) && (
                    <small className="text-success mt-1">
                      üî• Gi·∫£m {p.discount || p.discountPercent}%
                    </small>
                  )}

                  <small className={`mt-1 ${(p.quantity === 0 || p.stock === 0) ? 'text-danger' : 'text-success'}`}>
                    {p.quantity === 0 || p.stock === 0 ? 
                      '‚ùå H·∫øt h√†ng' : 
                      `üì¶ C√≤n ${p.quantity || p.stock} s·∫£n ph·∫©m`
                    }
                  </small>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Hi·ªÉn th·ªã n·∫øu kh√¥ng c√≥ s·∫£n ph·∫©m */}
      {products.length === 0 && (
        <div className="text-center py-5">
          <h4>üì≠ Kh√¥ng c√≥ s·∫£n ph·∫©m n√†o</h4>
          <p className="text-muted">Hi·ªán t·∫°i kh√¥ng c√≥ s·∫£n ph·∫©m n√†o ƒë·ªÉ hi·ªÉn th·ªã.</p>
        </div>
      )}
    </section>
  );
};

export default ProductList;
