// src/pages/order/OrderList.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import orderService from '../../services/orderService';

const OrderList = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cancelling, setCancelling] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showDetail, setShowDetail] = useState(false);
  const navigate = useNavigate();

  // Láº¥y thÃ´ng tin user tá»« localStorage
  const getCurrentUser = () => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      if (!user || !user.email) {
        throw new Error('User not found');
      }
      return user;
    } catch (error) {
      return null;
    }
  };

  const currentUser = getCurrentUser();

  useEffect(() => {
    if (!currentUser) {
      alert('âŒ Vui lÃ²ng Ä‘Äƒng nháº­p Ä‘á»ƒ xem Ä‘Æ¡n hÃ ng');
      navigate('/login');
      return;
    }
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      setLoading(true);
      console.log("ðŸ”„ Äang táº£i danh sÃ¡ch Ä‘Æ¡n hÃ ng cho user:", currentUser.email);

      const ordersData = await orderService.getUserOrders(currentUser.email);
      console.log("ðŸ“¦ Danh sÃ¡ch Ä‘Æ¡n hÃ ng sau xá»­ lÃ½:", ordersData);

      // Sáº¯p xáº¿p Ä‘Æ¡n hÃ ng má»›i nháº¥t lÃªn Ä‘áº§u
      const sortedOrders = Array.isArray(ordersData) 
        ? ordersData.sort((a, b) => new Date(b.createdAt || b.orderDate) - new Date(a.createdAt || a.orderDate))
        : [];

      setOrders(sortedOrders);

    } catch (error) {
      console.error('âŒ Lá»—i khi táº£i danh sÃ¡ch Ä‘Æ¡n hÃ ng:', error);
      alert('âŒ Lá»—i khi táº£i Ä‘Æ¡n hÃ ng: ' + (error.response?.data?.message || error.message));
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelOrder = async (orderId, reason = 'NgÆ°á»i dÃ¹ng yÃªu cáº§u há»§y') => {
    if (!window.confirm('Báº¡n cÃ³ cháº¯c muá»‘n há»§y Ä‘Æ¡n hÃ ng nÃ y?')) return;

    try {
      setCancelling(orderId);

      await orderService.cancelOrder(currentUser.email, orderId, reason);
      alert('âœ… ÄÃ£ há»§y Ä‘Æ¡n hÃ ng thÃ nh cÃ´ng');

      // Load láº¡i danh sÃ¡ch Ä‘Æ¡n hÃ ng
      await loadOrders();

    } catch (error) {
      console.error('âŒ Lá»—i khi há»§y Ä‘Æ¡n hÃ ng:', error);
      alert('âŒ Lá»—i khi há»§y Ä‘Æ¡n hÃ ng: ' + (error.response?.data?.message || error.message));
    } finally {
      setCancelling(null);
    }
  };

  const handleViewDetail = async (order) => {
    try {
      setSelectedOrder(null);
      setShowDetail(true);

      // Load chi tiáº¿t Ä‘Æ¡n hÃ ng tá»« API
      const orderDetail = await orderService.getOrderDetail(currentUser.email, order.orderId);
      console.log("ðŸ“¦ Chi tiáº¿t Ä‘Æ¡n hÃ ng:", orderDetail);
      setSelectedOrder(orderDetail);

    } catch (error) {
      console.error('âŒ Lá»—i khi láº¥y chi tiáº¿t Ä‘Æ¡n hÃ ng:', error);
      alert('âŒ Lá»—i khi láº¥y chi tiáº¿t Ä‘Æ¡n hÃ ng: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleCloseDetail = () => {
    setShowDetail(false);
    setSelectedOrder(null);
  };

  // Render sáº£n pháº©m trong order
  const renderOrderProducts = (order) => {
    const products = orderService.getOrderProducts(order);

    if (products.length === 0) {
      return (
        <div className="text-muted small">
          <i className="fas fa-exclamation-triangle me-1"></i>
          ChÆ°a cÃ³ thÃ´ng tin sáº£n pháº©m
        </div>
      );
    }

    return (
      <div className="order-products-preview">
        {products.slice(0, 2).map((product, index) => (
          <div key={index} className="d-flex align-items-center mb-1">
            <img
              src={product.image ? `http://localhost:8080/api/public/products/image/${product.image}` : 'https://cdn-icons-png.flaticon.com/512/679/679922.png'}
              alt={product.productName}
              className="rounded me-2"
              style={{ width: '30px', height: '30px', objectFit: 'cover' }}
              onError={(e) => {
                e.target.src = 'https://cdn-icons-png.flaticon.com/512/679/679922.png';
              }}
            />
            <div className="flex-grow-1">
              <div className="small text-truncate" style={{ maxWidth: '150px' }}>
                {product.productName}
              </div>
              <div className="text-muted extra-small">
                {product.quantity} Ã— {orderService.formatPrice(product.price)}
              </div>
            </div>
          </div>
        ))}
        {products.length > 2 && (
          <div className="text-primary small">
            +{products.length - 2} sáº£n pháº©m khÃ¡c...
          </div>
        )}
      </div>
    );
  };

  if (!currentUser) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <h3>Vui lÃ²ng Ä‘Äƒng nháº­p Ä‘á»ƒ xem Ä‘Æ¡n hÃ ng</h3>
          <Link to="/login" className="btn btn-primary mt-3">
            ÄÄƒng nháº­p ngay
          </Link>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Äang táº£i danh sÃ¡ch Ä‘Æ¡n hÃ ng...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">Trang chá»§</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            ÄÆ¡n hÃ ng cá»§a tÃ´i
          </li>
        </ol>
      </nav>

      <div className="row">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h1 className="h2 text-primary">
              <i className="fas fa-clipboard-list me-2"></i>
              ÄÆ¡n hÃ ng cá»§a tÃ´i
            </h1>
            <button 
              className="btn btn-outline-secondary"
              onClick={loadOrders}
              disabled={loading}
            >
              <i className="fas fa-sync-alt me-1"></i>
              {loading ? 'Äang táº£i...' : 'LÃ m má»›i'}
            </button>
          </div>

          {/* Debug Info */}
          <div className="alert alert-info">
            <small>
              <i className="fas fa-info-circle me-1"></i>
              Äang hiá»ƒn thá»‹ Ä‘Æ¡n hÃ ng cá»§a: <strong>{currentUser.email}</strong>
            </small>
          </div>

          {/* Thá»‘ng kÃª nhanh */}
          <div className="row mb-4">
            <div className="col-md-3">
              <div className="card bg-primary text-white">
                <div className="card-body text-center">
                  <h4 className="mb-0">{orders.length}</h4>
                  <small>Tá»•ng Ä‘Æ¡n hÃ ng</small>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card bg-success text-white">
                <div className="card-body text-center">
                  <h4 className="mb-0">
                    {orders.filter(order => order.status === 'DELIVERED').length}
                  </h4>
                  <small>ÄÃ£ giao hÃ ng</small>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card bg-warning text-white">
                <div className="card-body text-center">
                  <h4 className="mb-0">
                    {orders.filter(order => 
                      ['PENDING', 'CONFIRMED', 'PROCESSING', 'SHIPPED'].includes(order.status)
                    ).length}
                  </h4>
                  <small>Äang xá»­ lÃ½</small>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card bg-danger text-white">
                <div className="card-body text-center">
                  <h4 className="mb-0">
                    {orders.filter(order => order.status === 'CANCELLED').length}
                  </h4>
                  <small>ÄÃ£ há»§y</small>
                </div>
              </div>
            </div>
          </div>

          {/* Danh sÃ¡ch Ä‘Æ¡n hÃ ng */}
          {orders.length === 0 ? (
            <div className="text-center py-5">
              <div className="empty-order-icon mb-4">
                <i className="fas fa-box-open text-muted" style={{ fontSize: '5rem', opacity: 0.5 }}></i>
              </div>
              <h3 className="text-muted mb-3">ChÆ°a cÃ³ Ä‘Æ¡n hÃ ng nÃ o</h3>
              <p className="text-muted mb-4">HÃ£y mua sáº¯m vÃ  táº¡o Ä‘Æ¡n hÃ ng Ä‘áº§u tiÃªn cá»§a báº¡n</p>
              <Link to="/product" className="btn btn-warning btn-lg">
                <i className="fas fa-shopping-bag me-2"></i>
                Mua sáº¯m ngay
              </Link>
            </div>
          ) : (
            <div className="card shadow-sm">
              <div className="card-header bg-white">
                <h5 className="mb-0">
                  <i className="fas fa-list me-2"></i>
                  Danh sÃ¡ch Ä‘Æ¡n hÃ ng ({orders.length})
                </h5>
              </div>
              <div className="card-body p-0">
                {orders.map((order) => {
                  const statusInfo = orderService.formatOrderStatus(order.status);
                  const totalAmount = orderService.calculateOrderTotal(order);
                  const canCancel = orderService.canCancelOrder(order);
                  const products = orderService.getOrderProducts(order);

                  return (
                    <div key={order.orderId} className="border-bottom p-4">
                      <div className="row align-items-start">
                        {/* ThÃ´ng tin cÆ¡ báº£n */}
                        <div className="col-md-3">
                          <div className="d-flex align-items-center mb-2">
                            <div className="bg-light rounded p-2 me-3">
                              <i className="fas fa-receipt text-primary fs-4"></i>
                            </div>
                            <div>
                              <h6 className="mb-1">#{order.orderId}</h6>
                              <small className="text-muted">
                                {orderService.formatDate(order.createdAt || order.orderDate)}
                              </small>
                            </div>
                          </div>

                          {/* Sáº£n pháº©m */}
                          <div className="mt-2">
                            {renderOrderProducts(order)}
                          </div>
                        </div>

                        {/* Tráº¡ng thÃ¡i */}
                        <div className="col-md-2">
                          <span className={`badge bg-${statusInfo.class} fs-6 mb-2`}>
                            {statusInfo.text}
                          </span>
                          <div className="text-muted small">
                            {products.length} sáº£n pháº©m
                          </div>
                        </div>

                        {/* PhÆ°Æ¡ng thá»©c thanh toÃ¡n */}
                        <div className="col-md-2">
                          <small className="text-muted">Thanh toÃ¡n</small>
                          <div className="fw-bold">
                            {orderService.formatPaymentMethod(order.paymentMethod)}
                          </div>
                        </div>

                        {/* Tá»•ng tiá»n */}
                        <div className="col-md-2">
                          <small className="text-muted">Tá»•ng tiá»n</small>
                          <div className="text-danger fw-bold fs-5">
                            {totalAmount.toLocaleString()}â‚«
                          </div>
                        </div>

                        {/* HÃ nh Ä‘á»™ng */}
                        <div className="col-md-3">
                          <div className="d-flex gap-2 flex-wrap">
                            <button
                              className="btn btn-outline-primary btn-sm"
                              onClick={() => handleViewDetail(order)}
                            >
                              <i className="fas fa-eye me-1"></i>
                              Chi tiáº¿t
                            </button>

                            {canCancel && (
                              <button
                                className="btn btn-outline-danger btn-sm"
                                onClick={() => handleCancelOrder(order.orderId)}
                                disabled={cancelling === order.orderId}
                              >
                                {cancelling === order.orderId ? (
                                  <>
                                    <div className="spinner-border spinner-border-sm me-1" role="status">
                                      <span className="visually-hidden">Loading...</span>
                                    </div>
                                    Äang há»§y...
                                  </>
                                ) : (
                                  <>
                                    <i className="fas fa-times me-1"></i>
                                    Há»§y Ä‘Æ¡n
                                  </>
                                )}
                              </button>
                            )}

                            {order.status === 'DELIVERED' && (
                              <button className="btn btn-outline-success btn-sm">
                                <i className="fas fa-redo me-1"></i>
                                Mua láº¡i
                              </button>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Äá»‹a chá»‰ giao hÃ ng */}
                      <div className="row mt-3">
                        <div className="col-12">
                          <small className="text-muted">
                            <i className="fas fa-map-marker-alt me-1"></i>
                            Giao Ä‘áº¿n: {order.shippingAddress || 'ChÆ°a cÃ³ Ä‘á»‹a chá»‰'}
                          </small>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Modal chi tiáº¿t Ä‘Æ¡n hÃ ng */}
      {showDetail && (
        <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  <i className="fas fa-file-invoice me-2"></i>
                  Chi tiáº¿t Ä‘Æ¡n hÃ ng #{selectedOrder?.orderId}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={handleCloseDetail}
                ></button>
              </div>
              <div className="modal-body">
                {selectedOrder ? (
                  <OrderDetail order={selectedOrder} />
                ) : (
                  <div className="text-center">
                    <div className="spinner-border text-primary" role="status">
                      <span className="visually-hidden">Loading...</span>
                    </div>
                    <p className="mt-2">Äang táº£i chi tiáº¿t Ä‘Æ¡n hÃ ng...</p>
                  </div>
                )}
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={handleCloseDetail}
                >
                  ÄÃ³ng
                </button>
                {selectedOrder && orderService.canCancelOrder(selectedOrder) && (
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => handleCancelOrder(selectedOrder.orderId)}
                    disabled={cancelling === selectedOrder.orderId}
                  >
                    {cancelling === selectedOrder.orderId ? (
                      <>
                        <div className="spinner-border spinner-border-sm me-1" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </div>
                        Äang há»§y...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-times me-1"></i>
                        Há»§y Ä‘Æ¡n hÃ ng
                      </>
                    )}
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Component chi tiáº¿t Ä‘Æ¡n hÃ ng
const OrderDetail = ({ order }) => {
  const products = orderService.getOrderProducts(order);
  const totalAmount = orderService.calculateOrderTotal(order);

  return (
    <div className="row">
      {/* ThÃ´ng tin Ä‘Æ¡n hÃ ng */}
      <div className="col-md-6">
        <h6 className="text-primary mb-3">
          <i className="fas fa-info-circle me-2"></i>
          ThÃ´ng tin Ä‘Æ¡n hÃ ng
        </h6>
        <table className="table table-sm">
          <tbody>
            <tr>
              <td><strong>MÃ£ Ä‘Æ¡n hÃ ng:</strong></td>
              <td>#{order.orderId}</td>
            </tr>
            <tr>
              <td><strong>NgÃ y Ä‘áº·t:</strong></td>
              <td>{orderService.formatDate(order.createdAt || order.orderDate)}</td>
            </tr>
            <tr>
              <td><strong>Tráº¡ng thÃ¡i:</strong></td>
              <td>
                <span className={`badge bg-${orderService.formatOrderStatus(order.status).class}`}>
                  {orderService.formatOrderStatus(order.status).text}
                </span>
              </td>
            </tr>
            <tr>
              <td><strong>PhÆ°Æ¡ng thá»©c TT:</strong></td>
              <td>{orderService.formatPaymentMethod(order.paymentMethod)}</td>
            </tr>
          </tbody>
        </table>
      </div>

      {/* Äá»‹a chá»‰ giao hÃ ng */}
      <div className="col-md-6">
        <h6 className="text-primary mb-3">
          <i className="fas fa-truck me-2"></i>
          Äá»‹a chá»‰ giao hÃ ng
        </h6>
        <div className="border rounded p-3 bg-light">
          <p className="mb-1">
            <strong>Äá»‹a chá»‰:</strong> {order.shippingAddress || 'ChÆ°a cÃ³ Ä‘á»‹a chá»‰'}
          </p>
        </div>
      </div>

      {/* Danh sÃ¡ch sáº£n pháº©m */}
      <div className="col-12 mt-4">
        <h6 className="text-primary mb-3">
          <i className="fas fa-boxes me-2"></i>
          Sáº£n pháº©m Ä‘Ã£ Ä‘áº·t ({products.length})
        </h6>
        <div className="table-responsive">
          <table className="table table-bordered">
            <thead className="table-light">
              <tr>
                <th width="50%">Sáº£n pháº©m</th>
                <th>ÄÆ¡n giÃ¡</th>
                <th>Sá»‘ lÆ°á»£ng</th>
                <th>ThÃ nh tiá»n</th>
              </tr>
            </thead>
            <tbody>
              {products.map((item, index) => {
                const price = item.price || item.unitPrice || item.productPrice || 0;
                const quantity = item.quantity || item.amount || 1;
                const total = price * quantity;

                return (
                  <tr key={index}>
                    <td>
                      <div className="d-flex align-items-center">
                        <img
                          src={item.image ? `http://localhost:8080/api/public/products/image/${item.image}` : 'https://cdn-icons-png.flaticon.com/512/679/679922.png'}
                          alt={item.productName}
                          className="rounded me-3"
                          style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                          onError={(e) => {
                            e.target.src = 'https://cdn-icons-png.flaticon.com/512/679/679922.png';
                          }}
                        />
                        <div>
                          <div className="fw-bold">{item.productName}</div>
                          <small className="text-muted">MÃ£: #{item.productId}</small>
                        </div>
                      </div>
                    </td>
                    <td className="fw-bold">{price.toLocaleString()}â‚«</td>
                    <td>{quantity}</td>
                    <td className="fw-bold text-danger">{total.toLocaleString()}â‚«</td>
                  </tr>
                );
              })}
            </tbody>
            <tfoot className="table-light">
              <tr>
                <td colSpan="3" className="text-end"><strong>Tá»•ng cá»™ng:</strong></td>
                <td className="fw-bold text-danger fs-5">
                  {totalAmount.toLocaleString()}â‚«
                </td>
              </tr>
            </tfoot>
          </table>
        </div>
      </div>
    </div>
  );
};

export default OrderList;
