import { useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import productService from "../../services/productService";
import cartService from "../../services/cartService";

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductDetail = () => { 
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [addingToCart, setAddingToCart] = useState(false);

  const currentUser = JSON.parse(localStorage.getItem('user')) || { email: 'guest@example.com' };

  useEffect(() => {
    console.log("üîÑ Product ID t·ª´ URL:", productId, "Type:", typeof productId);

    const fetchProductDetail = async () => {
      if (!productId || productId === 'undefined' || productId === 'null') {
        setError("Kh√¥ng t√¨m th·∫•y ID s·∫£n ph·∫©m t·ª´ URL");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);
        console.log("üì° G·ªçi API v·ªõi ID:", productId);

        const productData = await productService.getProductById(productId);
        console.log("‚úÖ D·ªØ li·ªáu nh·∫≠n ƒë∆∞·ª£c:", productData);

        setProduct(productData);
      } catch (err) {
        console.error("‚ùå L·ªói khi t·∫£i chi ti·∫øt s·∫£n ph·∫©m:", err);
        setError(`Kh√¥ng th·ªÉ t·∫£i th√¥ng tin s·∫£n ph·∫©m: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetail();
  }, [productId]);

 // Trong handleAddToCart
const handleAddToCart = async () => {
  if (!currentUser || !currentUser.email) {
    alert('‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√™m v√†o gi·ªè h√†ng');
    navigate('/login');
    return;
  }

  if (productQuantity === 0) {
    alert('‚ùå S·∫£n ph·∫©m ƒë√£ h·∫øt h√†ng');
    return;
  }

  try {
    setAddingToCart(true);

    // 1. L·∫•y gi·ªè h√†ng active (ho·∫∑c t·∫°o m·ªõi n·∫øu ch∆∞a c√≥)
    let cart = await cartService.getActiveCart(currentUser.email);
    console.log("üì¶ Gi·ªè h√†ng:", cart);

    // 2. Th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng
    await cartService.addToCart(cart.cartId, productId, 1);

    alert('‚úÖ ƒê√£ th√™m v√†o gi·ªè h√†ng th√†nh c√¥ng!');

    setTimeout(() => {
      navigate('/cart');
    }, 1000);

  } catch (error) {
    console.error('‚ùå L·ªói khi th√™m v√†o gi·ªè h√†ng:', error);
    alert('‚ùå L·ªói khi th√™m v√†o gi·ªè h√†ng: ' + (error.response?.data?.message || error.message));
  } finally {
    setAddingToCart(false);
  }
};

  if (loading) {
    return (
      <div className="container mt-4">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">ƒêang t·∫£i th√¥ng tin s·∫£n ph·∫©m...</p>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger text-center">
          <h4>‚ùå Kh√¥ng th·ªÉ t·∫£i s·∫£n ph·∫©m</h4>
          <p>{error || "S·∫£n ph·∫©m kh√¥ng t·ªìn t·∫°i"}</p>
          <p className="small text-muted">Product ID t·ª´ URL: <code>"{productId}"</code></p>
          <div className="mt-3">
            <button 
              className="btn btn-primary me-2"
              onClick={() => navigate(-1)}
            >
              ‚Üê Quay l·∫°i
            </button>
            <Link to="/products" className="btn btn-outline-primary">
              üè† V·ªÅ trang s·∫£n ph·∫©m
            </Link>
          </div>
        </div>
      </div>
    );
  }

  // S·ª≠ d·ª•ng c·∫£ productName v√† product_name ƒë·ªÉ ƒë·∫£m b·∫£o t∆∞∆°ng th√≠ch
  const productName = product.productName || product.product_name;
  const productPrice = product.price;
  const productSpecialPrice = product.special_price;
  const productQuantity = product.quantity;
  const productDiscount = product.discount;
  const productDescription = product.description;

  return (
    <div className="container mt-4">
      {/* Breadcrumb */}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to="/" className="text-decoration-none">üè† Trang ch·ªß</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to="/products" className="text-decoration-none">S·∫£n ph·∫©m</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            {productName}
          </li>
        </ol>
      </nav>

      <div className="row">
        {/* H√¨nh ·∫£nh s·∫£n ph·∫©m */}
        <div className="col-md-6">
          <div className="card shadow-sm">
            <img
              src={`${IMAGE_BASE_URL}/${product.image}`}
              alt={productName}
              onError={(e) =>
                (e.target.src =
                  "https://cdn-icons-png.flaticon.com/512/679/679922.png")
              }
              className="img-fluid rounded"
              style={{ maxHeight: "500px", objectFit: "cover" }}
            />
          </div>
        </div>

        {/* Th√¥ng tin s·∫£n ph·∫©m */}
