import React, { useEffect, useState, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../services/userService";
import categoryService from "../services/categoryService";
import cartService from "../services/cartService";
import Swal from "sweetalert2";

const Header = () => {
  const [user, setUser] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [loadingCategories, setLoadingCategories] = useState(false);
  const [categories, setCategories] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  // 1. Tìm kiếm
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/product?search=${searchTerm.trim()}`);
    }
  };

  // 2. Init User
  const initUser = useCallback(async () => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      if (parsedUser.userId) {
        try {
          const data = await userService.getUserById(parsedUser.userId);
          setUserInfo(data);
        } catch (err) { console.error(err); }
      }
    } else { setUser(null); setUserInfo(null); }
  }, []);

  // 3. Cart Count
  const loadCartCount = async () => {
    try {
      const currentUser = JSON.parse(localStorage.getItem('user') || '{}');
      if (currentUser?.email) {
        const count = await cartService.getCartCount(currentUser.email);
        setCartCount(count);
      } else {
        const localCart = cartService.getCartFromStorage();
        setCartCount(cartService.getTotalItems(localCart?.products || []));
      }
    } catch (error) { setCartCount(0); }
  };

  // 4. Fetch Categories
  const fetchCategories = async () => {
    setLoadingCategories(true);
    try {
      const data = await categoryService.getAllCategories();
      setCategories(data || []);
    } catch (err) { console.error(err); } finally { setLoadingCategories(false); }
  };

  useEffect(() => {
    initUser();
    fetchCategories();
    window.addEventListener("authUpdated", initUser);
    return () => window.removeEventListener("authUpdated", initUser);
  }, [initUser]);

  useEffect(() => {
    loadCartCount();
    window.addEventListener('cartUpdated', loadCartCount);
    return () => window.removeEventListener('cartUpdated', loadCartCount);
  }, [user]);

  // 5. FIX: Click danh mục ra link /category/{id}
  const handleCategoryClick = (category) => {
    const id = category.categoryId || category.id;
    navigate(`/category/${id}`);
  };

  const handleLogout = () => {
    Swal.fire({
      title: 'Đăng xuất tài khoản?',
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#ff6a00',
      confirmButtonText: 'Đăng xuất'
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.clear();
        setUser(null);
        setUserInfo(null);
        setCartCount(0);
        window.dispatchEvent(new Event("authUpdated"));
        navigate("/login");
      }
    });
  };

  const renderUserName = () => {
    if (!user) return "Tài khoản";
    return userInfo?.firstName || user.firstName || user.email?.split('@')[0] || "User";
  };

  return (
    <header className="section-header sticky-top bg-white border-bottom shadow-sm">
      {/* Top Banner mỏng */}
      <div className="py-1 text-center text-white small fw-bold" style={{ background: 'linear-gradient(to right, #ff6a00, #ee0979)', fontSize: '11px' }}>
        GIAO HÀNG SIÊU TỐC 2H - FREESHIP MỌI ĐƠN HÀNG NỘI THÀNH
      </div>

      <section className="header-main py-3">
        <div className="container">
          <div className="row align-items-center">
            {/* Logo */}
            <div className="col-lg-3 col-md-3 col-6">
              <Link to="/" className="text-decoration-none">
                <h3 className="fw-bold mb-0" style={{ color: '#222', letterSpacing: '-1px' }}>
                  Shop<span style={{ color: '#ff6a00' }}>BeAnh</span>
                </h3>
              </Link>
            </div>

            {/* Search Bar bo tròn cực đại */}
            <div className="col-lg-5 col-md-5 d-none d-md-block">
              <form onSubmit={handleSearchSubmit}>
                <div className="input-group bg-light rounded-pill border p-1 shadow-sm">
                  <input 
                    type="text" 
                    className="form-control border-0 bg-transparent px-3 shadow-none small" 
                    placeholder="Bạn tìm gì hôm nay?..." 
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                  <button className="btn btn-warning rounded-pill px-4 text-white fw-bold border-0" type="submit" style={{ background: '#ff6a00' }}>
                    <i className="fa fa-search"></i>
                  </button>
                </div>
              </form>
            </div>

            {/* User & Cart */}
            <div className="col-lg-4 col-md-4 col-6">
              <div className="d-flex justify-content-end align-items-center gap-4">

                {/* User Dropdown */}
                <div className="dropdown user-dropdown">
                  <div className="d-flex align-items-center gap-2 pointer" style={{ cursor: 'pointer' }}>
                    <div className="avatar-mini border d-flex align-items-center justify-content-center bg-light" style={{ width: '38px', height: '38px', borderRadius: '50%' }}>
                      <i className="fa fa-user text-secondary"></i>
                    </div>
                    <div className="d-none d-lg-block">
                      <div className="text-muted" style={{ fontSize: '10px' }}>Xin chào,</div>
                      <span className="fw-bold small">{renderUserName()}</span>
                    </div>
                  </div>
                  <ul className="dropdown-menu-custom shadow-lg border-0 rounded-3">
                    {!user ? (
                      <>
                        <li><Link to="/login" className="dropdown-item-custom">Đăng nhập</Link></li>
                        <li><Link to="/register" className="dropdown-item-custom border-top">Đăng ký</Link></li>
                      </>
                    ) : (
                      <>
                        <li><Link to="/profile" className="dropdown-item-custom">Cá nhân</Link></li>
                        <li><Link to="/orders" className="dropdown-item-custom">Đơn hàng</Link></li>
                        <li><hr className="my-1" /></li>
                        <li><button onClick={handleLogout} className="dropdown-item-custom text-danger fw-bold border-0 bg-transparent w-100 text-start">Đăng xuất</button></li>
                      </>
                    )}
                  </ul>
                </div>

                {/* Giỏ hàng */}
                <Link to="/cart" className="text-dark text-decoration-none d-flex align-items-center gap-2">
                  <div className="icon-wrap position-relative border bg-orange-light d-flex align-items-center justify-content-center" style={{ width: '38px', height: '38px', borderRadius: '50%', background: '#fff5ee' }}>
                    <i className="fa fa-shopping-cart" style={{ color: '#ff6a00' }}></i>
                    <span className="badge rounded-pill bg-danger position-absolute top-0 start-100 translate-middle border border-white" style={{ fontSize: '10px' }}>
                      {cartCount}
                    </span>
                  </div>
                  <div className="d-none d-lg-block">
                      <div className="text-muted" style={{ fontSize: '10px' }}>Giỏ hàng</div>
                      <span className="fw-bold small">Của bạn</span>
                  </div>
                </Link>

              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Menu ngang */}
      <nav className="border-top bg-white d-none d-lg-block">
        <div className="container">
          <ul className="nav align-items-center">
            <li className="nav-item position-relative category-hover">
              <a className="nav-link text-dark fw-bold py-3 d-flex align-items-center" href="#" onClick={e => e.preventDefault()}>
                <i className="fa fa-bars me-2 text-warning"></i> DANH MỤC SẢN PHẨM
              </a>
              <div className="mega-dropdown shadow-lg border-0">
                 {loadingCategories ? <div className="p-3 small">Đang tải...</div> : (
                   categories.map((cat, i) => (
                    <button key={i} className="mega-item" onClick={() => handleCategoryClick(cat)}>
                      {cat.categoryName || cat.name}
                    </button>
                   ))
                 )}
              </div>
            </li>

            {!loadingCategories && categories.slice(0, 6).map((cat, i) => (
              <li className="nav-item" key={i}>
                <button className="nav-link-btn py-3 px-3" onClick={() => handleCategoryClick(cat)}>
                  {cat.categoryName || cat.name}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </nav>

      <style>{`
        .dropdown-menu-custom {
          display: none; position: absolute; right: 0; background: #fff; min-width: 160px; z-index: 1000; padding: 0;
        }
        .user-dropdown:hover .dropdown-menu-custom { display: block; animation: slideIn 0.2s ease; }
        .dropdown-item-custom { display: block; padding: 10px 15px; color: #333; text-decoration: none; font-size: 14px; }
        .dropdown-item-custom:hover { background: #f8f9fa; color: #ff6a00; }

        .mega-dropdown {
          display: none; position: absolute; top: 100%; left: 0; background: #fff; width: 240px; z-index: 1000;
        }
        .category-hover:hover .mega-dropdown { display: block; animation: slideIn 0.2s ease; }
        .mega-item {
          display: block; width: 100%; border: none; background: none; text-align: left;
          padding: 10px 20px; font-size: 14px; color: #444; transition: 0.2s;
        }
        .mega-item:hover { background: #fff5ee; color: #ff6a00; padding-left: 25px; }

        .nav-link-btn {
          border: none; background: none; font-size: 13px; font-weight: 600; color: #555; text-transform: uppercase;
        }
        .nav-link-btn:hover { color: #ff6a00; }

        @keyframes slideIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }
      `}</style>
    </header>
  );
};

export default Header;
