import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";

const Login = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    email: "",
    password: "",
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      console.log("üîê Attempting login with:", { email: form.email });

      const res = await authService.login(form);
      console.log("‚úÖ LOGIN SUCCESS:", res.data);

      const loginData = res.data;

      // ‚úÖ L·∫§Y TOKEN
      const token = loginData.token || loginData['jwt-token'];

      if (!token) {
        console.error("‚ùå No token found in response:", loginData);
        throw new Error("Server kh√¥ng tr·∫£ v·ªÅ token");
      }

      console.log("üîê Token received");

      // ‚úÖ L∆ØU TOKEN
      localStorage.setItem("token", token);

      // ‚úÖ GI·∫¢I M√É TOKEN ƒê·ªÇ L·∫§Y USER ID TH·∫¨T
      let realUserId = null;
      let userEmail = form.email;

      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        console.log("üîì Token payload:", payload);

        // ‚úÖ T√åM USER ID TRONG TOKEN - QUAN TR·ªåNG!
        // Th·ª≠ c√°c key ph·ªï bi·∫øn ch·ª©a user ID
        realUserId = payload.sub || payload.userId || payload.user_id || payload.id;

        // N·∫øu v·∫´n kh√¥ng t√¨m th·∫•y, t√¨m trong t·∫•t c·∫£ c√°c key
        if (!realUserId) {
          const idKeys = Object.keys(payload).filter(key => 
            key.toLowerCase().includes('user') || key.toLowerCase().includes('id')
          );
          if (idKeys.length > 0) {
            realUserId = payload[idKeys[0]];
          }
        }

        userEmail = payload.email || userEmail;

        console.log("‚úÖ User ID from token:", realUserId, `(type: ${typeof realUserId})`);

        // ‚úÖ ƒê·∫¢M B·∫¢O USER ID L√Ä S·ªê
        if (realUserId && typeof realUserId === 'string' && !isNaN(realUserId)) {
          realUserId = parseInt(realUserId, 10);
          console.log("‚úÖ Converted user ID to number:", realUserId);
        }

      } catch (decodeError) {
        console.warn("‚ö†Ô∏è Could not decode token:", decodeError);
      }

      // ‚úÖ N·∫æU KH√îNG C√ì USER ID T·ª™ TOKEN, TH·ª¨ API /auth/me
      if (!realUserId) {
        console.log("üîç No user ID from token, calling /auth/me...");
        try {
          const userInfoResponse = await fetch('http://localhost:8080/api/auth/me', {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });

          if (userInfoResponse.ok) {
            const userInfo = await userInfoResponse.json();
            realUserId = userInfo.userId || userInfo.user_id;
            console.log("‚úÖ User ID from /auth/me:", realUserId);
          } else {
            console.warn("‚ö†Ô∏è /auth/me failed, status:", userInfoResponse.status);
          }
        } catch (meError) {
          console.warn("‚ö†Ô∏è /auth/me endpoint error:", meError);
        }
      }

      // ‚úÖ N·∫æU V·∫™N KH√îNG C√ì USER ID TH·∫¨T, KH√îNG CHO ƒêƒÇNG NH·∫¨P
      if (!realUserId || typeof realUserId !== 'number') {
        console.error("‚ùå CRITICAL: No valid numeric user ID found");
        localStorage.removeItem("token");
        throw new Error("Kh√¥ng th·ªÉ l·∫•y th√¥ng tin ng∆∞·ªùi d√πng h·ª£p l·ªá. Vui l√≤ng th·ª≠ l·∫°i.");
      }

      // ‚úÖ L∆ØU USER DATA V·ªöI USER ID TH·∫¨T (S·ªê)
      const userToStore = {
        userId: realUserId, // ‚úÖ ƒê·∫¢M B·∫¢O L√Ä S·ªê
        email: userEmail,
        first_name: loginData.first_name || loginData.firstName || "",
        last_name: loginData.last_name || loginData.lastName || "",
        mobile_number: loginData.mobile_number || loginData.mobileNumber || ""
      };

      localStorage.setItem("user", JSON.stringify(userToStore));
      console.log("üíæ User data saved with NUMERIC ID:", userToStore);

      // ‚úÖ KI·ªÇM TRA L·∫†I
      const savedToken = localStorage.getItem("token");
      const savedUser = JSON.parse(localStorage.getItem("user"));

      console.log("üîç FINAL AUTH CHECK:", {
        token: savedToken ? "EXISTS" : "MISSING",
        user: savedUser ? `EXISTS (ID: ${savedUser.userId}, type: ${typeof savedUser.userId})` : "MISSING"
      });

      alert("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");

      // ‚úÖ CHUY·ªÇN TRANG
      setTimeout(() => {
        window.location.href = "/";
      }, 500);

    } catch (err) {
      console.error("‚ùå LOGIN ERROR:", err);
      localStorage.clear();
      alert("‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i: " + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card mx-auto" style={{ maxWidth: "380px", marginTop: "100px" }}>
      <div className="card-body">
        <h4 className="card-title mb-4">Sign in</h4>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
