import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import productService from '../../services/productService';
import BeautyBlog from '../BeautyBlog'; // Giả sử bạn đã tách Blog ra riêng

const IMAGE_BASE_URL = "http://localhost:8080/api/public/products/image";

const ProductNew = () => {
  const [newProducts, setNewProducts] = useState([]); // State cho sản phẩm mới
  const [topProducts, setTopProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      // Gọi đồng thời 3 API: Top bán chạy, Sản phẩm mới, và Tất cả sản phẩm
      const [topRes, allRes] = await Promise.all([
        productService.getTopSellingProducts(0, 8),
        productService.getAllProducts(0, 8)
      ]);

      setTopProducts(topRes.content || []);
      setAllProducts(allRes.content || []);

      // Giả lập lấy sản phẩm mới bằng cách sắp xếp theo ID giảm dần (nếu BE chưa có API riêng)
      // Hoặc nếu BE có API: await productService.getNewArrivals(0, 4)
      const sortedNew = [...(allRes.content || [])]
        .sort((a, b) => (b.productId || 0) - (a.productId || 0))
        .slice(0, 4);
      setNewProducts(sortedNew);

    } catch (err) {
      console.error('Lỗi tải dữ liệu:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadMoreProducts = async () => {
    const nextPage = page + 1;
    setLoading(true);
    try {
      const res = await productService.getAllProducts(nextPage, 8);
      setAllProducts([...allProducts, ...(res.content || [])]);
      setPage(nextPage);
    } catch (err) { console.error(err); } 
    finally { setLoading(false); }
  };

  const ProductItem = ({ p, index, isTop, isNew }) => {
    const productId = p.productId || p.product_id;
    return (
      <div className="col-xl-3 col-lg-4 col-md-6 col-6 mb-4">
        <div className="card h-100 border-0 shadow-sm hover-up overflow-hidden position-relative">
          {/* Badge Sản phẩm mới */}
          {isNew && (
            <div className="position-absolute top-0 end-0 m-2 badge bg-success text-white shadow" style={{zIndex: 5}}>
              NEW
            </div>
          )}
          {isTop && index < 3 && (
            <div className="position-absolute top-0 start-0 m-2 badge bg-primary text-white shadow" style={{zIndex: 5}}>
              #{index + 1} Hot
            </div>
          )}
          <div className="p-3 text-center bg-white">
            <Link to={`/product/${productId}`}>
              <img
                src={`${IMAGE_BASE_URL}/${p.image}`}
                alt={p.productName}
                onError={(e) => (e.target.src = "https://cdn-icons-png.flaticon.com/512/679/679922.png")}
                className="img-fluid"
                style={{ height: "200px", objectFit: "contain" }}
              />
            </Link>
          </div>
          <div className="card-body p-3">
            <Link to={`/product/${productId}`} className="text-dark fw-bold text-decoration-none d-block mb-1 text-truncate">
              {p.productName}
            </Link>
            <div className="text-danger fw-bold h5 mb-2">
              {(p.specialPrice || p.price).toLocaleString()} ₫
            </div>
            <div className="d-flex justify-content-between align-items-center small text-muted">
              <span>Đã bán: {p.sold || 0}</span>
              <span className="badge bg-light text-dark"><i className="fa fa-star text-warning"></i> 5.0</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="main-content-wrapper bg-light pb-5">

      {/* SECTION 1: NEW ARRIVALS (SẢN PHẨM MỚI) */}
      <section className="container pt-5">
        <div className="d-flex align-items-center mb-4">
          <h3 className="fw-bold mb-0 text-uppercase border-start border-4 border-success ps-3">Sản phẩm mới</h3>
          <span className="ms-3 badge rounded-pill bg-success-light text-success">Vừa cập nhật</span>
        </div>
        <div className="row">
          {newProducts.map((p, i) => (
            <ProductItem key={`new-${i}`} p={p} isNew={true} />
          ))}
        </div>
      </section>

      {/* SECTION 2: TOP SELLING */}
      <section className="container mt-5">
        <h3 className="fw-bold mb-4 text-uppercase border-start border-4 border-primary ps-3">Bán chạy nhất</h3>
        <div className="row">
          {topProducts.map((p, i) => (
            <ProductItem key={`top-${i}`} p={p} index={i} isTop={true} />
          ))}
        </div>
      </section>

      {/* SECTION 3: DISCOVER ALL */}
      <section className="container mt-5">
        <h3 className="fw-bold mb-4 text-uppercase border-start border-4 border-dark ps-3">Dành cho bạn</h3>
        <div className="row">
          {allProducts.map((p, i) => (
            <ProductItem key={`all-${i}`} p={p} isTop={false} />
          ))}
        </div>
        <div className="text-center mt-5">
          <button className="btn btn-outline-primary px-5 rounded-pill shadow-sm fw-bold" onClick={loadMoreProducts} disabled={loading}>
            {loading ? "ĐANG TẢI..." : "KHÁM PHÁ THÊM"}
          </button>
        </div>
      </section>

      {/* SECTION BLOG */}
      <BeautyBlog />

      <style>{`
        .bg-success-light { background-color: #e8f5e9; }
        .hover-up { transition: 0.3s; }
        .hover-up:hover { transform: translateY(-5px); box-shadow: 0 10px 20px rgba(0,0,0,0.08) !important; }
      `}</style>
    </div>
  );
};

export default ProductSold;
