import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2";

const AdminLogin = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      /* ================== 1. LOGIN ================== */
      const res = await authService.login({ email, password });
      const data = res.data;

      /* ================== 2. L·∫§Y TOKEN ================== */
      const token =
        data.token ||
        data.jwt ||
        data["jwt-token"];

      if (!token) {
        throw new Error("Kh√¥ng nh·∫≠n ƒë∆∞·ª£c token");
      }

      /* ================== 3. L·∫§Y ROLE ================== */
/* ================== 3. L·∫§Y ROLE (FIXED) ================== */
console.log("D·ªØ li·ªáu Backend tr·∫£ v·ªÅ n√®:", data); // F12 l√™n xem c√°i n√†y ƒë·∫ßu ti√™n!

// C·ªë g·∫Øng l·∫•y roleName t·ª´ m·∫£ng roles
const roleObj = data.roles?.[0] || data.authorities?.[0];
const roleName = data.role || roleObj?.roleName || roleObj?.authority;

console.log("Role b√≥c ƒë∆∞·ª£c l√†:", roleName);

// Ki·ªÉm tra: Ph·∫£i kh·ªõp tuy·ªát ƒë·ªëi v·ªõi ch·ªØ "ADMIN" (c√≥ th·ªÉ Backend tr·∫£ v·ªÅ "ROLE_ADMIN")
if (roleName !== "ADMIN" && roleName !== "ROLE_ADMIN") {
    Swal.fire({
        icon: "error",
        title: "‚õî Kh√¥ng c√≥ quy·ªÅn",
        text: `T√†i kho·∫£n c√≥ quy·ªÅn [${roleName || 'NULL'}], kh√¥ng ph·∫£i ADMIN`,
    });
    return;
}

      /* ================== 4. L∆ØU LOCALSTORAGE ================== */
      localStorage.setItem("token", token);
      localStorage.setItem("role", "ADMIN");

      /* ================== 5. UPDATE APP ================== */
      window.dispatchEvent(new Event("authUpdated"));

      /* ================== 6. SUCCESS ================== */
      Swal.fire({
        icon: "success",
        title: "ƒêƒÉng nh·∫≠p th√†nh c√¥ng",
        text: "Ch√†o m·ª´ng Admin üëë",
        timer: 1500,
        showConfirmButton: false,
      });

      navigate("/admin");
    } catch (err) {
      console.error(err);
      Swal.fire({
        icon: "error",
        title: "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i",
        text:
          err.response?.data?.message ||
          err.message ||
          "Sai email ho·∫∑c m·∫≠t kh·∫©u",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ minHeight: "100vh", display: "flex", alignItems: "center", justifyContent: "center" }}>
      <form
        onSubmit={handleSubmit}
        style={{
          width: 320,
          padding: 30,
          borderRadius: 10,
          boxShadow: "0 10px 30px rgba(0,0,0,0.15)",
        }}
      >
        <h3 style={{ textAlign: "center", marginBottom: 20 }}>
          üîê ADMIN LOGIN
        </h3>

        <div style={{ marginBottom: 15 }}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
            style={{ width: "100%", padding: 10 }}
          />
        </div>

        <div style={{ marginBottom: 20 }}>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading}
            style={{ width: "100%", padding: 10 }}
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            width: "100%",
            padding: 10,
            fontWeight: "bold",
            cursor: "pointer",
          }}
        >
          {loading ? "ƒêang ƒëƒÉng nh·∫≠p..." : "LOGIN"}
        </button>
      </form>
    </div>
  );
};

export default AdminLogin;
