import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    console.log("üîÑ EditProfile Component Mounted");

    const storedUser = localStorage.getItem("user");
    const storedToken = localStorage.getItem("token");

    console.log("üîç DEBUG STORAGE:", {
      user: storedUser ? JSON.parse(storedUser) : "NO USER",
      token: storedToken ? `EXISTS (${storedToken.length} chars)` : "NO TOKEN"
    });

    // CH·ªà ki·ªÉm tra user, KH√îNG ki·ªÉm tra token
    if (!storedUser) {
      console.log("‚ùå NO USER - REDIRECTING TO LOGIN");
      setMessage({ 
        type: "error", 
        text: "Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ch·ªânh s·ª≠a h·ªì s∆°" 
      });
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    const initializeUser = async () => {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        const userId = parsedUser.userId || parsedUser.user_id;
        console.log("üë§ User ID from storage:", userId);

        if (!userId) {
          setMessage({ type: "error", text: "Kh√¥ng t√¨m th·∫•y ID ng∆∞·ªùi d√πng" });
          return;
        }

        setLoading(true);

        // S·ª≠ d·ª•ng th√¥ng tin t·ª´ localStorage thay v√¨ g·ªçi API
        console.log("üìã Using data from localStorage instead of API");
        setFormData({
          first_name: parsedUser.first_name || "",
          last_name: parsedUser.last_name || "",
          email: parsedUser.email || "",
          mobile_number: parsedUser.mobile_number || ""
        });

      } catch (error) {
        console.error("‚ùå Initialize error:", error);
        setMessage({ 
          type: "error", 
          text: "L·ªói kh·ªüi t·∫°o. Vui l√≤ng th·ª≠ l·∫°i." 
        });
      } finally {
        setLoading(false);
      }
    };

    initializeUser();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("üîÑ Submit button clicked");

    const storedUser = localStorage.getItem("user");

    // CH·ªà ki·ªÉm tra user, KH√îNG ki·ªÉm tra token
    if (!storedUser) {
      setMessage({ 
        type: "error", 
        text: "Phi√™n ƒëƒÉng nh·∫≠p ƒë√£ h·∫øt h·∫°n" 
      });
      setTimeout(() => navigate("/login"), 2000);
      return;
    }

    const parsedUser = JSON.parse(storedUser);
    const userId = parsedUser.userId || parsedUser.user_id;

    if (!userId) {
      setMessage({ type: "error", text: "Kh√¥ng t√¨m th·∫•y ID ng∆∞·ªùi d√πng" });
      return;
    }

    // Validation
    if (!formData.first_name.trim() || !formData.last_name.trim() || !formData.email.trim()) {
      setMessage({ type: "error", text: "Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß th√¥ng tin b·∫Øt bu·ªôc" });
      return;
    }

    setSaving(true);
    setMessage({ type: "", text: "" });

    try {
      console.log("üöÄ Starting update process...");
      console.log("üì¶ Data to update:", formData);
      console.log("üë§ User ID:", userId);

      // TH·ª¨ NGHI·ªÜM: L∆∞u v√†o localStorage thay v√¨ g·ªçi API
      console.log("üíæ Saving to localStorage instead of API");

      // C·∫≠p nh·∫≠t localStorage
      const updatedUser = { 
        ...parsedUser, 
        ...formData
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      setMessage({ 
        type: "success", 
        text: "C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng! (L∆∞u c·ª•c b·ªô)" 
      });

      // Stay on page to show success message
      setTimeout(() => {
        navigate(-1);
      }, 1500);

    } catch (error) {
      console.error("üí• UPDATE FAILED:", error);

      let errorMessage = "C·∫≠p nh·∫≠t th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.";

      setMessage({ 
        type: "error", 
        text: errorMessage 
      });
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    console.log("‚Ü©Ô∏è Cancel button clicked");
    navigate(-1);
  };

  if (loading) {
    return (
