import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    mobile_number: ""
    // address: "",
    // date_of_birth: ""
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: "", text: "" });
  const navigate = useNavigate();

  useEffect(() => {
    const initializeUser = async () => {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        setMessage({ type: "error", text: "Please login to edit profile" });
        setTimeout(() => navigate("/login"), 2000);
        return;
      }

      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        // L·∫•y userId t·ª´ user object
        const userId = parsedUser.userId || parsedUser.user_id;
        if (!userId) {
          setMessage({ type: "error", text: "User ID not found" });
          return;
        }

        console.log("üÜî Loading profile for user ID:", userId);
        setLoading(true);

        // S·ª≠ d·ª•ng getUserProfile ƒë·ªÉ l·∫•y th√¥ng tin ƒë·∫ßy ƒë·ªß
        const userData = await userService.getUserProfile(userId);
        console.log("‚úÖ User profile loaded:", userData);

        // C·∫≠p nh·∫≠t form data v·ªõi d·ªØ li·ªáu t·ª´ API
        setFormData({
          first_name: userData.first_name || "",
          last_name: userData.last_name || "",
          email: userData.email || "",
          mobile_number: userData.mobile_number || "",
        //   address: userData.address || "",
        //   date_of_birth: userData.date_of_birth || ""
        });

      } catch (error) {
        console.error("‚ùå Error loading user profile:", error);
        setMessage({ 
          type: "error", 
          text: "Failed to load profile data. Please try again." 
        });
      } finally {
        setLoading(false);
      }
    };

    initializeUser();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setMessage({ type: "error", text: "User not found" });
      return;
    }

    const userId = user.userId || user.user_id;
    if (!userId) {
      setMessage({ type: "error", text: "User ID not found" });
      return;
    }

    setSaving(true);
    setMessage({ type: "", text: "" });

    try {
      console.log("üì§ Submitting form data:", formData);

      // Format data ƒë·ªÉ g·ª≠i l√™n server
      const updateData = {
        first_name: formData.first_name,
        last_name: formData.last_name,
        mobile_number: formData.mobile_number,
        email: formData.email,
        address: formData.address,
        date_of_birth: formData.date_of_birth
      };

      // G·ªçi API c·∫≠p nh·∫≠t
      const response = await userService.updateUser(userId, updateData);
      console.log("‚úÖ Update response:", response);

      setMessage({ 
        type: "success", 
        text: "Profile updated successfully!" 
      });

      // C·∫≠p nh·∫≠t localStorage v·ªõi th√¥ng tin m·ªõi
      const updatedUser = { 
        ...user, 
        ...formData,
        userId: userId,
        user_id: userId
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      // Reload page sau 2 gi√¢y ƒë·ªÉ c·∫≠p nh·∫≠t header
      setTimeout(() => {
        window.location.reload();
      }, 2000);

    } catch (error) {
      console.error("‚ùå Error updating profile:", error);

      let errorMessage = "Failed to update profile";
      if (error.response) {
        errorMessage = error.response.data?.message || errorMessage;
      }

      setMessage({ 
        type: "error", 
        text: errorMessage 
      });
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    navigate(-1); // Quay l·∫°i trang tr∆∞·ªõc
  };

  if (loading) {
    return (
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-8">
            <div className="card">
              <div className="card-body text-center py-5">
                <div className="spinner-border text-primary mb-3" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
                <p>Loading your profile...</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6">
          {/* Header */}
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h2 className="mb-0">Edit Profile</h2>
            <button 
              className="btn btn-outline-secondary"
              onClick={handleCancel}
            >
              <i className="fas fa-arrow-left me-2"></i>
              Back
            </button>
          </div>

          {/* Profile Card */}
          <div className="card shadow-sm">
            <div className="card-header bg-primary text-white">
              <h5 className="card-title mb-0">
                <i className="fas fa-user-edit me-2"></i>
                Personal Information
              </h5>
            </div>

            <div className="card-body">
              {/* Message Alert */}
              {message.text && (
                <div 
                  className={`alert ${
                    message.type === "success" ? "alert-success" : "alert-danger"
                  } alert-dismissible fade show`} 
                  role="alert"
                >
                  <i className={`fas ${
                    message.type === "success" ? "fa-check-circle" : "fa-exclamation-triangle"
                  } me-2`}></i>
                  {message.text}
                  <button 
                    type="button" 
                    className="btn-close" 
                    onClick={() => setMessage({ type: "", text: "" })}
                  ></button>
                </div>
              )}

              {/* Profile Form */}
              <form onSubmit={handleSubmit}>
                <div className="row">
                  {/* First Name */}
                  <div className="col-md-6 mb-3">
                    <label htmlFor="first_name" className="form-label">
                      <i className="fas fa-user me-1 text-muted"></i>
                      First Name *
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="first_name"
                      name="first_name"
                      value={formData.first_name}
                      onChange={handleChange}
                      required
                      placeholder="Enter your first name"
                    />
                  </div>

                  {/* Last Name */}
                  <div className="col-md-6 mb-3">
                    <label htmlFor="last_name" className="form-label">
                      <i className="fas fa-user me-1 text-muted"></i>
                      Last Name *
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="last_name"
                      name="last_name"
                      value={formData.last_name}
                      onChange={handleChange}
                      required
                      placeholder="Enter your last name"
                    />
                  </div>
                </div>

                {/* Email */}
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">
                    <i className="fas fa-envelope me-1 text-muted"></i>
                    Email Address *
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    placeholder="your.email@example.com"
                  />
                  <div className="form-text">
                    Your email address is used for account verification and notifications.
                  </div>
                </div>

                {/* Mobile Number */}
                <div className="mb-3">
                  <label htmlFor="mobile_number" className="form-label">
                    <i className="fas fa-phone me-1 text-muted"></i>
                    Mobile Number
                  </label>
                  <input
                    type="tel"
                    className="form-control"
                    id="mobile_number"
                    name="mobile_number"
                    value={formData.mobile_number}
                    onChange={handleChange}
                    placeholder="+84 123 456 789"
                  />
                </div>

                {/* Address */}
                <div className="mb-3">
                  <label htmlFor="address" className="form-label">
                    <i className="fas fa-home me-1 text-muted"></i>
                    Address
                  </label>
                  <textarea
                    className="form-control"
                    id="address"
                    name="address"
                    rows="3"
                    value={formData.address}
                    onChange={handleChange}
                    placeholder="Enter your full address"
                  />
                </div>

                {/* Date of Birth */}
                <div className="mb-4">
                  <label htmlFor="date_of_birth" className="form-label">
                    <i className="fas fa-birthday-cake me-1 text-muted"></i>
                    Date of Birth
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    id="date_of_birth"
                    name="date_of_birth"
                    value={formData.date_of_birth}
                    onChange={handleChange}
                  />
                </div>

                {/* Action Buttons */}
                <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                  <button
                    type="button"
                    className="btn btn-outline-secondary me-md-2"
                    onClick={handleCancel}
                    disabled={saving}
                  >
                    <i className="fas fa-times me-2"></i>
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={saving}
                  >
                    {saving ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status">
                          <span className="visually-hidden">Loading...</span>
                        </span>
                        Saving...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-save me-2"></i>
                        Save Changes
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>

            {/* Card Footer */}
            <div className="card-footer bg-light">
              <small className="text-muted">
                <i className="fas fa-info-circle me-1"></i>
                Fields marked with * are required
              </small>
            </div>
          </div>

          {/* Additional Info */}
          <div className="mt-3 text-center">
            <p className="text-muted small">
              Last updated: {new Date().toLocaleDateString()}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
