import { Routes, Route } from "react-router-dom";
// Layouts
import UserLayout from "./layouts/UserLayout";
import AdminLayout from "./layouts/AdminLayout";
// Pages
import Home from "./pages/user/Home";
import Login from "./pages/user/Login";
import AdminLogin from "./pages/admin/AdminLogin";
import ProductListAD from "./pages/admin/ProductListAD";

function App() {
  return (
    <Routes>
      {/* 1. NHÓM USER: Có Header, Footer của khách hàng */}
      <Route path="/" element={<UserLayout />}>
        <Route index element={<Home />} />
        <Route path="login" element={<Login />} />
        {/* Các trang khách hàng khác... */}
      </Route>

      {/* 2. TRANG LOGIN ADMIN: Độc lập hoàn toàn (Trắng tinh khôi) */}
      <Route path="/admin/login" element={<AdminLogin />} />

      {/* 3. NHÓM ADMIN: Có Sidebar, Navbar riêng của Admin */}
      <Route path="/admin" element={<AdminLayout />}>
        <Route path="products" element={<ProductListAD />} />
        {/* Các trang quản trị khác... */}
      </Route>
    </Routes>
  );
}
