import React, { useState, useEffect, useCallback } from "react";
import api from "../../../services/api";
import { Eye, Package, CreditCard, MapPin, Calendar, User, Clock, CheckCircle, Truck, XCircle, ChevronRight, Hash, Info } from "lucide-react";
import Swal from "sweetalert2";

const OrderListAD = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [pageConfig, setPageConfig] = useState({ pageNumber: 0, totalPages: 0, totalElements: 0 });

    const fetchOrders = useCallback(async (page = 0) => {
        try {
            setLoading(true);
            const token = localStorage.getItem("token");
            const res = await api.get(`/admin/orders?pageNumber=${page}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setOrders(res.data.content || []);
            setPageConfig({
                pageNumber: res.data.pageNumber,
                totalPages: res.data.totalPages,
                totalElements: res.data.totalElements
            });
        } catch (err) {
            console.error("Lỗi tải đơn hàng:", err);
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => { fetchOrders(0); }, [fetchOrders]);

    const handleViewOrder = async (orderId) => {
        try {
            const token = localStorage.getItem("token");
            const res = await api.get(`/admin/debug/orders/${orderId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setSelectedOrder(res.data);
            setShowModal(true);
        } catch (err) {
            Swal.fire("Lỗi", "Không thể lấy chi tiết đơn hàng!", "error");
        }
    };

    const updateStatus = async (orderId, newStatus) => {
        const result = await Swal.fire({
            title: 'Xác nhận?',
            text: `Bạn muốn chuyển đơn hàng sang trạng thái ${newStatus}?`,
            icon: 'question',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Đồng ý'
        });

        if (result.isConfirmed) {
            try {
                const token = localStorage.getItem("token");
                await api.put(`/admin/orders/${orderId}/status?orderStatus=${newStatus}`, {}, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                Swal.fire("Thành công", "Trạng thái đã cập nhật!", "success");
                fetchOrders(pageConfig.pageNumber);
                setShowModal(false);
            } catch (err) {
                Swal.fire("Lỗi", "Cập nhật thất bại", "error");
            }
        }
    };

    const getStatusBadge = (status) => {
        const configs = {
            'PENDING': { class: 'bg-warning-subtle text-warning border-warning', icon: <Clock size={12}/> },
            'SHIPPED': { class: 'bg-info-subtle text-info border-info', icon: <Truck size={12}/> },
            'DELIVERED': { class: 'bg-success-subtle text-success border-success', icon: <CheckCircle size={12}/> },
            'CANCELLED': { class: 'bg-danger-subtle text-danger border-danger', icon: <XCircle size={12}/> }
        };
        const config = configs[status] || { class: 'bg-secondary-subtle', icon: null };
        return (
            <span className={`badge border rounded-pill px-3 py-2 d-inline-flex align-items-center gap-1 ${config.class}`}>
                {config.icon} {status}
            </span>
        );
    };

    return (
        <div className="container-fluid p-4" style={{ background: '#f8f9fa', minHeight: '100vh' }}>
            <div className="card border-0 shadow-sm overflow-hidden" style={{ borderRadius: '15px' }}>
                <div className="card-header bg-white border-0 py-4 px-4 d-flex justify-content-between align-items-center">
                    <div>
                        <h4 className="mb-1 fw-bold text-dark d-flex align-items-center">
                            <Package className="me-2 text-primary" size={28}/> QUẢN LÝ ĐƠN HÀNG
                        </h4>
                        <p className="text-muted small mb-0">Theo dõi và cập nhật trạng thái đơn hàng của hệ thống</p>
                    </div>
                </div>

                <div className="table-responsive">
                    <table className="table table-hover align-middle mb-0">
                        <thead style={{ background: '#f1f4f9' }}>
                            <tr className="text-muted small text-uppercase fw-bold">
                                <th className="ps-4 py-3">ID</th>
                                <th>Khách hàng</th>
                                <th>Ngày đặt</th>
                                <th>Tổng tiền</th>
                                <th>Trạng thái</th>
                                <th className="text-center">Hành động</th>
                            </tr>
                        </thead>
                        <tbody>
                            {loading ? (
                                <tr><td colSpan="6" className="text-center py-5"><div className="spinner-border text-primary spinner-border-sm"></div></td></tr>
                            ) : orders.map(o => (
                                <tr key={o.orderId} className="transition-all">
                                    <td className="ps-4 fw-bold text-primary">#{o.orderId}</td>
                                    <td>
                                        <div className="d-flex align-items-center gap-2">
                                            <div className="avatar-sm bg-primary-subtle text-primary rounded-circle p-2 text-center" style={{width: '35px'}}>
                                                {o.email?.charAt(0).toUpperCase()}
                                            </div>
                                            <span className="small fw-medium">{o.email}</span>
                                        </div>
                                    </td>
                                    <td className="text-muted small">{o.orderDate}</td>
                                    <td className="fw-bold text-dark">{o.totalAmount?.toLocaleString()}đ</td>
                                    <td>{getStatusBadge(o.orderStatus)}</td>
                                    <td className="text-center">
                                        <button 
                                            className="btn btn-light btn-sm rounded-pill shadow-sm hover-scale" 
                                            onClick={() => handleViewOrder(o.orderId)}
                                        >
                                            <Eye size={18} className="text-primary"/>
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* MODAL CHI TIẾT - VIP PRO VERSION */}
            {showModal && selectedOrder && (
                <div className="modal d-block animate-fade-in" style={{ background: 'rgba(15, 23, 42, 0.7)', backdropFilter: 'blur(5px)', zIndex: 1050 }}>
                    <div className="modal-dialog modal-lg modal-dialog-centered">
                        <div className="modal-content border-0 shadow-2xl" style={{ borderRadius: '20px', overflow: 'hidden' }}>
                            <div className="modal-header bg-white border-0 p-4">
                                <div className="d-flex align-items-center gap-3">
                                    <div className="bg-primary-subtle p-3 rounded-4">
                                        <Package className="text-primary" size={24}/>
                                    </div>
                                    <div>
                                        <h5 className="modal-title fw-bold text-dark mb-0">Chi tiết đơn hàng #{selectedOrder.orderId}</h5>
                                        <span className="text-muted small">{selectedOrder.orderDate}</span>
                                    </div>
                                </div>
                                <button type="button" className="btn-close shadow-none" onClick={() => setShowModal(false)}></button>
                            </div>

                            <div className="modal-body p-4 pt-0">
                                {/* Grid thông tin khách hàng */}
                                <div className="row g-3 mb-4">
                                    <DetailBoxVip icon={<User className="text-primary"/>} label="Khách hàng" value={selectedOrder.email} />
                                    <DetailBoxVip icon={<MapPin className="text-danger"/>} label="Giao đến" value={selectedOrder.shippingAddress} />
                                    <DetailBoxVip icon={<CreditCard className="text-success"/>} label="Thanh toán" value={selectedOrder.paymentMethod || "COD"} />
                                </div>

                                {/* Table Sản phẩm */}
                                <div className="card border-0 bg-light-subtle rounded-4 overflow-hidden mb-4">
                                    <div className="px-3 py-2 fw-bold small text-muted text-uppercase d-flex align-items-center gap-2">
                                        <Info size={14}/> Sản phẩm trong đơn
                                    </div>
                                    <div className="table-responsive">
                                        <table className="table table-borderless align-middle mb-0">
                                            <thead className="bg-white">
                                                <tr className="text-muted small fw-bold">
                                                    <th className="ps-3">Sản phẩm</th>
                                                    <th className="text-center">Số lượng</th>
                                                    <th className="text-end pe-3">Thành tiền</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {selectedOrder.orderItems?.map((item, i) => (
                                                    <tr key={i} className="bg-white-hover">
                                                        <td className="ps-3 py-3">
                                                            <div className="d-flex align-items-center gap-3">
                                                                <div className="position-relative">
                                                                    <img 
                                                                        src={`http://localhost:8080/api/public/products/image/${item.image}`} 
                                                                        alt="" 
                                                                        style={{ width: '55px', height: '55px', objectFit: 'cover', borderRadius: '12px' }} 
                                                                        className="shadow-sm border border-2 border-white"
                                                                        onError={(e) => e.target.src = 'https://placehold.co/100?text=No+Img'} 
                                                                    />
                                                                </div>
                                                                <div>
                                                                    <div className="fw-bold text-dark small">{item.productName}</div>
                                                                    <div className="text-muted" style={{fontSize: '11px'}}>
                                                                        <Hash size={10} className="d-inline"/> {item.productId}
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </td>
                                                        <td className="text-center fw-bold text-secondary">x{item.quantity}</td>
                                                        <td className="text-end pe-3 fw-bold text-primary">
                                                            {(item.quantity * item.orderedProductPrice)?.toLocaleString()}đ
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>

                                {/* Summary */}
                                <div className="d-flex justify-content-end mb-4">
                                    <div className="bg-white p-3 rounded-4 shadow-sm border" style={{ minWidth: '250px' }}>
                                        <div className="d-flex justify-content-between mb-2">
                                            <span className="text-muted small">Tạm tính:</span>
                                            <span className="fw-medium">{selectedOrder.totalAmount?.toLocaleString()}đ</span>
                                        </div>
                                        <div className="d-flex justify-content-between pt-2 border-top">
                                            <span className="fw-bold h6 mb-0">Tổng cộng:</span>
                                            <span className="fw-bold h5 mb-0 text-danger">{selectedOrder.totalAmount?.toLocaleString()}đ</span>
                                        </div>
                                    </div>
                                </div>

                                {/* Action Buttons - Layout mới */}
                                <div className="d-flex gap-3 justify-content-center p-3 bg-light rounded-4">
                                    <button className="btn btn-info text-white px-4 py-2 d-flex align-items-center gap-2 rounded-3 shadow-sm hover-up" 
                                            onClick={() => updateStatus(selectedOrder.orderId, 'SHIPPED')}>
                                        <Truck size={18}/> <span>Vận chuyển</span>
                                    </button>
                                    <button className="btn btn-success text-white px-4 py-2 d-flex align-items-center gap-2 rounded-3 shadow-sm hover-up" 
                                            onClick={() => updateStatus(selectedOrder.orderId, 'DELIVERED')}>
                                        <CheckCircle size={18}/> <span>Hoàn tất</span>
                                    </button>
                                    <button className="btn btn-danger text-white px-4 py-2 d-flex align-items-center gap-2 rounded-3 shadow-sm hover-up" 
                                            onClick={() => updateStatus(selectedOrder.orderId, 'CANCELLED')}>
                                        <XCircle size={18}/> <span>Hủy đơn</span>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            {/* Thêm CSS Custom trực tiếp */}
            <style>
                {`
                .transition-all { transition: all 0.3s ease; }
                .hover-scale:hover { transform: scale(1.1); }
                .hover-up:hover { transform: translateY(-2px); box-shadow: 0 5px 15px rgba(0,0,0,0.1) !important; }
                .animate-fade-in { animation: fadeIn 0.3s ease; }
                @keyframes fadeIn { from { opacity: 0; transform: translateY(-10px); } to { opacity: 1; transform: translateY(0); } }
                .bg-primary-subtle { background-color: #e0e7ff !important; }
                .bg-success-subtle { background-color: #dcfce7 !important; }
                .bg-danger-subtle { background-color: #fee2e2 !important; }
                .bg-info-subtle { background-color: #e0f2fe !important; }
                .bg-warning-subtle { background-color: #fef9c3 !important; }
                .avatar-sm { display: flex; align-items: center; justify-content: center; font-weight: bold; font-size: 14px; }
                `}
            </style>
        </div>
    );
};

const DetailBoxVip = ({ icon, label, value }) => (
    <div className="col-md-4">
        <div className="p-3 bg-white border border-light-subtle rounded-4 shadow-sm h-100 transition-all hover-up">
            <div className="d-flex align-items-center gap-2 mb-2">
                <div className="p-2 bg-light rounded-3">{icon}</div>
                <span className="text-muted text-uppercase fw-bold" style={{ fontSize: '10px', letterSpacing: '0.5px' }}>{label}</span>
            </div>
            <div className="fw-bold text-dark small text-break">{value || "---"}</div>
        </div>
    </div>
);

export default OrderListAD;
