import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../../services/authService";
import Swal from "sweetalert2"; // << PH·∫¢I C√ì D√íNG N√ÄY

const AdminLogin = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    console.log("ƒêang b·∫Øt ƒë·∫ßu ƒëƒÉng nh·∫≠p...");

    try {
      const res = await authService.login({ email, password });
      console.log("K·∫øt qu·∫£ API:", res.data);

      const data = res.data;
      const token = data.token || data.jwt || data["jwt-token"];

      if (!token) {
        throw new Error("Server kh√¥ng tr·∫£ v·ªÅ m√£ x√°c th·ª±c!");
      }

      // GI·∫¢I M√É KI·ªÇM TRA QUY·ªÄN ADMIN
      const payload = JSON.parse(window.atob(token.split('.')[1]));
      const rawRole = payload.roles || payload.authorities || "";
      const roleStr = JSON.stringify(rawRole).toUpperCase();

      if (!roleStr.includes("ADMIN")) {
        console.log("Sai quy·ªÅn: ", roleStr);
        await Swal.fire({
          icon: "warning",
          title: "TRUY C·∫¨P B·ªä T·ª™ CH·ªêI",
          text: "T√†i kho·∫£n n√†y kh√¥ng c√≥ quy·ªÅn Admin!",
          background: "#1e1e2d",
          color: "#fff",
          confirmButtonColor: "#f39c12",
        });
        setLoading(false);
        return;
      }

      // L∆ØU KEY RI√äNG BI·ªÜT
      localStorage.setItem("admin-token", token);
      localStorage.setItem("admin-role", "ADMIN");

      // HI·ªÜN TH√îNG B√ÅO TH√ÄNH C√îNG
      await Swal.fire({
        icon: "success",
        title: "X√ÅC TH·ª∞C TH√ÄNH C√îNG",
        text: "Ch√†o s·∫øp, h·ªá th·ªëng ƒë√£ s·∫µn s√†ng!",
        timer: 1500,
        showConfirmButton: false,
        background: "#1e1e2d",
        color: "#fff",
      });

      navigate("/admin");
    } catch (err) {
      console.error("L·ªói ƒëƒÉng nh·∫≠p:", err);
      // HI·ªÜN TH√îNG B√ÅO L·ªñI
      await Swal.fire({
        icon: "error",
        title: "ƒêƒÇNG NH·∫¨P TH·∫§T B·∫†I",
        text: err.response?.data?.message || "Sai Email ho·∫∑c m·∫≠t kh·∫©u r·ªìi!",
        background: "#1e1e2d",
        color: "#fff",
        confirmButtonColor: "#e74c3c",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      {/* Hi·ªáu ·ª©ng n·ªÅn */}
      <div style={styles.blurLight}></div>
      
      <div style={styles.glassCard}>
        <div style={{ marginBottom: "30px" }}>
          <div style={styles.iconCircle}>üîë</div>
          <h2 style={{ color: "#fff", margin: "10px 0 5px" }}>ADMIN LOGIN</h2>
          <p style={{ color: "#888", fontSize: "13px" }}>Security System v2.0</p>
        </div>

        <form onSubmit={handleSubmit}>
          <div style={styles.inputBox}>
            <input
              type="email"
              placeholder="Email Admin"
              style={styles.input}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div style={styles.inputBox}>
            <input
              type="password"
              placeholder="M·∫≠t kh·∫©u"
              style={styles.input}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button type="submit" style={styles.button} disabled={loading}>
            {loading ? "ƒêANG X√ÅC TH·ª∞C..." : "ƒêƒÇNG NH·∫¨P"}
          </button>
        </form>

        <p onClick={() => navigate("/")} style={styles.backLink}>
           ‚Üê V·ªÅ trang ch·ªß User
        </p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    width: "100vw",
    background: "#050505",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    position: "fixed",
    top: 0,
    left: 0,
    zIndex: 99999,
  },
  blurLight: {
    position: "absolute",
    width: "300px",
    height: "300px",
    background: "rgba(0, 123, 255, 0.2)",
    filter: "blur(100px)",
    zIndex: -1,
  },
  glassCard: {
    width: "380px",
    padding: "40px",
    background: "rgba(255, 255, 255, 0.05)",
    backdropFilter: "blur(15px)",
    borderRadius: "20px",
    border: "1px solid rgba(255, 255, 255, 0.1)",
    boxShadow: "0 25px 50px rgba(0,0,0,0.5)",
    textAlign: "center",
  },
  iconCircle: {
    width: "60px",
    height: "60px",
    background: "rgba(255,255,255,0.1)",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto",
    fontSize: "24px"
  },
  inputBox: {
    marginBottom: "20px",
  },
  input: {
    width: "100%",
    padding: "12px 15px",
    background: "rgba(0,0,0,0.5)",
    border: "1px solid #333",
    borderRadius: "10px",
    color: "#fff",
    outline: "none",
    boxSizing: "border-box"
  },
  button: {
    width: "100%",
    padding: "14px",
    background: "#007bff",
    border: "none",
    borderRadius: "10px",
    color: "#fff",
    fontWeight: "bold",
    cursor: "pointer",
    boxShadow: "0 5px 15px rgba(0,123,255,0.3)"
  },
  backLink: {
    marginTop: "25px",
    color: "#555",
    fontSize: "12px",
    cursor: "pointer",
    textDecoration: "underline"
  }
};

export default AdminLogin;
