import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import userService from "../../services/userService";

const EditProfile = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    firstName: "",  // ‚úÖ S·ª≠a th√†nh camelCase
    lastName: "",   // ‚úÖ S·ª≠a th√†nh camelCase
    email: "",
    mobileNumber: "" // ‚úÖ S·ª≠a th√†nh camelCase
  });
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const loadUserData = async () => {
      try {
        const token = localStorage.getItem("token");
        const userData = localStorage.getItem("user");

        if (!token || !userData) {
          alert("‚ùå Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ ti·∫øp t·ª•c!");
          navigate("/login");
          return;
        }

        const user = JSON.parse(userData);
        const currentUserId = user.userId;

        console.log("üë§ Current User ID:", currentUserId);
        setUserId(currentUserId);

        if (typeof currentUserId === 'number' && currentUserId > 0) {
          setLoading(true);
          console.log("üì° Loading user data from API...");

          const userProfile = await userService.getUserById(currentUserId);
          console.log("‚úÖ API Response:", userProfile);

          // ‚úÖ S·ª≠a: D√πng camelCase theo DTO
          setForm({
            firstName: userProfile.firstName || userProfile.first_name || "",
            lastName: userProfile.lastName || userProfile.last_name || "",
            email: userProfile.email || "",
            mobileNumber: userProfile.mobileNumber || userProfile.mobile_number || ""
          });
        } else {
          // D√πng data t·ª´ localStorage
          setForm({
            firstName: user.firstName || user.first_name || "",
            lastName: user.lastName || user.last_name || "",
            email: user.email || "",
            mobileNumber: user.mobileNumber || user.mobile_number || ""
          });
        }

      } catch (error) {
        console.error("‚ùå Failed to load user data:", error);
        alert("‚ùå Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng!");
      } finally {
        setLoading(false);
      }
    };

    loadUserData();
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation - s·ª≠a th√†nh camelCase
    if (!form.firstName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p h·ªç!");
      return;
    }

    if (!form.lastName.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p t√™n!");
      return;
    }

    if (!form.email.trim()) {
      alert("‚ùå Vui l√≤ng nh·∫≠p email!");
      return;
    }

    if (!/\S+@\S+\.\S+/.test(form.email)) {
      alert("‚ùå Email kh√¥ng h·ª£p l·ªá!");
      return;
    }

    setSaving(true);

    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      const currentUserId = userData.userId;

      console.log("üöÄ STARTING UPDATE...");
      console.log("üì¶ Form Data:", form);

      // ‚úÖ QUAN TR·ªåNG: D√πng camelCase theo DTO
      const updateData = {
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };

      console.log("üì§ Sending camelCase data:", updateData);

      // G·ªçi API update
      const response = await userService.updateUser(currentUserId, updateData);

      console.log("‚úÖ UPDATE SUCCESS:", response);

      // C·∫≠p nh·∫≠t localStorage
      const updatedUser = { 
        ...userData,
        firstName: form.firstName,
        lastName: form.lastName,
        email: form.email,
        mobileNumber: form.mobileNumber
      };
      localStorage.setItem("user", JSON.stringify(updatedUser));

      alert("‚úÖ C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!");
      navigate("/profile");

    } catch (error) {
      console.error("‚ùå UPDATE FAILED:", error);

      if (error.response) {
        console.log("üîç Full Error Response:", error.response);
        const serverMessage = error.response.data?.message || JSON.stringify(error.response.data);
        alert(`‚ùå L·ªói server: ${serverMessage}`);
      } else {
        alert(`‚ùå L·ªói: ${error.message}`);
      }
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <section className="section-content padding-y">
        <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
          <article className="card-body text-center py-5">
            <div className="spinner-border text-primary mb-3" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mb-0">ƒêang t·∫£i th√¥ng tin h·ªì s∆°...</p>
          </article>
        </div>
      </section>
    );
  }

  return (
    <section className="section-content padding-y">
      <div className="card mx-auto shadow" style={{ maxWidth: "520px", marginTop: "40px" }}>
        <article className="card-body">
          <header className="mb-4 text-center">
            <h4 className="card-title">Ch·ªânh s·ª≠a h·ªì s∆°</h4>
            {userId && (
              <small className="text-muted d-block mt-1">
                User ID: <strong>{userId}</strong>
              </small>
            )}
          </header>

          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="col form-group">
                <label>First name *</label>
                <input
                  name="firstName"  
                  type="text"
                  className="form-control"
                  value={form.firstName}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
              <div className="col form-group">
                <label>Last name *</label>
                <input
                  name="lastName"
                  type="text"
                  className="form-control"
                  value={form.lastName}
                  onChange={handleChange}
                  required
                  disabled={saving}
                />
              </div>
            </div>

            <div className="form-group">
              <label>Email *</label>
              <input
                name="email"
                type="email"
                className="form-control"
                value={form.email}
                onChange={handleChange}
                required
                disabled={saving}
              />
            </div>

            <div className="form-group">
              <label>Mobile number</label>
              <input
                name="mobileNumber" 
                type="text"
                className="form-control"
                value={form.mobileNumber}
                onChange={handleChange}
                disabled={saving}
                placeholder="+84 123 456 789"
              />
            </div>

            <div className="form-group d-flex gap-2">
              <button 
                type="button" 
                className="btn btn-secondary flex-fill"
                onClick={() => navigate(-1)}
                disabled={saving}
              >
                ‚Üê Quay l·∫°i
              </button>
              <button 
                type="submit" 
                className="btn btn-primary flex-fill"
                disabled={saving}
              >
                {saving ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                    ƒêang l∆∞u...
                  </>
                ) : (
                  'üíæ C·∫≠p nh·∫≠t'
                )}
              </button>
            </div>
          </form>
        </article>
      </div>

      <p className="text-center mt-4">
        <Link to="/">‚Üê Quay v·ªÅ trang ch·ªß</Link>
      </p>
    </section>
  );
};

export default EditProfile;
